css:
@-moz-document domain("aimware.net") {
body {
    background-attachment: fixed !important;
    /* Don't delete this line */
    
    background-image: url(http://www.designbolts.com/wp-content/uploads/2012/11/Free-Topographic-Map-Seamless-pattern.jpg) !important;
    background-color: YOUR_COLOR_HERE !important;
    color: FONT_COLOR_HERE !important;
    font-family: FONT_HERE !important;
    font-size: FONT_SIZE px !important;
}
#content .wrapper {
    background: #222;
    padding: 10px;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}
.navigation .active {
    color: #fff;
    font-size: small;
    font-weight: bold;
}
#logo {
    border: 0px;
    height: 80px;
    background: #222;
}
#panel .upper,
#panel .lower {
    background: #222;
    border: 0px;
    color: #fff;
}
.thead {
    background: #695947;
    padding-top: 4px;
    padding-bottom: 4px;
    border: 0px;
}
.trow1,
.trow2 {
    background: #fff;
    border: 0px;
    border-bottom: 1px solid #c4c4c4;
    background: rgba(255, 255, 255, 1);
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 45%, rgba(246, 246, 246, 1) 73%, rgba(237, 237, 237, 1) 100%);
    background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(255, 255, 255, 1)), color-stop(45%, rgba(255, 255, 255, 1)), color-stop(73%, rgba(246, 246, 246, 1)), color-stop(100%, rgba(237, 237, 237, 1)));
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 45%, rgba(246, 246, 246, 1) 73%, rgba(237, 237, 237, 1) 100%);
    background: -o-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 45%, rgba(246, 246, 246, 1) 73%, rgba(237, 237, 237, 1) 100%);
    background: -ms-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 45%, rgba(246, 246, 246, 1) 73%, rgba(237, 237, 237, 1) 100%);
    background: linear-gradient(to bottom, #2f2e2e 0%, #2f2e2e 45%, #2f2e2e 73%, #2f2e2e 100%);
   
    /*filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#ededed', GradientType=0);*/
}
.tborder {
    border: 0px solid #222;
    padding: 3px;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    box-shadow: 0 0 5px #2f2e2e;
    background: #222;
}
#notice_box {
    background: #695947;
    -webkit-box-shadow: -3px 3px 5px -1px rgba(208, 208, 208, 1);
    -moz-box-shadow: -3px 3px 5px -1px rgba(208, 208, 208, 1);
    box-shadow: -3px 3px 5px -1px rgba(208, 208, 208, 1);
    padding: 5px 10px;
    margin-top: 5px;
    margin-bottom: 5px;
}
table {
    color: #d8d8d8;
    font-size: 13px;
}
.tcat {
    background: #695947;
    color: #333;
    border: 0px;
}
.tfoot {
    border-top: 1px solid #695947;
    padding: 6px;
    background: #695947;
    color: #666;
}
a:visited {
    color: #fff;
}
a:link {
    color: #d8d8d8;
}
#footer .upper {
    background: #222222;
    border-top: 1px solid #bbb;
    border-bottom: 1px solid #bbb;
    padding: 6px;
    font-size: 12px;
    overflow: hidden;
}
.trow_sep {
    background: #695947;
    color: #333;
    border-bottom: 1px solid #c5c5c5;
    padding: 6px;
    font-size: 12px;
    font-weight: bold;
}
.codeblock {
    background: #222;
    border: 1px solid #ccc;
    padding: 10px;
}
.post .post_author {
    border-bottom: 1px solid #222;
    border-top: 2px solid #292929;
    background: #292929;
    padding: 5px;
    overflow: hidden;
}
.post_controls {
    clear: both;
    background: #695947;
    border-bottom: 1px solid #ccc;
    padding: 5px;
    overflow: hidden;
}
blockquote {
    border: 1px solid #ccc;
    margin: 0;
    background: #292929;
    padding: 10px;
}
textarea {
    background: #222222;
    color: #333;
    border: 1px solid #ccc;
    padding: 2px;
    line-height: 1.4;
    outline: 0;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
}
#search input.button {
    background: #695947;
    border: 1px solid #695947;
}
}
