css:
@-moz-document domain("atelier801.com") {



body{
    background-image: url(http://66.media.tumblr.com/8c4d740fabd1c284af9a163f5dddcc05/tumblr_mjunjh67sy1qexo6wo6_400.png);
    background-attachment: fixed;
    background-size: contain;
}

.cadre-code {
    background-color: rgba(166, 77, 171, 0.27);
}

.menu-principal{
    background-image: url(http://i.imgur.com/ybyjP3v.png);
}

.menu-secondaire{
    background-image: url(http://i.imgur.com/XV4dQKI.png);
}

footer{
    background-color: transparent;
    border-top: 1px dotted #f3ceeb;
}

.navbar-inverse .nav>li>a, .cadre-sujet-titre-sombre, .empeche-selection-texte{
    color: #ead2ff;
}

.composant-selection-image-utilisateur-upload{
    background-image: linear-gradient(to top,#411b47,transparent);
    border: none;
}

.navbar-inverse .brand, .dataTables_wrapper .dataTables_info{
    color: #ead2ff;
}

.cadre legend{
	color: #ead2ff;
	text-shadow: 0 -1px 0 rgba(0,0,0,0.25);
}

legend, .nav-tabs{
    border-bottom: 1px dotted #f3ceeb;   
}

.navbar-inverse .nav .active>a, .navbar-inverse .nav .active>a:hover{
    background-color: rgba(66, 29, 72, 0.2); 
    border-radius: 50px;
    color: transparent;
    text-shadow: 0 -1px 0 #ead2ff;
}

.cadre-forum{
    background-color: transparent;
    border: 1px groove #6f4566;;
}

.table-section{
    background-color: transparent; 
}

.rang-prestige, .cadre-type-auteur-joueur {
    color: #f1c7ff;
}

.cadre-message, .cadre-sondage, .cadre-resultat-recherche, .cadre-utilisateur, .cadre-defaut,  table.dataTable tbody tr{
    background-color: transparent;
    border: 1px groove #6f4566;  
}

.infos-image-utilisateur-grille{
    background-color: rgba(33, 6, 37, 0.84);
}

.cadre-spoil, .cadre-quote{
    background-color: rgba(40, 11, 45, 0.66);
    border: 1px dotted #6f4566;    
}

.cadre-moderation{
    color: #ead2ff;
    background-color: transparent;
    border: 1px groove #6f4566;   
    border-radius: 5px;     
}

.cadre-message-contenu, .cadre-message-recherche, .cadre-presentation, .table.dataTable tbody tr, .cadre-formulaire{
    background-color: rgba(66, 29, 72, 0.59);
    border: 1px dotted #6f4566;  
    border-radius: 5px;  
    padding: 10px 25px;
}

.cadre-sujet, .cadre-repondre, .cadre-upload-image-utilisateur, .cadre-dernieres-images-utilisateurs, .cadre-conversation, .cadre-jeu, .cadre-image-utilisateur{
    background-color: rgba(66, 29, 72, 0.59);
    border: 1px groove #6f4566;  
    border-radius: 10px;    
}

hr{
    border-top: 1px solid #876e9c;
    border-bottom: 1px solid #341838;
}

.dropdown-menu .divider{
    background-color: #a64dad;
    border-bottom: 1px solid #341838;
}

.btn-inverse:hover, .btn-inverse:focus, .btn-inverse:active, .btn-inverse.active, .btn-inverse.disabled, .btn-inverse[disabled], .composant-recherche input{
    background-color: rgba(48, 33, 49, 0.7);  	   
}

.cadre-quote small, .label-source-upload-image-utilisateur{
    color: #ead2ff;
}

.menu-contextuel table tr:nth-child(odd) td, .menu-contextuel table tr:nth-child(odd) th{
    background-color: transparent;   
    color: #ead2ff;
}

.menu-contextuel{
    color: #ead2ff;
    background-color: rgba(66, 29, 72, 0.9);
    border: 1px groove #6f4566;  
    border-radius: 10px;     
}

.contenant-image-utilisateur-grille-sans-marge{
    background-color: rgba(66, 29, 72, 0.59);
    border-radius: 10px;  
}

.liste-menu-contextuel>li>a:hover{
    background-color: transparent;
    background-image: -webkit-gradient(linear,0 0,0 100%,from(transparent),to(transparent));
    border: 1px dotted #f3ceeb;
}

.dropdown-menu>li>a:hover{
	background-color: transparent;
    background-image: -webkit-gradient(linear,0 0,0 100%,from(transparent),to(transparent));
    border-top: 1px dotted #f3ceeb;
    border-bottom: 1px dotted #f3ceeb;
}

.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th{
	background-color: transparent;    
}

.cadre-sections-actu{
    border-radius: 10px;
	background-color: rgba(66, 29, 72, 0.59); 
}

.line-striped, .navbar-inverse .nav li.dropdown.open>.dropdown-toggle{
    background-color: transparent; 
}

.btn-inverse{
	text-shadow: none;    
    color: #ead2ff;
    background-color: rgba(66, 29, 72, 0.9);
    border: 1px groove #6f4566;  
    border-radius: 10px;  
}

.modal-header{
    border-bottom: 1px solid #5959a5;
}

.modal-footer{
    border-top: 1px solid #5959a5;
}

.modal, .modal-footer{
 	background-image: url(http://i.imgur.com/7ldnxMd.png);  
    color: #ead2ff;
}

.lien-blanc, .display-inline-block, .nav-header{
    color: #ead2ff;
}

.nombre-messages{
    text-shadow: 1px 1px #222;   
}

.cadre-sujet-date, .date-image-utilisateur, .cadre-auteur-message-date, .texte-date, .numero-message, .bouton-like{
    color: #b88ada;
}

.barre-navigation, .navigation-resultat-recherche{
    background-color: transparent;
}

.barre-navigation a{
    color: #ead2ff;
}

.cadre-pagination input, .cadre-invitation input{
    background-color: rgba(48, 33, 49, 0.7); 
    color: #ead2ff;
}

.btn-post{
    background-color: #beb4c5;
    text-shadow: none;
}

.btn, .bouton-barre-navigation, .composant-selection-image-utilisateur-upload:hover {
    background-image: -moz-linear-gradient(top,#b474c3,#cfa6e4,#cfa6e4);
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#b474c3),color-stop(50%,#cfa6e4),to(#cfa6e4));
    background-image: -webkit-linear-gradient(top,#b474c3,#cfa6e4,#cfa6e4);
    background-image: -o-linear-gradient(top,#b474c3,#cfa6e4,#cfa6e4);
    background-image: linear-gradient(to bottom,#b474c3,#cfa6e4,#cfa6e4);
    background-size: auto 200%;
    
    
    color: #562f5e;
    border-top: transparent;
}

.btn-post, .btn-info {
	background-color: transparent; 
}

.btn-message:hover, .btn-message:focus, .btn-message:active, .btn-message.active, .btn-message.disabled, .btn-message[disabled] {
    color: #562f5e;
    background-color: #cb8ac1;
}

.nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-tabs>.active>a:focus{
    background-image: linear-gradient(to bottom,#cb8ac1,transparent);
    color: #ead2ff;
}

.nav-tabs a {
    color: #cb8ac1;
}

.contenant-image-utilisateur-grille-avec-bordure:hover{
    border-color: #f1afbf;    
}

.nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-tabs>.active>a:focus, .btn:active {
    color: #3f1b44;
    background-color: #f1afbf;
    border: transparent;
}

.bouton-like .coeur {
    background: url(http://i.imgur.com/9HvPKVD.png);
}

.cadre-message-like {
    background: url(http://i.imgur.com/4GuUrd8.png) left top repeat-x,url(http://i.imgur.com/0On3TW8.pngg) right 10px top 30px no-repeat;
}

.cadre-sujet-postit {
            background-color: rgba(117, 28, 124, 0.49);
}
}
