css:
@-moz-document url-prefix("http://play.typeracer.com/"), domain("play.typeracer.com"), domain("pit.typeracer.com"), domain("data.typeracer.com"), domain("preview.typeracer.com") {
html {
    min-height: 100%;
}
body {
    height: 100%;
    background: 0;
    color: #292b2c;
}
a {
    color: #333;
}
a:hover {
    color: #555;
}
h1,
h2,
h3,
.gameView .gameStatusLabel,
.textInfoView .textInfoHeader,
.textInfoView .textInfoOwnStats,
.textInfoView .textInfoTitle a,
.textInfoView .textInfoOwnStatsNumber,
.tblOwnStatsNumber .datetime,
.tblOwnStatsNumber,
.textInfoView .tblOwnStats {
    color: #292b2c !important;
}
/* Navigation */

.gwt-MenuBar {
    background-color: #292b2c;
}
.gwt-MenuItem-selected {
    background-color: #222;
}
.themeHeader,
.mainUserHeader {
    background: 0;
    background-color: #292b2c;
}
.themeHeader a {
    color: #FFF;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: #EEE;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title h1 a {
    text-transform: uppercase;
}
.themeHeader .navigation {
    padding: 22px 20px;
    height: 0;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #EEE;
}
.subTitle {
    display: none;
}
/* Player Info */

.playerInfoPopup {
    background: #292b2c;
    color: #FFF;
    border-color: #222;
}
.playerInfoPopup a {
    color: #EEE;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: #FFF;
}
/* Content */

.main,
.themeContent {
    background: 0;
    background-color: #FFF;
    margin: 0 auto;
    max-width: 750px;
}
.mainViewportHolder,
.mainViewport {
    width: 750px;
}
.nonHideableWords {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    font-size: 22px !important;
}
[id^='nhwMiddlegwt-uid-'],
[id^='nhwMiddleCommagwt-uid-'] {
    color: #5fa7e8 !important;
}
.friendsTable .headerRow {
    font-size: 14px;
    text-transform: capitalize;
    font-style: normal !important;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #E9E9E9;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #F9F9F9;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #333;
}
.popupContent {
    background: #EEE;
    color: #292b2c;
}
.PopupWithIcon {
    background-color: #292b2c;
    border-color: #292b2c;
}
.trPopupDialog .xButton {
    background-color: #292b2c;
}
.trPopupDialog {
    box-shadow: 1px 2px 7px #333;
}
.fullTextStr {
    color: #292b2c !important;
    background-color: #FFF !important;
}
/* Footer */

.footer {
    background: 0;
    background-color: #292b2c;
    color: #EEE;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: 0;
}

/* Preview Updates */
.inputPanel > tbody > tr > td > div > span { 
    text-decoration: none;
}

.inputPanel > tbody > tr > td > div > span:nth-child(1),
.inputPanel > tbody > tr > td > div > span:nth-child(2) {
    color: #5fa7e8;
}
}
