css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("multiplayer.it") {
  
/*GENERAL*/
body {
  color:#fff;
}
#wrapper {
  background:#222 !important;
}
.grid {
	margin: 0 0 0px 0;
}
hr {
	background: #555;
}
.content-filter a {
	border: solid 2px #555;
	color: #ddd;
}
.comment-grade-1 {
	background-color: #582c24;
}
.comment-grade-2 {
	background-color: #8e3726;
}
.comment-grade-3 {
	background-color: #c44128;
}
.comment-grade-4 {
	background-color: #fa4b2a;
}
.comment-grade-1 .comment-ballon-point {
	border-color: transparent #582c24 transparent transparent;
}
.comment-grade-2 .comment-ballon-point {
	border-color: transparent #8e3726 transparent transparent;
}
.comment-grade-3 .comment-ballon-point {
	border-color: transparent #c44128 transparent transparent;
}
.comment-grade-4 .comment-ballon-point {
	border-color: transparent #fa4b2a transparent transparent;
}
.user_avatar {
	background: transparent;
}
.rsNav.rsBullets {
	background: transparent;
}
.rsNav .rsNavItem {
	background: #555;
}
#login_window .button.login .loading, #registration_window .button.login .loading {
 background: transparent;
}
.modal_box_window {
	background: #111;
}
#multiplayer_ajax_loading {
	background-image: url('https://www.gearboxsports.com/media/loader.gif');
}
body#news_archive_page .archive_box h2 a, body#user_page .archive_box h2 a, body#game_page .archive_box h2 a {
	color: #fff;
}	
#sidebar_column .sidebar_filter li a {
	color: #fff;
}
  
	
/*NAVIGATION*/
#navigation {
	background: #101010;
}
#navigation #main-navigation > li > a {
	color: #ddd;
}
#navigation #main-navigation #user_nav .notification_counter {
	background-color: #555;
}
#navigation #main-navigation #user_nav .user_panel_container {
	background-color: #111;
	border-top: 5px solid #fa4b2a;
}
#navigation #main-navigation #user_nav .user_panel_container li.user_information_container .user_information .ribbon {
	color: #ddd;
}
#navigation #main-navigation #user_nav .user_panel_container li.notifications_container li .icon {
	border: 2px solid #ddd;
	background: #111;
	color: #111;
}
#navigation #main-navigation #user_nav .user_panel_container li.notifications_container a {
	color: #ddd;
}
#navigation #main-navigation .navigation_menu {
	background: #111;
}
.navigation_menu a {
	color: #fff;
}
#navigation #main-navigation .navigation_menu li {
	color: #fff;
}
#navigation #super_diretta_header .color_ribbon .very_light {
	background-color: #582c24;
}
#navigation #super_diretta_header .color_ribbon .light {
	background-color: #8e3726;
}
#navigation #super_diretta_header .color_ribbon .dark {
	background-color: #c44128;
}
#navigation #super_diretta_header .color_ribbon .very_dark {
	background-color: #FA4B2A;
}
#super_diretta_header {
	background-color: #222;
}
#super_diretta_header .info .link {
	color: #919191;
}
.head_fake_left_bar {
  opacity: 0.4;
	background: #000;
}
.head_kind {
	color: rgba(255,255,255,0.5);
}
.head_kind a {
	color: rgba(255,255,255,0.5);
}
  
/*TABS*/  
#top_tab_list #tab_tag {
	color: #ddd;
}
#top_tab_list .tab_image a {
	color: #ddd;
}
#top_tab_list .tab_video a {
	color: #ddd;
}
#top_tab_list li {
	background: #111;
}
#top_tab_list #tab_tag:hover {
	color: #fff;
}
#top_tab_list a:hover {
	color: #fff;
}
  
	
/*GAME TAB MENU*/
#game_tab_menu {
	background: #121212;
}
.game_tab_actions li a {
	color: #ddd;
}
  
  
/*CONTENT*/
#content {
	max-width: 100%; /*Note 1: orifinal value is 1600px, chandged to 100% bc is only way to get rid of some of the white spaces */
	background: #222;
}
body#home_page #content {
	margin-top: 0px; /*Note 2: orifinal value is 15px, chandged to 0px to solve the white spaces issue */
}
.news-box a {
	color: #fff;
}
.sidebar_box, .reviews-box, .top_games {
	background-color: #111;
	color: #fff;
}
.news_navigation {
	border-bottom: solid 1px #555;
	border-top: solid 1px #555;
}
.news_navigation a {
	color: #999;
}
.small-news {
	border-top: 1px #555 solid;
}
.top_games hr {
	background: #555;
}
.popular-contents {
	background-color: #111;
}
.top_games .game_name a {
	color: #6c98be;
}
	

/*ARTICLE*/
.article_box {
	background: #111;
}
#comment_retweet .tweet {
	border-top: solid 1px #555;
	color: #999;
}
#comment_retweet .tweet .twitter_separator {
	display: none;
}
.article_source_list a {
	color: #aaa;
}
.article_box.box_icon {
	background: #111;
}
.newsletter-box {
	background: #444;
}
  
/*COMMENTS*/
#comments_container {
	border-top: 2px solid #444;
}
#comments_container .comment_container .comment_top_data .comment_info .username a {
	color: #fff;
}
#comments_container .comment_container {
	border-top: 1px solid #555;
}
#comments_container .endless_container {
	border-top: 1px solid #555;
}
#comments_container form textarea {
	border: 1px solid #333;
	outline: 0px none transparent;
	background: #121212;
  color: #fff;
}
#comments_container .comment_container .comment_top_data .comment_vote .vote {
	background: #555;
}
textarea.ui-autocomplete-input {
	border-color: #333 !important;
}
#modify-comment {
	background: #222;
}	
#comments_container .comment_container .comment_content .quote {
	background-color: #333;
}
#comments_container .error_report a:not(:hover) {
	color: #bbb;
}
#comments_container .error_report a {
	color: #ddd;
}
.article_summary a {
	color: #ddd;
}
#sticky_sidebar_column .top_gradient {
	background: rgb(34,34,34);
	background: -moz-linear-gradient(top,  rgba(34,34,34,1) 0%, rgba(34,34,34,0) 100%);
	background: -webkit-linear-gradient(top,  rgba(34,34,34,1) 0%,rgba(34,34,34,0) 100%);
	background: linear-gradient(to bottom,  rgba(34,34,34,1) 0%,rgba(34,34,34,0) 100%);
	/*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#222222', endColorstr='#222222',GradientType=0 );*/
	height: 15px;
	width: 300px;
}
#sticky_sidebar_column .bottom_gradient {
	background: rgb(34,34,34);
	background: -moz-linear-gradient(top,  rgba(34,34,34,0) 0%, rgba(34,34,34,1) 100%);
	background: -webkit-linear-gradient(top,  rgba(34,34,34,0) 0%,rgba(34,34,34,1) 100%);
	background: linear-gradient(to bottom,  rgba(34,34,34,0) 0%,rgba(34,34,34,0) 100%);
	/*filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#222222', endColorstr='#222222',GradientType=0 );*/
	height: 15px;
	width: 300px;
}
	
	
/*****FORUM*****/
body {
	background: #222;
}
.body_wrapper {
	background: #222;
}
body #navigation a {
	color: #fff;
}
body a, body a:hover {
	color: #f54c2c;
}
#navbar .floatcontainer .floatcontainer {
	background-color: #151515;
	border-bottom: 0px solid #c4c4c4;
}
.navtabs li.selected a.navtab {
	background: transparent;
	/*background: url("http://multiplayer.it/static/images/repeatable/bottom_shadow_50x50.png") repeat-x center bottom;*/
	color: #fff;
}
#forums .forumdata .moderators a.username, #forums .forumdata .moderators a.username:hover, #forums .popupmenu.memberaction a.username, #forums .popupmenu.memberaction a.username:hover {
	color: #aaa;
}
.toplinks form input.loginbutton {
	background: #f54c2c;
}
.toplinks form input.loginbutton:hover {
	background: #c44128;
}
.toplinks .remember {
	color: #fff;
}
.above_threadlist .threadpagenav .threadpagestats {
	color: #fff;
}
.pagination span a {
	color: #fff;
	border: 1px solid #333;
	background: #444;
}
.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	color: #ddd;
}
.forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	background: #333 none repeat-x;
}
.forumbit_nopost .forumrowdata, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	border-bottom: 1px solid #222;
	border-left: 1px solid #222;
	border-right: 1px solid #222;
}
.forumbit_nopost .subforumdescription {
	color: #ddd;
	background: #2d2d2d none repeat-x;
}
.toolsmenu {
	background: #333;
	border: 1px solid #252525;
}
.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .nopopupgroup li a {
	color: #ddd;
}
.toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
	color: #fff;
}
.time {
	color: #ddd;
}
.pagination span.selected a, .pagination span.selected a:hover, .pagination .popupctrl span.selected a, .pagination span a.popupctrl {
	background: #f54c2c;
	border-color: #f54c2c;
}
.announcements .announcerow, .announcerow .announceinfo, .announcerow .announcement {
    background: #3d3d3d;
}

.threadbit {
	background: #333;
	color: #fff;
}
.threadbit .alt {
	background: #2d2d2d;
}
.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored {
	border: 1px solid #252525;
}
.threadbit .sticky {
	background: #454545;
	border-top: 0;
}
.threadbit .sticky {
	border: 1px solid #252525;
}
.threadbit .deleted, .threadbit .ignored {
	background: #333;
	opacity: 0.25;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
	color: rgb(255, 255, 255);
}
.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
	border-left: 1px solid #555;
	background: #222 none;
}
.forumhead, .threadlisthead, .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .blockhead, #forum_icon_legend h5, .postbitdeleted .posthead, .postbitignored .posthead {
	background: #111;
	border-right: 1px solid #111;
}
.postbitlegacy .userinfo, .postbitlegacy .postdetails, .eventbit .eventdetails {
	background: #151515;
}
.postbit, .postbitlegacy, .eventbit {
	color: #ddd;
	border: 1px solid #555;
}
.postbit .posttitle, .postbitlegacy .title {
	border-bottom: 1px solid #555;
}
.navlinks {
	background: #2d2d2d none;
	border-top: 1px solid #222;
	color: #fff;
}
.navlinks a {
	color: #ddd;
}
.navlinks a:hover {
	color: #fff;
}
.wgo_block .section {
	color: #ddd;
}
.wgo_block .blockhead {
	background: #151515;
	border: 0px solid #111;
}
.wgo_block .blocksubhead {
	color: #ddd;
	background: #2d2d2d;
}
.wgo_block .section div, .wgo_block .section ol {
	background: #333;
}
.wgo_block .blockbody {
	border: 1px solid #222;
}
.blocksubhead {
	border-top: 1px solid #222;
}
#wgo_onlineusers .username, .inner_block #showthread_wrt_list a, #social_bookmarks_list a, #posting_rules a {
	color: #aaa;
}
.footer {
	-moz-box-shadow: -0px 0px 0px #c8c8c8;
	-webkit-box-shadow: -0px 0px 0px #c8c8c8;
	box-shadow: -0px 0px 0px #c8c8c8;
	background: #444 none;
	border: 0px solid #425769;
}
.textbox, textarea, select {
	border: 1px solid #333;
}
.forum_info .blockhead, .thread_info h4 {
	background: #111 repeat-x;
	_background-image: none;
	border: 0px solid #606060;
	-moz-box-shadow: 0px 0 2px #c8c8c8;
	-webkit-box-shadow: 0px 0 2px #c8c8c8;
	box-shadow: 0px 0 0px #c8c8c8;
}
.forum_info .blockbody, .thread_info .blockbody {
	border-bottom: 1px solid #222;
	border-left: 1px solid #222;
	border-right: 1px solid #222;
	background: #222 repeat-x;
	_background-image: none;
	-moz-box-shadow: 0px 0 2px #c8c8c8;
	-webkit-box-shadow: 0px 0 2px #c8c8c8;
	box-shadow: 0px 0 0px #c8c8c8;
}
.forum_info .blockbody, .thread_info .blockbody {
	background: #333 !important;
}
.forum_info, .thread_info {
	color: #ddd;
}
.blocksubhead {
	color: #ddd;
	background: #444;
	border-top: 1px solid #222;
}
	
/*Post Footer*/	
.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
	background: #333;
}
.postbitlegacy .postfoot .textcontrols a.report, .postbit .postfoot .textcontrols a.report {
	background: url(http://i.imgur.com/6qRZkng.png) no-repeat left;
}
.postbitlegacy .postfoot .textcontrols a.quickreply, .postbit .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.forwardpost {
	background: url(http://i.imgur.com/xCwccHr.png) no-repeat left;
}
.postbitlegacy .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.newreply {
	background: url(http://i.imgur.com/Mr3nORt.png) no-repeat left;
}
.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
	background: url(http://i.imgur.com/kpcNaTJ.png) no-repeat left;
}
.postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.editpost, .eventbit .eventfoot .textcontrols a.editevent {
	background: url(http://i.imgur.com/RlmBbNP.png) no-repeat left;
}
.postbitlegacy .postfoot .textcontrols span.seperator, .postbit .postfoot .textcontrols span.seperator {
	border-right: 1px solid #555;
}
.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile {
	color: #ddd;
}
	
/*Contenuto aggiuntivo*/
table {
	border-top: 1px solid #ffffff !important;
	border-right: 1px solid #cccccc !important;
	border-bottom: 1px solid #cccccc !important;
	background-color: #111 !important;
}

/*Editor*/
span.cke_skin_kama {
	border: 1px solid #222;
}
.cke_skin_kama *, .cke_skin_kama a:hover, .cke_skin_kama a:link, .cke_skin_kama a:visited, .cke_skin_kama a:active {
	background: #fff;
}
.texteditor.forum .cke_skin_kama textarea.cke_source, .texteditor.forum textarea {
	color: #222222;
	background: #fff none;
}
.cke_panel_block {
	color: #222222;
	background: #fff none;
}
.blockrow.posticons table {
	border: 0px solid #111 !important;
	background-color: #2d2d2d !important;
}
	
/*[SPOILER] tag**/
div.alt2 {
	border: 0px inset !important;
	background: #333 !important;
}
	
/*[QUOTE] tag**/
.bbcode_container div.bbcode_quote {
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	background: #323232 none;
	border-radius: 5px;
	border: 1px solid #111;
}
.bbcode_container .bbcode_quote_container {
	display: none;
}

/*Forum Archive*/
div.pagebody {
	background: #222;
	border: 0px solid silver;
}
#navbar, div.pda {
	background: #111;
	border: 1px solid #111;
}
#content {
	border: 1px solid #111;
}
	
/*FAQ*/
.faqtext + .faqlinks {
	border-top: solid 1px #222;
}
.faqblock .blockbody {
	border-left: 1px solid #222;
	border-right: 1px solid #222;
	border-bottom: 1px solid #222;
}
.blockbody {
	background: #2d2d2d none no-repeat;
}
.formcontrols .blockrow {
	background: #2d2d2d repeat-x center bottom;
	border-top: solid 1px #222;
}
.blockrow {
	background: #2d2d2d;
}
.faqlinks {
	background: #333 none repeat-x center bottom;
}
.formcontrols, .blockbody.settings_form_border {
	border-bottom: 1px solid #222;
	border-left: 1px solid #222;
	border-right: 1px solid #222;
}
.blockfoot, .blocksubfoot {
	background: #333 none;
}
	
/*NONESENSE FIX*/
#top_content .video-container, .rmp-ad-block-container, .rmp-ad-schedule-background, .rmp-ad-block-text, .wrapper-shopping, #video-playback {
  display: none;
}
iframe[id^="vsn"] {
  /*display: none;*/
}
	
}
