css:
@-moz-document url-prefix("http://play.typeracer.com/"), domain("play.typeracer.com"), domain("pit.typeracer.com"), domain("data.typeracer.com") {
html {
    min-height: 100%;
}
body {
    height: 100%;
    background: 0;
}
a {
    color: #333;
}
a:hover {
    color: #555;
}
/* Navigation */

.themeHeader,
.mainUserHeader {
    background: 0;
    background-color: #292b2c;
}
.themeHeader a {
    color: #FFF;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: #EEE;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title h1 a {
    text-transform: uppercase;
}
.themeHeader .navigation {
    padding: 22px 20px;
    height: 0;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #EEE;
}
.subTitle {
    display: none;
}
/* Player Info */

.playerInfoPopup {
    background: #292b2c;
    color: #FFF;
    border-color: #222;
}
.playerInfoPopup a {
    color: #EEE;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: #FFF;
}
/* Content */

.main,
.themeContent {
    background: 0;
    background-color: #FFF;
    margin: 0 auto;
    max-width: 900px;
}
.mainViewportHolder,
.mainViewport {
    width: 900px;
}
.nonHideableWords {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    font-size: 20px !important;
    /*font-family: Garamond, Baskerville, "Baskerville Old Face", "Hoefler Text", "Times New Roman", serif !important;*/
    /*font-family: Bookman !important;*/
}
.friendsTable .headerRow {
    font-size: 14px;
    text-transform: capitalize;
    font-style: normal !important;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #E9E9E9;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #F9F9F9;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #333;
}
/* Footer */

.footer {
    background: 0;
    background-color: #292b2c;
    color: #EEE;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: 0;
}
.popupContent {
    background: #292b2c;
}
/* DARK THEME */

body {
    background: #111 !important;
    color: #999;
}
a {
    color: #888;
}
a:hover,
a:focus {
    color: #777;
}
.mainViewport,
.main,
.themeContent,
.statsView table {
    background-color: #111;
    color: #999;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #222;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #111;
}
.popupContent {
    background: #111;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #888;
}
.gwt-TabBar .gwt-TabBarItem:hover,
.gwt-TabBar .gwt-TabBarItem:focus {
    color: #777;
}
}
