css:
@-moz-document domain("feedly.com") {
*{font-family:MuseoSans-700, 'Helvetica Neue', Helvetica, Arial, sans-serif !important}
body{font-size:13px !important}
.u0Entry .title,
#feedlyTabs *{font-size:14px !important}
h1,
.inlineFrame .u100entry .title,
.hhint{font-size:18px !important}
.navSelector{font-size:16px !important}

.entryList .label{margin-left:10px !important}

#section0,
#floatingBar,
#fullyLoaded,
.u0Entry:hover .condensedTools,
.bottomWikiWidget,
.viewerIcon.highlightableIcon,
.abZone,
.viewerIcon.requiresLogin.tagHolder,
.wikiWidgetShareHolder,
img[alt="Custom Sharing Tool"],
img[data-app-popup="askPickNotebook"],
img[data-page-entry-action="tweetEntry"],
img[data-page-entry-action="linkedInEntry"],
img[data-page-entry-action="bufferEntry"],
img[data-page-entry-action_="mailEntry"],
img[data-page-entry-action="facebookEntry"],
img[title="Facebook"],
img[title="Twitter"],
img[title="Add to Instapaper"],
.metadata,
img[data-app-action="jumpToNext"],
img[data-app-action="refresh"],
img[data-page-action-input="6"],
img[data-page-action-input="4"],
img[data-page-action-input="0"],
img[data-page-action-input="100"],
.pin,
.panels div[style="height:17px"],
#popupPart .section:nth-child(1),
#popupPart .section:nth-child(2),
#popupPart .section:nth-child(4),
.customizer .label,
#feedlyProBar,
#searchBox,
#feedlyMessageBar{display:none !important}

#feedlyFrame {width:auto !important}
#feedlyPart0.area{padding-left: 0px!important}
#feedlyPart,
.wikiWidgetAction {padding-right:0 !important}
.u0Entry {position: relative;margin-left:0 !important; padding-bottom: 6px !important;padding-top: 6px !important;white-space: nowrap !important;}
#feedlyTitleBar{height:auto !important}
.pageActionBar{margin-top:-3px !important}
.u0EntryList{margin-top:0 !important}
.u100Entry .title{margin-top:0 !important;margin-bottom:0 !important}
.hhint{margin-left:10px !important}
.inlineFrame .u100entry .title {margin-left: 50px !important;}

#feedlyPageHeader {padding: 12px !important;margin:0 !important;}
h1 .hhint{display:inline !important}
.entriesLoadingMessage{text-align: center !important;}

.u0Entry .lastModified {position:absolute;right:0px;top:6px;padding-right:10px !important; background-color: white;}

.inlineFrame {position: relative;padding:15px 33px 15px 20px !important;border-bottom:0 !important;border-right:0 !important;border-left:3px #3C97DE solid !important;margin-left:-1px !important}
.condensed .entryholder .u100entry{margin-left:0 !important;position:relative}
.topWikiWidget {padding-bottom: 0 !important;border-bottom: 0 !important;margin-bottom: 0 !important;position: absolute;left: -21px;top: 5px;}
.inlineFrame .u100entry .title{margin-left: 10px;}

.infoBox{margin:50px 0 !important;text-align:center}
#navSelectorHolder{height:auto !important}
.navSelector{margin:0 !important;padding:0 !important;width:50% !important;text-align:center !important;line-height: 49px !important;}
#feedlyTabs{width:auto !important;padding-right:10px !important;padding-left:6px !important}
#feedlyTabs div[style="margin-bottom:20px; margin-top:40px"]{margin-top:6px !important}

#feedlyTabsHolder .feedIndex .nonEmpty {font-weight: bold !important;}
.pageAction{opacity:1 !important;padding:3px 5px !important;border-radius: 3px !important;float:right}
h1 .hAction {top:-2px !important; margin-right:12px !important; text-decoration: none !important;}
#feedlyPart{float:none !important}
.customizer .section {padding-bottom: 0 !important;border-bottom: 0 !important;margin-bottom: 0 !important;}
.popup{box-shadow:none !important;padding: 0 !important}
.listViewCustomizerLabel{padding:10px 10px 10px 20px !important}

.inlineFrame .entryBody img{width:auto !important;height:auto !important;max-width:1000px !important}

.entryBody a,
*.action,
.categoryUnreadCountHint span{color: #3498db !important;}
.u0Entry .title{color: #656d73 !important;}
.u0Entry .sourcetitle a{color: rgb(44, 62, 80) !important;}
h1 .hhint,
h1 .hAction {color: rgb(154, 166, 175) !important;}
.u0Summary{color: rgb(153, 160, 165) !important;}

body{background-color:white !important}
.panels{background-color: #EBEEF1;}
.navSelector,
.pageAction:hover,
.hAction:hover{background-color:#E7EAED !important}
.navSelector:hover{background-color:#D9DEE2 !important}
#feedlyTabsHolder .header:hover,
.moreHandle:hover,
.tab .feedIndex:hover{background-color:#DBE0E4}

#feedlyPageHeader,
.listViewCustomizerLabel:hover{background-color:#F7F9FA}
.hAction,
.pageAction{background-color:#E7ECF0 !important}
.u0Entry:hover,

.u0Entry:hover .u0Entry .lastModified {background-color: white !important}

.navSelector{border-bottom-color:#CCD2D8 !important}
.u0Entry{border-top-color:#E1E8ED !important}
#section0_column0{border-bottom:1px #E1E8ED solid !important}
.panels {border-right: 1px #DCE1E7 solid;}
.popup{border-color:#CAD0D5 !important}
}
