css:
#titlebar{
margin-bottom:2px !important;
height: 0px !important;
}

#urlbar[focused="true"] { background-color:transparent !important; color:#666666 !important; border-top: 0px solid rgba(0,0,0,0); 
border-left: 0px !important; 
border-right: 0px !important;
border-color: rgba(0,0,0,0) !important;
}




#nav-bar {
	background: rgba(225,225,225,100) !important;
	margin-top: 0px !important;
	margin-left: 0px !important;
	margin-right: 0px !important;
	margin-bottom: 0px !important;
	padding: 6px !important;
	border-radius: 0px 0px 0px 0px !important;
	border-top: 0px #000000!important;
	box-shadow: none!important;
        border-bottom: 2px solid #ccc !important;
}

#urlbar {
	background: transparent !important;
	padding: 5px !important;
	text-align: center!important;
	padding-left: 8px !important;
	margin-top: 0px !important;
	margin-bottom: 0px !important;
	margin-left: 6px !important;
	margin-right: 4px !important;
	border-top: 1px solid rgba(0,0,0,100)!important;
	border-bottom: 1px solid rgba(0,0,0,100)!important;
	border-left: 1px solid rgba(0,0,0,100)!important;
	border-right: 1px solid rgba(0,0,0,100)!important;
	color: #757983 !important;
	font-family: "Segoe UI Semibold",Helvetica,Arial,sans-serif;
	font-size: 11px;
	box-shadow: none !important;
}





    .tabbrowser-tab:not([pinned]) .tab-icon-image { 
      display: none !important;
}
    .tabbrowser-tab:not([pinned]) .tab-text { 
      margin-left:0px !important; 
      margin-right:0px !important;
}

.tabbrowser-tabs{

  position:fixed !important;
  top:0!important;
  min-width:-moz-available !important;
  padding:4px 0px 0px !important;
}

#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]){
  -moz-appearance:-moz-win-glass !important;
  background-color:transparent !important;
}

.tabs-stack{
 min-width:-moz-available !important;
}

#navigator-toolbox{
  margin-top:32px !important;
}

menupopup{
  -moz-appearance:menupopup !important;
  background: rgba(225,225,225,100)!important;
}
toolbar #star-button{
display:none !important;
}


toolbargrippy {
  display: none;
}

.toolbar-holder {
  border-left: none !important;
}










.tab-close-button, 
.tabs-closebutton, 
#find-closebutton {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

tab[selected="true"] .tab-close-button, 
.tabs-closebutton, 
tab:hover .tab-close-button, 
#find-closebutton {
  list-style-image: 
url("data:image/png;base64,") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

tab .tab-close-button:hover, 
.tabs-closebutton:hover, 
#find-closebutton:hover {
  list-style-image:
url("data:image/png;base64,") !important;
}


@-moz-document url("about:blank"), url("about:newtab") {

* {

background: rgba(150,150,150,100) !important;

}
}




.tabbrowser-tab[selected="true"],
.tabs-bottom {
  -moz-appearance: none !important;
   border-top: 0px solid transparent !important; 
   border-right:  0px solid rgba(225,225,225,100) !important;
   border-bottom: 2px solid rgba(225,225,225,100) !important;
   border-left:  0px solid transparent !important; 
   background: rgba(225,225,225,100) !important }
.tabs-right {
  -moz-appearance: none !important;
   border-top: 0px solid rgba(85,140,215,100) !important; 
   border-right:  0px solid rgba(225,225,225,0) !important;
   border-bottom: 0px solid rgba(85,140,215,100) !important;
   border-left:  0px solid transparent !important; }

#content tab:not([selected]) {
font-style: italic !important;
background-color: rgba(250,250,250,0) !important;
} 



.tabbrowser-tab[selected="false"],
.tabs-bottom {
  -moz-appearance: none !important;
   border-top: 0px solid transparent !important; 
   border-right:  0px solid rgba(50,90,250,100) !important;
   border-bottom: 2px solid rgba(225,225,225,100) !important;
   border-left:  0px solid transparent !important;
   background: rgba(25,25,25,100) !important }








*.tabbrowser-tabs
{
padding: 1px 0 0 0 !important;
background: rgba(225,225,225,100) !important;
background-color: transparent !important;
}
*.tabbrowser-tabs > *.tabbrowser-tab
{
max-height: none!important;
padding: 6px !important;
border-left: 6px solid !important;
border-right: 6px solid !important;
-moz-border-left-colors: rgba(225, 225, 225, 100) !important;
-moz-border-right-colors: rgba(225, 225, 225, 100) !important;


-moz-box-sizing: border-box !important;
}

*.tabbrowser-tabs > *.tabbrowser-tab
{
height: 0px !important;
margin-top: 2px !important;
}
*.tabbrowser-tabs > *.tabbrowser-tab:hover
{
height: 0px !important;
margin-top: 2px !important;
}



.tabbrowser-tab {
  -moz-appearance: none;
  margin-left: 0px;
  margin-right: 1px;
  background-image: linear-gradient(rgba(220,220,220,255), rgba(220,220,220,255));
}


.tabbrowser-tab:not([selected="true"]):hover {
  background-image: linear-gradient(rgba(220,220,220,255), rgba(220,220,220,255));
}




.tab-text { text-align: center !important; }
.tab-icon {
  display: none;
}







