css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ernstchan.com") {
    body {
        background-color: #eee;
        color: #000000;
        font: normal 0.8em sans-serif;
    }
    .post {
        background-color: #aca7cc;
        border-right: 1px solid #afa7e0;
        border-bottom: 1px solid #9891c3;
        margin-bottom: 0.5em;
        overflow: auto;
    }
    .postername {
        color: #381fb8;
    }
    .reflink a:link,
    a:visited,
    a:hover {
        color: #000000;
    }
    a:link,
    a:visited,
    a:hover {
        color: #392e93;
    }
    blockquote.unkfunc,
    span.quote {
        color: #237A7E;
        margin: 0;
    }
    .filesize {
        font-style: italic;
        text-decoration: none;
    }
    .file {
        background-color: #847cb9;
        text-align: center;
        margin: 0 1.2em 1.2em 0;
        float: left;
        display: block;
        text-align: left;
        padding-left: 0.1em;
        background-color: #9999bb;
        border-color: #aaaaff;
        border-style: solid;
        border-width: 1px;
    }
    .filename {
        text-decoration: underline;
        text-decoration-color: #222299;
    }
    .thread_head {
        clear: left;
        background-color: #EEEEEE;
        padding: 5px;
    }
    .thread_OP .post {
        background-color: #EEEEEE;
        border: 0;
        margin: 0;
    }
    .thread_OP .file {
        background-color: #E4E4E4;
        border-color: #BBBBBB !important;
        border-style: solid;
        border-width: 1px;
    }
    .thread_OP .post_body {
        padding-top: 0.2em;
    }
    .thread_reply .post_body {
        padding-top: 0.7em;
        padding-left: 1.3em;
    }
    img[src="/img/icons/hide.png"] {
        height: 0px !important;
        width: 0px !important;
        padding-left: 15px !important;
        padding-top: 15px !important;
        background: url(https://i.imgur.com/Z2AoLuj.png) no-repeat !important;
    }
    img[src="/img/icons/show.png"] {
        height: 0px !important;
        width: 0px !important;
        padding-left: 15px !important;
        padding-top: 15px !important;
        background: url(https://i.imgur.com/1MqrKvG.png) no-repeat !important;
    }
    img[src="/banner.pl?board=int"] {
        height: 0px !important;
        width: 0px !important;
        padding-left: 300px !important;
        padding-top: 100px !important;
        background: url(https://i.imgur.com/RIfGmVP.png) no-repeat !important;
        border-color: #000000;
        border-style: solid;
        border-width: 1px;
    }
    .subject {
        color: rgb(204, 51, 51) !important;
        font-size: large !important;
    }
    .sage:before {
        content: "SAGE!";
        color: rgb(255, 0, 0);
        visibility: visible;
        font-size: 13px;
    }
    .sage {
        visibility: hidden;
        font-size: 0px;
    }
    
    .postername:before {
        content: "Bernd";
        visibility: visible;
        font-size: 13px;
    }
    .postername {
        visibility: hidden;
        font-size: 0px;
    }    
    
    .boardname:before {
        content: "/int/ - International";
        visibility: visible;
        font-size: 27px;
    }
    .boardname {
        visibility: hidden;
        font-size: 0px;
    }
    ul.pagelist {
        background-color: #eee;
        border-right: 0px;
        border-bottom: 0px;
        padding: 0em;
        float: left;
    }
    .postblock {
        background-color: #313370;
        color: #FFFFFF;
        padding: 0.5em;
    }
    .info {
        background-color: #aca7cc;
        border-right: 1px solid #BFB5A1;
        border-bottom: 1px solid #BFB5A1;
        border-left: 1px solid #BFB5A1;
        padding: 1em;
        margin: 0 0 0.5em 0;
    }
    .title,
    .info th {
        background-color: #9891c3;
        color: #FFFFFF;
        font-weight: bold;
        padding: 5px;
    }
    #ancbox {
        padding-top: 0.5em;
        padding-left: 0.5em;
        padding-right: 0.5em;
        bottom: 0.5em;
        box-shadow: 0 0 0 100000px rgba(112, 107, 94, 0.5);
    }
    #desbox {
        top: -0.25em;
        padding-top: 0.5em;
        padding-left: 0.5em;
        padding-right: 0.5em;
    }
    .context {
        position: relative;
        clear: left;
    }
    .context>div,
    #preview {
        border: 1px solid #9891c3;
        background-color: #eeeeee;
        position: absolute;
        left: 10em;
    }
    #preview {
        box-shadow: 1px 2px 2px 1px solid #9891c3;
        ;
    }
    #preview .post {
        margin-bottom: 0;
        border-color: #9891c3;
    }
    .thread_reply.highlight .post,
    .thread_reply:target .post {
        background-color: #aca7cc !important;
        border-width: 2px;
        border-style: dashed;
        border-color: #42458c;
    }
    .thread_reply.highlight .post .file,
    .thread_reply:target .post .file {
        background-color: #9999bb !important;
    }
    .thread_OP.highlight .post,
    .thread_OP:target .post {
        background-color: #eeeeee !important;
    }
    .thread_OP.highlight .post .file,
    .thread_OP:target .post .file {
        background-color: #E4E4E4 !important;
    }
    .highlight .post,
    :target .post {
        background-color: inherit;
    }
    .highlight .post .file,
    :target .post .file {
        background-color: inherit;
    }
}
