css:
@-moz-document domain("w3school.com.cn") {
div#header,
div#header_index {
    display: none
}
div#sidebar {
    display: none
}
div#footer {
    display: none
}
div#tpn {
    display: none
}
body {
    background: none
}
div#navsecond {
    background-color: #FFF;
    border-radius: 10px
}
div#maincontent {
    width: 829px;
    margin: 25px 0 25px 26px;
    padding-left: 50px;
    background-color: #FFF;
    border-radius: 10px;
    box-shadow: 0 0 20px rgba(3, 3, 3, .3)
}
html>body div#navsecond {
    margin: 25px 15px;
    text-align: center
}
div#navsecond {
    margin: 0;
    box-shadow: 0 0 20px rgba(3, 3, 3, .3)
}
div#maincontent div {
    width: auto;
    margin: 0 0 10px;
    border-top: none;
    border-bottom: none
}
html {
    background: #F5F5F5
}
div#wrapper {
    background: none
}
div#navfirst {
    margin: 25px auto 0 auto;
    background: none
}
div#navfirst ul#menu {
    display: inline-block;
    border-radius: 10px;
    box-shadow: 0 0 20px rgba(3, 3, 3, .3)
}
div#navfirst ul#menu li a {
    border-radius: 10px
}
div#course {
    background: none;
    padding: 0;
    text-align: center
}
body {
    font-family: "Helvetica Neue"
}
div#navsecond li.currentLink a:link,
div#navsecond li.currentLink a:visited {
    color: #5F89DF
}
div#navsecond a:hover,
div#navsecond a:active {
    text-decoration: none;
    color: #5F89DF
}
div#navsecond li.currentLink a:hover {
    text-decoration: none
}
div#navsecond h2,
div#sidebar h2 {
    margin: 25px 0 0;
    font-size: 16px
}
div#navsecond ul,
div#sidebar ul {
    margin-left: 0;
    margin-bottom: 15px;
    font-size: 13px
}
div#navsecond li,
div#sidebar li {
    line-height: 23px
}
div#selected {
    text-align: center
}
div#navsecond a#link_about {
    display: none
}
div#navsecond a#link_help {
    display: none
}
div#maincontent h1 {
    margin: 0;
    padding-top: 30px;
    padding-bottom: 10px;
    text-align: center
}
strong {
    font-family: "Helvetica Neue";
    font-size: 15px
}
div#maincontent p {
    padding-right: 45px;
    font-size: 15px
}
div#maincontent h2,
div#sidebar div#ad h2,
body#homefirst div#maincontent h3 {
    font-size: 16px
}
body#homefirst div#maincontent,
div#maincontent h1,
div#maincontent h2,
div#sidebar div#ad h2 {
    font-family: "Helvetica Neue"
}
* {
    font-family: "Helvetica Neue"
}
::selection {
    background-color: rgba(3, 3, 3, .4);
    color: rgba(255, 255, 255, 1)
}
a:link,
a:visited {
    text-decoration: none;
    color: #5F89DF
}
a:hover,
a:active {
    text-decoration: underline
}
pre {
    width: 600px;
    margin: 20px auto;
    padding: 15px 30px;
    background: #F9F9F9;
    font-family: "Helvetica Neue";
    font-size: 14px;
    border: 1px solid rgba(9, 9, 9, .1);
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(3, 3, 3, .1);
}
div#maincontent p.tiy {
    margin: 10px 360px;
    padding: 0;
    background-color: #5F89DF;
    border: 1px solid #5F89DF;
    border-radius: 8px;
    box-shadow: 0 0 20px rgba(3, 3, 3, .1)
}
div#maincontent p.tiy a:link,
div#maincontent p.tiy a:visited {
    display: inline-block;
    padding: 5px 12px;
    border-radius: 20px
}
div#maincontent p.tiy:hover {
    background-color: #5577FF
}
dl {
    padding-right: 48px;
    font-size: 15px
}
div#navsecond h2,
div#sidebar h2,
div#maincontent h3,
div#maincontent h4 {
    font-size: 16px
}
dl.define dd {
    line-height: 30px
}
ul#menu li,
ul#menu a {
    border-radius: 10px
}
div#tpn ul.prenext,
div#bpn ul.prenext {
    margin-right: 50px;
    float: right
}



/* 5.27 更新 */
div#navfirst ul#menu {
	width: 1100px;
    overflow: hidden
}

div#navsecond a, div#sidebar a {
    padding: 5px 0
}

div#navsecond a:hover, div#navsecond a:active {
	color: #FFF
}

div#navsecond li.currentLink a:link, div#navsecond li.currentLink a:visited {
    background: #aaa;
    color: #FFF
}

div#navsecond a:active {
    background: #aaa;
    color: #FFF
}

a:link, a:visited {
    border-bottom: none
}

ul#menu li, ul#menu a {
	border-radius: 0
}

div#navfirst ul#menu li a {
    border-radius: 0
}

div#navsecond h2, div#sidebar h2 {
	padding: 5px 0
}

div#maincontent h1 {
	padding-top: 0;
    padding-bottom: 0;
    margin-left: -50px
}

html>body div#navsecond {
	overflow: hidden
}

body#homesecond div#navsecond li {
	margin: 0
}

div#navsecond ul, div#sidebar ul {
	margin-bottom: 0
}

div#navsecond {
	width: 170px
}

div#maincontent {
    width: 855px;
    margin: 25px 0 25px 10px;
}

pre {
	line-height: 1.8em
}

table.dataintable {
	width: 825px
}

div#navsecond h2#link_about {
	display: none
}

div#navsecond h2#link_help {
	display: none
}

div#selected {
    padding: 0;
	background: none
}

div#navfirst ul#menu li a {
	background-color: rgba(255,255,255,.6)
}

div#wrapper {
	width: 1100px
}

div#navfirst {
	width: 1100px
}

html>body div#navsecond {
	margin: 25px 15px 25px 0
}

html {
	min-width: 1100px
}
}
