css:
@-moz-document domain("7cups.com") {
.navbar-fixed-top {
    background-color: #663399;
    background-image: none;
}
nav.hidden-therapy.navbar.navbar-primary-rev.navbar-fixed-top {
    background-image: linear-gradient(to right, #663399, #000000);
    border-style: none;
}
body {
    background-color: #303030;
    color: #D4D9E6;
}
navbar,
.navbar a {
    color: #D4D9E6;
}
.navbar .specialnav {
    border: .15rem solid #198EA0;
}
.chatBox .chatInput #Comment {
    background-color: #274B80;
    border: 0 solid #043565;
}
.chatBox .card-title {
    color: #D4D9E6;
    border-bottom: solid .0625rem #043565;
}
.card-block .card-title {
    color: #D4D9E6 !important;
}
.form-control {
    color: #D4D9E6;
    background-color: #D6FF00;
    border: 1px solid #043565;
}
.card {
    background-color: #043565;
}
#actionButtons {
    background-color: #043565;
}
.list-group-item {
    background-color: #043565;
}
.chatBox .youWrap {
    color: #D4D9E6;
    background-color: #216D9E;
}
.chatBox .details {
    color: #FFFFFF;
}
.modal-content {
    background-color: #043565;   
}
.nav-tabs.chat-tabs .nav-link.active {
    color: #257EF6;
    border-bottom: solid .0625rem #216D9E;
}
.nav-tabs .nav-item.open .nav-link,
.nav-tabs .nav-item.open .nav-link:focus,
.nav-tabs .nav-item.open .nav-link:hover,
.nav-tabs .nav-link.active,
.nav-tabs .nav-link.active:focus,
.nav-tabs .nav-link.active:hover {
    background-color: #1e3962;
}
.navbar-primary-rev .notification-menu>.dropdown-item {
    color: #D4D9E6;
}
.navbar-primary-rev .notification-menu>.dropdown-item:hover {
    background-color: #663399;
}
.card {
    color: #84BBE8;
}
a.list-group-item,
button.list-group-item {
    color: #81BFEC;
}
.note,
.note-free,
.progress[value],
.stats .note-free {
    color: #D4D9E6;
}
.dropdown-menu {
    background-color: #043565;
}
.chat-list-downbar,
.downbar {
    border-top: solid .0625rem #216D9E;
}
.chatBox .details a {
    color: #6DA9DB;
}
.card {
    color: #8A90A3;
}
.chatBox .meWrap {
    color: #D4D9E6;
    background-color: #663399;
}
.btn-simple {
    background-color: #303030;
}
.btn-simple.btn-default {
    color: #1C8AF2;
    border: .0625rem solid #663399;
}
.btn-primary, .btn-simple.btn-primary, .btn.btn-sm {
    background-color: #663399;
    color: #D4D9E6;
    border: #663399;
}
.btn-info {
    background-color: #8c53c6;
    border: #8c53c6;
}
.btn-warning, .btn-simple.btn-warning, .btn-full {
 	background-color: #303030;
    color: #99ccff;
    border: #99ccff solid .0625rem;
}
.btn-primary:hover,
.btn-info:hover,
.btn-simple.btn-warning:hover,
.btn.btn-sm:hover{
    background-color: #0099ff;
}
a, a:hover {
    color: #99ccff;
}
.chat-list a.list-group-item.newMsg,
.chat-list a.list-group-item.newMsg:hover,
.chat-list a.list-group-item.activeConv,
.chat-list a.list-group-item.activeConv:hover,
a.list-group-item:focus,
a.list-group-item:hover,
button.list-group-item:focus,
button.list-group-item:hover {
    background-color: #663399;
}
.chat-list a.list-group-item.newMsg:hover,
.chat-list a.list-group-item.activeConv:hover,
a.list-group-item:hover,
button.list-group-item:hover {
    color: #D4D9E6;
}
.form-control {
    color: #D4D9E6;
    background-color: #606060;
    border: 1px solid #043565;
}
.userName {
    color: #D4D9E6;
}
.threadTitle, .forumInfo h3 a{
    color: #D4D9E6;
}
.table-forum tr:hover{
    background-color: #663399 !important;
}
.table-forum tr, .details, .forumInfo table a .details{
 	color:#D4D9E6;   
}
.breadcrumb{
    background-color: #043565;
}
ul.reposterList .reposterListComment {
 	color: #D4D9E6;   
}
.appAd{
 	display: none;
}
#bottomStatus,
.bottomStatus {
    color: #043565 !important;
    background-color: #663399 !important;
}
#bottomStatus.heavyLoad {
    color: #043565 !important;
    background-color: #043565 !important;
    background: #663399!important;
}
}
