css:
@-moz-document domain("www.meneame.net") {
img {
    opacity:0.2;
}
img:hover {
    opacity:1;
}
.header-top-wrapper {
    background-color: #666666;
}
ul#userinfo a.notifications span {
    color: #000000;
    border: 3px solid #AAAAAA;
}
.header-menu01 a.submit_new_post{
    color: #000033;
}
.header-menu01 a.submit_new_post:hover{
    color: #000099;
}
.header-menu01 ul li a:hover {
    color: #000099;
}
.news-shakeit{
    color: #000066;
}
.news-shakeit .votes, .news-shakeit .clics {
    background-color: #EEEEFF;
}
.news-shakeit .votes a {
    color: #000066;
}
.news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
    border-color: #EEEEFF;
    background-color: #fff;
}
.menealo a {
    background-color: #DDDDFF;
    background-image: linear-gradient(-180deg, #9999AA 0%, #9999CC 100%);
    box-shadow: none;
    border: 1px solid #000033;
}
.menealo span {
    background-color: #000099;
    background-image: none;
    border: 1px solid #000033;
    border-bottom: 1px solid #000033;
}
.news-submitted a {
    color: #000066;
}
a:visited {
    color: #000066;
}
a:hover, a:focus {
    color: #000099;
}
.header-menu01 .selected a, .header-menu01 ul li a:hover {
    background: linear-gradient(#E9E9E9, #E9E9E9, #E9E9E9, #E9E9E9, #E9E9E9, #000066);
}
.sidebox.brown .header {
    background: #000066;
}
.sidebox .votes span, .sidebox .clics span {
    border: 2px solid #AAAAAA;
    color: #000000;
}
.news-details {
    border-top: 1px solid #DDDDFF;
    border-bottom: 1px solid #DDDDFF;
}
.news-details a.comments {
    background-color: #DDDDFF;
    color: #000066;
    font-weight: bold;
    margin-top: 0px;
    padding: 0px 15px;
}
.news-details button.social-share {
    border-right: 1px solid #DDDDFF;
}
.news-details button {
    color: #000099;
}
.news-details a i, .news-details button i {
    color: #000099;
}
button.link.favorite {
    border-right: 1px solid #DDDDFF;
}
.news-details span.votes-up i, .news-details span.votes-anonymous i, .news-details span.votes-down i {
    color: #000099;
}
.news-details span.karma-letter {
    color: #000099;
}
select {
    color: #000099;
}
.sidebox-rounded.orange .header {
    background: #000066 !important;
}
.sidebox.red .header {
    background-color: #000099;
}
.sidebox h5 a:hover {
    color: #000099;
}
.sidebox.red .votes span, .sidebox.red .clics span {
    border: 2px solid #AAAAAA;
    color: #000000;
}
.sidebox-rounded .datetime {
    color: #000099;
}
.sidebox-rounded div.finished {
    color: #000099 !important;
    background-color: #DDDDFF !important;
}
.sidebox-rounded a.show-responses {
    color: #000099 !important; 
}
#widget-official-subs-slider{
    display:none;
}
.news-summary .warn, .story-blog .warn {
    background-color: #EEEEFF;
}
.pages span.current {
    color: #FFF;
    background-color: #000099;
    border: solid 1px #000099;
}
.pages a:hover {
    color: #FFF;
    background-color: #000099;
    border-color: #000099;
}
.pages a {
    text-decoration: none;
    border: solid 1px #000099;
}
.pages a, .pages span {
    color: #000099;
}
.pages span.nextprev {
    border: 1px solid #EEEEFF;
    color: #EEEEFF;
}
.sidebox .mainsites a {
    color: #000099;
}
#footcol1 h5, #footcol2 h5, #footcol3 h5, #footcol4 h5, #footcol5 h5 {
    color: #000099;
}
#footthingy {
    background: none;
}
#footthingy p {
    border-color: #000099;
}
.news-details a:link, .news-details a:visited, .news-tags, .news-tags a:link, .news-tags a:visited {
    color: #000099;
}
h2 {
    color: #000099;
}
.comment-body {
    background: #F5F5FF;
}
.comment.author .comment-body {
     background-color: #EBEDF4;
    border:none;
}
.comment.high .comment-body {
   background-color: #EBEDFF;
    border: none;
}
.comment-header .comment-order {
    color: #000099;
}
.comment-footer a.comment-answers, .comment-footer a:hover {
    color: #000099;
}
.comment-header a {
    color: #000099;
}
fieldset {
    background: #EEEEFF;
}
legend, legend a, legend a:visited {
    background: #000099;
    color: #fff;
}
.section h1 {
    color: #000099;
}
a.action-link, .action-text {
    background-color: #000099;
    color: white;
}
#notifier_panel div:before {
    color: #000099;
}
.button, button, input[type="button"], input[type="submit"] {
    background-color: #000099;
    color: #fff;
}
.button:hover, button:hover, input[type="button"]:hover, input[type="submit"]:hover {
    background-color: #000066;
}
label {
    color: #000099;
}
.genericform {
    color: #000099;
}
#toplink {
    background: #EEEEFF;
}
#toplink a.footer {
    background-color: #000099;
}
#toplink h1 a {
    color: #000066;
}
.note {
    font-size: 85%;
    font-weight: normal;
    color: #000099;
}
.sidebox-sub .btn.btn-mnm {
    color: #000099;
    border: 1px solid #000099;
}
.error {
    font-size: 120%;
    margin: 0 0 10px 0;
    padding: 10px;
    color: #000000;
}
h3 {
    color: #000066;
}
.button:disabled, button:disabled, input[type="button"]:disabled, input[type="submit"]:disabled {
    background: #DDDDFF;
}
.subs-listing .followers span {
    border: 2px solid #000099;
}
.subs-listing .followers {
    color: #000099;
}
.subs-listing .name a {
    font-size: 18px;
    color: #000099;
}
.section h2 {
    color: #000066;
}
.form.form-edit label.title {
    color: #000066;
}
.btn.btn-mnm {
    background-color: #000066;
}
.btn.btn-mnm:hover {
    background-color: #000099;
    color:#FFF;
}
.form-control:focus {
    border-color: #cdaefd;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 2px rgba(100, 0, 255, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 2px rgba(100, 0, 255, .6);
}
.mnm-tooltip.top .tooltip-arrow {
    border-top-color: #EEEEFF; 
}
.mnm-tooltip.top-left .tooltip-arrow { 
    border-top-color: #EEEEFF; 
}
.mnm-tooltip.top-right .tooltip-arrow { 
	border-top-color: #EEEEFF; 
}
.mnm-tooltip.right .tooltip-arrow { 
    border-right-color: #EEEEFF; 
}
.mnm-tooltip.left .tooltip-arrow { 
   border-left-color: #EEEEFF; 
}
.mnm-tooltip.bottom .tooltip-arrow { 
    border-bottom-color: #EEEEFF; 
}
.mnm-tooltip.bottom-left .tooltip-arrow {
    border-bottom-color: #EEEEFF; 
}
.mnm-tooltip.bottom-right .tooltip-arrow { 
    border-bottom-color: #EEEEFF; 
}
.tooltip-inner {
    color: #000099;
    background-color: #EEEEFF;
}
.sneaker-legend {
    color: #000099;
    background: #EEEEFF;
    border-bottom-color: #DDDDFF;
}
.sneaker-title strong {
    color: #000099;
}
.sneaker-item {
    border-bottom-color: #DDDDFF;
}
.sneaker-published {
    color: #6666AA;
}
.voters-list {
    color: #000099;
    border-color: #000099;
}
#voters-container div {
    border-bottom-color: #000099 !important;
}
.sidebox-sub a {
    color: #000099;
}
.news-summary .warn:before, .story-blog .warn:before {
    color: #000099;
}
.uploadFile-button {
    filter: hue-rotate(230deg);
}
div.box div.logo.text-center img.thumb.ok.lazy.img-circle{
    filter: hue-rotate(230deg);
}
.error-text {
    color: #000099;
}
#header-center .checkbox-slider--b-flat input:checked+span:before {
    background-color: #6666AA;
    background: #6666AA;
}
 .checkbox-slider-warning.checkbox-slider--b input:checked+span:before, .checkbox-slider-warning.checkbox-slider--b-flat input:checked+span:before, .checkbox-slider-warning.checkbox-slider--c input:checked+span:before, .checkbox-slider-warning.checkbox-slider--c-weight input:checked+span:before {
    background: #EEEEFF;
}
.form-error-submit {
    background: #EEEEFF;
    border-color: #000099;
    
}
.form-error, .form-error-submit {
    color: #000099;
    background: url(https://mnmstatic.net/v_116/img/common/error.png) 0 50% no-repeat;
    background-color: #FFDDDD;
    filter: hue-rotate(230deg);
    padding: 15px;
}
.mini-icon-text i.fa.fa-edit {
    color: #000099;
}
.emojiCat li {
    border: 1px solid #000099;
}
.commentform a.emoji-tab.active {
    background-color: #000099;
}
.commentform a.emoji-tab {
    background-color: #EEEEFF;
}
.emoji-panel {
    border: 1px solid #000099;
}
img.emoji-btn{
    opacity:1 !important;
}
.emoji-kbd-btn.active {
    border: 1px solid #000099;
    background-color: #000099;
}
.emoji-kbd-btn img{
    opacity:1 !important;
}
#mDialog_header {
    background: #000099;
    color: #fff;
}
#mDialog_box {
    border: 1px solid #000099;
    background: #EEEEFF;
}
}
