css:
@-moz-document domain("events.zulily.com"), domain("mbarr-emsweb-01.vps.zulily.com"), regexp(".*emsweb.*\\.vps\\.zulily\\.com.*") {
.v6-view {
	background-color: #636363;
	color: #ddd;
}

body.v6-view > div.header .title,
.v6-view h5,
.v6-view h4,
.v6-view h3,
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover {
	color: #fafafa;
}

.v6-view .sidebar h5 {
	color: #333;
}

.v6-view .react-grid-HeaderCell {
	background-color: #a0bcff;
	color: #000;
}

.v6-view .react-grid-Container,
.v6-view .contextWrapper.active,
.v6-view .contextWrapper:hover {
	color: #666;
}

.v6-view a {
	color: #8bd8ff;
}

ul.nav-tabs,
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.nav-tabs > .active > a:focus {
	background-color: #926565;
}

.react-grid-HeaderCell--locked:last-of-type,
.react-grid-Cell--locked:last-of-type {
	border-right-color: #666;
}

.v6-view > div.header .title {
    text-shadow: 1px 1px 1px black;
}
}
