css:
@-moz-document domain("azala.info") {

#rest_container.theme_dark {
    background-color: #232323;
}



.big_hdr {
    font-size: 28px;
    color: #232323;
    text-align: center;
}



#server_load {
    font-family: "Open Sans";
    font-size: 18px;
    text-align: center;
    color: #5d0000;
}


.flat_button, .feedback_send_btn {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    position: relative;
    border-radius: 3px;
    font-size: 14px;
    text-align: center;
    padding: 6px;
    font-family: "Open Sans";
    cursor: pointer;
    color: white;
    border: none;
    background-color: #313131;
}



.sidebar_header {
    font-size: 19.2px;
    color: white;
    padding: 8px;
    height: calc(42px - 2 * 8px);
    background-color: #313131;
    cursor: pointer;
}


.adorable_link_button {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 3px;
    font-size: 14px;
    padding: 6px;
    font-family: "Open Sans";
    cursor: pointer;
    color: white;
    border: none;
    background-color: #313131;
    text-decoration: none;
}



.font_subhdr, .big_subhdr, .fp_error, .fp_other_stuff_elt {
    font-family: "Open Sans";
    font-size: 18px;
    color: #2c55f1;
}



.fp_preinput {
    display: inline-block;
    width: 80px;
    text-align: right;
    font-family: "Open Sans";
    color: #2c55f1;
}


.fp_step_icon > div {
    vertical-align: middle;
    font-family: "Open Sans";
    font-size: 24px;
    color: #2c55f1;
}



.fp_big_font {
    font-family: "Open Sans";
    font-weight: bold;
    font-size: 24px;
    color: #2c55f1;
}


.fp_hdr {
    font-family: "Open Sans";
    font-weight: bold;
    font-size: 24px;
    color: #2c55f1;
    display: inline-block;
    text-align: center;
}


.sidebar_cells {
    background-color: #232323;
    height: calc(100% - 42px);
}


.input_border, .generic_text_field, #feedback input, #feedback textarea, .input_deck_name, #blank_input, .fp_input, textarea {
    border: 1px solid #000000;
}


div.button.toggle.theme_dark.on {
    background-color: #313131;
}


div.button.toggle.on {
    border: 1px solid #313131;
    color: white;
}


.fc_purple_1 {
    color: rgb(0, 183, 253);
}


div {
    -ms-overflow-style: none;
    font-family: "Arial", "sans-serif";
    font-weight: 400;
    color: #2b59ff;
}


.tab_bar {
    background-color: #232323;
    overflow: hidden;
    padding: 4px 0 0 0;
    height: calc(42px - 4px);
    position: relative;
}


.tab_bar .tab {
    margin: 0px 1px 0px 0px;
    padding: 6px;
    position: relative;
    background-color: #242438;
    display: inline-block;
    border-radius: 4px 4px 0 0;
    height: 26px;
}


#adm_deck_list_box > .wrapper > .dv_deck_entry_container > .dv_name {
    font-family: "Open Sans";
    font-weight: normal;
    color: #fff;
    padding-left: 2px;
}


.hdr {
    font-size: 19.2px;
    line-height: 25.6px;
    color: #2c55f1;
}


#adv_deck_list_box > .wrapper > .dv_deck_entry_container > .dv_name {
    font-family: "Open Sans";
    font-weight: normal;
    color: #fff;
    padding-left: 2px;
}

#adv_deck_list_box > .wrapper > .dv_deck_entry_container > .dv_author.theme_dark {
    color: #ff6d6d;
}

.purple_1 {
    color: #2c55f1;
}

.adm_leftbar > .top > * {
    font-family: "Open Sans";
    font-weight: 600;
    font-size: 18px;
    color: #2b59ff;
}

#cotd_view .section_bar .sbb {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    cursor: pointer;
    display: inline-block;
    font-size: 20px;
    color: #2c55f1;
    border-radius: 3px;
    padding: 8px;
}

#cotd_view .section_bar .sbb.on {
    background-color: #313131;
    color: white;
}

#cotd_view .entries .entry .btn {
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    border-radius: 3px;
    text-align: center;
    padding: 6px;
    font-family: "Open Sans";
    cursor: pointer;
    color: white;
    border: none;
    background-color: #313131;
    border: 1px solid #313131;
    font-size: 16px;
    display: inline-block;
    width: 80px;
    vertical-align: top;
}

#cotd_view .right .ldr .name {
    display: inline-block;
    color: #fff;
    width: calc(100% - 40px);
}

#cotd_view .right .my_score, #cotd_view .right .my_total_score {
    font-size: 36px;
    color: #fff;
}

#cotd_view .right .ldr .score {
    display: inline-block;
    color: #fff;
    width: 40px;
    text-align: right;
}
}
