:root {
  --main-page-bg-color: #313e49;

  --main-page-text-color: #EDEDED;
  --main-page-text-color-two: #FF006F;
  --main-page-text-invert-color-two: #EDEDED;
  --main-page-link-color: #FF006F;

  --main-border-radius: 0px;
  --main-page-border-color: rgba(0, 0, 0,.2);

  --main-font-family: "Open Sans", sans-serif;
}

@-moz-document url("about:Home"),
url("about:home") {

  #sync,  #searchLogoContainer, #searchEngineLogo, #restorePreviousSessionSeparator {
    display: none !important;
  }

  html {
    margin-bottom: -4px !important;
    border-image: linear-gradient(to left, rgba(230,30,133,1) 0%, rgba(255,123,0,1) 70%) 100% 0 100% 0/0px 0% 2px 0% stretch !important; 

    background-color: var(--main-page-bg-color) !important;
    color: #000000;
    /*height: 100%;*/
  }

  body {
    -moz-appearance: none !important;
    background: transparent !important;
    margin: 0;
    -moz-box-orient: vertical;
    width: 100%;
    height: 100%;
  }

  a {
    color: var(--main-page-link-color);
    text-decoration: none;
  }
  
  .searchSuggestionRow {
    color: rgb(0,0,0);
  }

  #brandLogo {
    background: transparent  !important;
    height: 50px;
    width: 100%;
    margin: 40px 50px 40px 0px;
    text-align: center !important;
    -moz-transition: 2s !important;
    filter: opacity(1);
  }

  #brandLogo::before {
    font-style: normal;
    font-family: 'Lobster' !important;
    font-size: 60px !important;
    font-weight: bold;
    content: "/*[[Homepage-motto]]*/" !important;
    color: rgba(255,255,255,0.8) !important;
    cursor:default;
    text-shadow: 2px 2px 8px rgba(0,0,0,0.25) !important;
    background: -webkit-linear-gradient(right, #E62083,#FF7B00);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

    #brandLogo:after {
    font-style:normal;
    font-family: 'calibri' !important;
    font-size: 12px !important;
    font-weight: 100;
    content: "  " !important;
    letter-spacing: 12px;
     word-spacing: 8px ;
    color: rgba(255,255,255,0.25) !important;
    cursor:default;
    text-shadow: 2px 2px 2px rgba(0,0,0,0.55) !important;
    margin: 50px auto 0px auto !important;
     width: 470px !important; 
    background: -webkit-linear-gradient( #1f2830,#313E49);
   height: 10px;
      display: block !important;
  }
  #searchForm{
    width: 500px;
  }

  #searchIcon{ 
    display: none !important;
  }

  #searchForm {
    border-radius: 4px !important;
  }

  #searchText {
    padding: 0px 15px !important;
    -moz-appearance: none !important;
    background: rgba(0,0,0,.09) !important;
    border: 1px solid #FF7B00 !important;
    border-radius: 0px !important;
    outline: 0 none !important;
    box-shadow: none !important;
    font-size: 16px !important;
    color: #FF7B00 !important;
    font-family: var(--main-font-family) !important;
  }
  
  #searchText:focus,
  #searchText[autofocus] {
    border: 1px solid var(--main-page-text-color-two)!important;
    border-radius: 0px !important;
    background: rgba(0,0,0,.12) !important;
    transition: background .6s ease-in-out !important;
    box-shadow: inset 0px 0px 9px rgba(0,0,0,.22) !important;
    transition: border 2s ease-in-out !important;;
  }
  
  #searchSubmit {
    padding: 0px 15px 0px 15px !important;
    -moz-appearance: none !important;
    background: transparent !important;
    border: 1px solid #FF7B00 !important;
    border-left: 1px solid transparent!important;
    border-radius: 0px !important;
    box-shadow: inset 0px 0px 9px rgba(255, 123, 0,.28) !important;
    font-family: var(--main-font-family) !important;
    font-size: 12px !important;
    text-transform: uppercase;
    color: var(--main-page-text-color) !important; 
  }
  
  #searchText:focus + #searchSubmit,
  #searchText[autofocus] + #searchSubmit{
    background: transparent !important;
    border: 1px solid var(--main-page-text-color-two)!important;
    border-radius: 0px !important;
    box-shadow: inset 0px 0px 9px rgba(230, 31, 131,.48) !important;
    color: var(--main-page-text-color-two) !important;
    font-family: var(--main-font-family) !important;
    transition: border 2s ease-in-out !important;

  }
  
  #searchText + #searchSubmit:hover {
    background: var(--main-page-text-color-two) !important;
    border: 1px solid var(--main-page-text-color-two)!important;
    color: var(--main-page-text-invert-color-two) !important; 
  }
  
  #searchText + #searchSubmit:hover:active {
    background: var(--main-page-text-invert-color-two) !important;
    border: 1px solid var(--main-page-text-color-two)!important;
    color: var(--main-page-text-color-two) !important;
  }
  
  #snippets {
    display: none !important;
    width: 450px;
    opacity: 0.5;
  }
  
  #defaultSnippet1,
  #defaultSnippet2,
  #rightsSnippet {
    display: block;
    min-height: 38px;
    background: 30px center no-repeat;
    padding: 6px 0px;
    margin: 0px 00px 0px -00px;
  }

  #defaultSnippet1 {
    background: transparent !important;
  }
  #defaultSnippet2 {
    background: transparent !important;
  }

  #snippets {
    display: inline-block;
    text-align: start;
    text-align: left;
    margin: 12px 0;
    color: var(--main-page-text-color);
    font-family: var(--main-font-family) !important;
    font-size: 14px;
  }    

  #launcher,
  #restorePreviousSession {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
  }
  
  .launchButton::before {
    display: none !important;
  }

  #launcher button {
    -moz-box-orient: vertical;
    background: none !important;
    border-color: transparent !important;
    min-width: 38px;
    max-height: 45px;
    font-family: var(--main-font-family) !important;
    font-weight: normal;
    font-size: 8pt !important;
    color: var(--main-page-text-color) !important;
    text-transform: uppercase;
    border-top: 1px solid rgba(255,255,255,0.0)!important;
    border-radius: 0px !important;
    margin-right: 0px !important;
    margin-left: 0px !important;
    transition-property: background, color, border-top;
    transition-duration: 150ms;
  }

  #launcher button:hover,
  #restorePreviousSession:hover {
    color: var(--main-page-text-color-two) !important;
    border-top: 1px solid var(--main-page-text-color-two)!important;
    border-radius: 0px !important;
  }

  #launcher button:active {
    background: none !important;
  }

  #aboutMozilla {
    opacity: .6;
  }   

  #aboutMozilla::before {
    font-family: var(--main-font-family) !important;
    font-size: 18px !important;
    color: var(--main-page-text-color) !important;
    content: "mozilla" !important;
    transition-property: color;
    transition-duration: 150ms;
  }

  #aboutMozilla:hover:before {
    font-family: var(--main-font-family) !important;
    font-size: 18px !important;
    color: var(--main-page-text-color-two) !important;
    content: "mozilla" !important;
  }

}

#personal-bookmarks toolbarbutton.bookmark-item[image]:not([image=""]) .toolbarbutton-text
{ display: none !important; }
#personal-bookmarks toolbarbutton.bookmark-item[image]:not([image=""]) .toolbarbutton-icon
{ margin-right: 0px !important; }

#personal-bookmarks toolbarbutton.bookmark-item:not([image]) .toolbarbutton-icon,
#personal-bookmarks toolbarbutton.bookmark-item[image=""] .toolbarbutton-icon
{ display: none !important; }

#personal-bookmarks toolbarbutton.bookmark-item[container="true"][label="Smart Bookmarks"]
{ list-style-image: url(chrome://browser/skin/places/query.png) !important; }
#personal-bookmarks toolbarbutton.bookmark-item[container="true"][label="Smart Bookmarks"] .toolbarbutton-icon
{ display: -moz-box !important; }
#personal-bookmarks toolbarbutton.bookmark-item[container="true"][label="Smart Bookmarks"] .toolbarbutton-text
{ display: none !important; }
