@-moz-document domain("www.yesstyle.com") {
*{    text-shadow:none!important;    box-shadow:none!important;    border-radius:0px!important;    text-decoration:none!important;}
@font-face{	font-family: 'Roboto';	font-style: normal;	font-weight: 300;}
* {font-family: 'Roboto' !important;}
img[src$="http://c.ysi.bz/Assets/res/p/130/imgs/yesstyle.svg"]{
height:23px!important; /*width of new image*/
padding-left:189px!important; /*height of new image*/
width:0!important;background-image:url("https://k61.kn3.net/9/6/C/C/3/4/D8A.png")!important}
/*------------Links--------------*/
a:link,header .accessbar .md-button,header .subcategory ul li a, header .subcategory ul li a:link,header .subcategory.active,md-card,.aboutys p a,.frontpage-grid .quicklinks a,v-pane.ys-multiple-accordion ul li a,.itemMostPopular,.ysSection .sectionHeading,.parentSection h4,.parentSection .childColumn.titleLink a,.reviewTitle,.reviewHead,.ratingRow .reviewCount,v-pane.ys-checkbox md-checkbox,.remark,.showReviews .satisfied,.roundCornerSelect,header .category a,header md-sidenav ul li>a, header md-sidenav ul li>a:link
{    color: /*[[lnk]]*/;}
.headingContent .vAccordion--default v-pane-header, .headingContent .vAccordion--default v-pane-header:hover, .headingContent .vAccordion--default v-pane-header:focus
{    color: /*[[lnk]]*/ !important;}
a:hover,header .subcategory ul li a:hover,footer .vAccordion--default v-pane-header:hover,.frontpage-grid .quicklinks a:hover,a.pill-btn:hover, a.pill-btn.darker .icon,nobr,.productList a:hover,.productCover .coverImageLink .pill-btn.small,md-grid-tile>figure>a:hover
{	color: /*[[lnkhvr]]*/;}
a:visited,header md-tab-item.md-active,.ysTabs .ysTab.active .md-button
{    color: #4caf50 !important;}
/*------------Text----------------*/
html.md-default-theme, html, body.md-default-theme, body,p,md-card md-card-content,v-pane,.headingDescription,v-pane.ys-multiple-accordion v-pane-header,md-select-menu md-content md-option,.eliteClubMembership,.unitPrice .listPrice,.availability,.arrowLinkWrapper a,.arrowLinkWrapper a .icon,.md-ys-theme.roundCorner md-select,md-select.md-default-theme:not([disabled]):focus .md-select-value, md-select:not([disabled]):focus .md-select-value,.reviewBody,.circularWrapper .reviewCount,.productReviews .sectionContent .ratingChartWrapper h4,v-pane.ys-multiple-accordion ul li a, v-pane.ys-multiple-accordion ul li .md-button:not(.md-raised),.optionContainer .md-list-item-inner .ships,md-dialog,md-input-container.md-default-theme label, md-input-container label, md-input-container.md-default-theme .md-placeholder, md-input-container .md-placeholder,a.md-button.ng-scope > div,md-input-container.md-default-theme:not(.md-input-invalid).md-input-has-value label, md-input-container:not(.md-input-invalid).md-input-has-value label,.md-button[disabled], .md-button.md-raised[disabled],.paging .pageNumber
{	color: /*[[txt]]*/;}
span.inactive.ng-scope,span.animated.icon.icon-shopping-bag.ng-scope
{	color: #fff;}
/*-------------------Background-------------------*/
header,md-tabs md-tabs-wrapper,header .subcategory.active,header .subcategory .promotion md-card,header .subcategory .promotion,.ysSection,.brandlist,.aboutys p,footer,.welcomepop.page,.frontpage-grid .quicklinks,.blog,.rightContent,md-dialog,md-menu-content,main .regByEmail,md-grid-tile .msg,header .yshead
{	background-color: /*[[bgs]]*/;}
.headingPromo .parentSection .childColumn,.parentSection .childColumn.blockLink a,v-pane.ys-multiple-accordion,md-select-menu md-content,.round-btn-wrapper .round-btn,.childColumn,.itemStatus,.ysTabsSection .ysTabs,.scrollspyNav.sticky .container,.pill-btn, a.pill-btn,.masonryGrid li,.circularWrapper,main .content,div.animate,.categories>div>h2,.topbar .carousel-inner>.item>div,header .category,.productAlsoBought .productGrid
{	background: /*[[bgs]]*/;}
html, body,.itemContainer .itemStatus
{    background-color: /*[[bgs2]]*/;}
header .backdrop,.welcomepop .shippingInfo,.itemTitle:after, .itemOption:after, .buyingOption .roundCornerSelect span:after,.bottomButton .md-button,.ratingRow>.colored:before
{	background: transparent !important;}
.ysSection.brandIcons a
{	background: #fff;}
/*-bg hover-*/
md-select-menu.md-default-theme md-content md-option:not([disabled]):focus, md-select-menu md-content md-option:not([disabled]):focus, md-select-menu.md-default-theme md-content md-option:not([disabled]):hover, md-select-menu md-content md-option:not([disabled]):hover,.round-btn-wrapper .round-btn:hover:not([disabled]),.ysTabs .ysTab .md-button:hover,.md-button.md-default-theme:not([disabled]):hover, .md-button:not([disabled]):hover,.reviewHead,.chartWrapper,.ratingRow .reviewCount,main .otherOption,main .regByEmail:hover
{    background: rgba(158,158,158,0.2) !important;}
/*-------------------NO BORDER----------------*/
.sectionBorder,.ysSection,footer,.frontpage-grid .quicklinks a:hover,.frontpage-grid .quicklinks a,li.ng-scope,.md-ys-theme.roundCorner md-select,v-pane .parentCategory,.arrowLinkWrapper a:first-child,.arrowLinkWrapper a,.ysSection.tagRow .sectionContent,.bottomButton,.ysTabsSection .sectionHeading,.optionContainer,main .emailRegistration,header
{    border-bottom: 0px solid red !important;    border: 0px solid red !important;    border-top: 0px solid red !important;    border-color: transparent !important;    border-width: 0px !important;}
/*-------------------BORDERS------------------*/
.cardList .itemContainer, .groupCardList .itemContainer,.round-btn-wrapper .round-btn,.pill-btn, a.pill-btn,.roundCornerSelect,.bottomButton .md-button,main .regByEmail
{    border: 1px solid /*[[bgs2]]*/;}
.itemStatus div:nth-child(2)
{    border-left: 1px solid /*[[bgs]]*/;}
.scroller
{    border-top: 3px solid /*[[bgs]]*/;}
.leftContent v-accordion:first-child
{    border-top: 5px solid /*[[bgs]]*/;}
.leftContent v-accordion:first-child .clearAll
{    border-top: 1px solid /*[[bgs]]*/;}
.leftContent v-accordion:first-child v-pane-content,v-pane.ys-multiple-accordion.is-expanded>v-pane-content
{    border-color: /*[[bgs]]*/ !important;}
v-pane.ys-multiple-accordion v-pane-content
{    border-top: 1px solid /*[[bgs]]*/;}
/*---------------ICONS---------------*/
.cartButtonWrapper .saveForLater .icon
{    color: #a03737;}
.cartButtonWrapper .saveForLater.activePulse .icon
{    color: #f00;}
/*-------------------input------------------*/
.searchpanel input[type="search"]
{	background: /*[[bgs]]*/;	    border-left: 3px solid #4caf50;}
input, textarea, select, md-input-container.md-default-theme .md-input, md-input-container .md-input
{	color:#999 !important;}
/*-------------?--------------*/
.topbar
{    margin: 10px auto 8px;}
.icon-save-items:before
{    color: #d63300;}
}
