css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("webspace.apiit.edu.my"){
  
 body {
    background: #EC2020;
    background-color: rgb(236, 32, 32);
    color: white;
    background-image: none;
    background-repeat: repeat;
    background-attachment: scroll;
    background-clip: border-box;
    background-origin: padding-box;
    background-position-x: 0%;
    background-position-y: 0%;
    background-size: auto auto;
    margin: 0;
    padding: 0;
    color: #281f18;
}
  
  .block {
    margin-bottom: 20px;
    border: none;
    background: #E9C847;
    color: black;
    font-family: arial;
    font-size: 17px;
    box-shadow: 0px 0px 10px black;
    opacity: 0.7;
    transition-property: opacity, box-shadow;
    transition-duration: 0.6s;
  }
  
  .block:hover{
    opacity: 1;
    box-shadow: 5px 5px 10px black;
  }
  
  #page-header {
    float: none;
    padding: 10px 0 0;
    background: #000 url(http://il5.picdn.net/shutterstock/videos/17174794/thumb/1.jpg) bottom left repeat-x;
    border-bottom: 5px solid #dbd6c7;
    color: #FFF;
    height: 100px;
}
  
}
