css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("good-tutorials.com") {

* {
font-family: arial !important;
}

#top {
width: auto !important;
margin: 0 20px 10px 20px !important;
}
#wrapper {
width: auto !important;
margin: 0 20px 10px 20px !important;
padding:0 189px 0 0 !important;
}

#logo {
padding: 0 !important;
}

#header ul {
padding: 7px 0 0 0 !important;
}
#header li {
font-size: 13px !important;
}

.search input {
margin-bottom: 0 !important;
}

.teaser {
font-size: 15px !important;
}
.teaser a:hover {
color: #222 !important;
}

#topics {
padding-left:1px !important;
}
#topics li {
font-weight: bold;
font-size: 1.2em !important;
padding: 4px 0 4px 0 !important;
}
#upcoming {
font-size: 1.25em !important;
padding: 4px 0 4px 9px !important;
}
#topics li a {
padding: 4px 6px 4px 6px !important;
border-right: 1px solid #ccc !important;
}

#container {
float: left !important;
width: 100% !important;
font-size: 20px !important;
}
.content p {
font-size: 17px !important;
}
.cap {
padding-top: .3em !important;
}
.cap h1 {
font-size: 22px !important;
}

#sidebar {
width: 179px !important;
float: right !important;
margin: 0 -179px 0 0 !important;
}

.sidenote:after {
clear: both !important;
display: block !important;
visibility: hidden !important;
content: "&nbsp" !important;;
height: 0 !important;
}

#skycolumn ul {
display: none !important;
}
#skycolumn ul:first-child {
display: block !important;
margin: 0 !important;
}
#skycolumn li {
font-size: 1.2em !important;
}
#skycolumn li a {
padding:0.15em 0.25em 0.15em 0.5em !important;
}
#skycolumn li.headline {
font-size: .95em !important;
margin-bottom: 2px !important;
}

.cube, #adspan, #skyscraper, iframe, .sidechunk:last-child {
display: none !important;
}

#footer {
font-size: .95em !important;
padding: 10px 0 !important;
text-align: center !important;
}

}
