css:
@-moz-document domain("edugen.wileyplus.com") {
table {
    background-color: #37474F;
}
td {
    background-color: #37474F;
    color: #ECEFF1;
    font-family: Verdana, Geneva, sans-serif;
}
body {
    background-color: #37474F;
}
p, .textBlack    {
    background-color: #37474F;
    color: #ECEFF1 !important;
    font-family: Verdana, Geneva, sans-serif;
}
 
img.MathMLImage {
    background-color: #CFD8DC;
}
body.agTableOfContent {
    background-color: #37474F;
}
input {
    background-color: #90A4AE;
    color: #ECEFF1;
	font-family: Verdana, Geneva, sans-serif;
    border-style: none;
    border-radius: 3px;
}
input, textarea, keygen, select, button {
    text-indent: 5px;
}
select {
    background-color: #90A4AE;
    color: #ECEFF1;
    font-family: Verdana, Geneva, sans-serif;
    border-style: none;
    border-radius: 3px;
}
a.breadcrumblink, a span.breadcrumblink, a, a.span {
    color: #90CAF9;
    font-family: Verdana, Geneva, sans-serif;
}
.agItem a {
    color: #ECEFF1;
    font-family: Verdana, Geneva, sans-serif;
}
.agItem .selected {
    background-color: #ECEFF1;
    color: #00796B;
    font-family: Verdana, Geneva, sans-serif;
}
div.GradeMarker {
    background-color: #CFD8DC;
    height: 15px;
}
#wp-footer .copyright, #wp-footer .copyright * {
    color: #ECEFF1;
    font-family: Verdana, Geneva, sans-serif;
}
h3.wp-qti-section-title, h4.wp-qti-part-title, h3.wp-qti-item-title, h3, body.agTableOfContent h3 {
    color: #FB8C00;
    font-family: Verdana, Geneva, sans-serif;
}
#breadcrumb-content .breadcrumb {
    color: #ECEFF1;
    font-family: Verdana, Geneva, sans-serif;
}
#wp-page-header-tabs li a {
    color: #82B1FF;
    background-color: #455A64;
    border-top: solid;
    border-bottom: solid;
    border-width: 1px;
    border-color: #263238;
    margin-top: -2px;
}
#wp-page-header-tabs li.active {
    color: #82B1FF;
    background-color: #455A64;
    border-top: solid;
    border-bottom: solid;
    border-width: 1px;
    border-color: #263238;
    margin-top: -2px;
    text-decoration: underline;
}
#wp-page-header-tabs li.hover {
    color: #82B1FF;
    background-color: #78909C;
    border-top: solid;
    border-bottom: solid;
    border-width: 1px;
    border-color: #263238;
    margin-top: -2px;
}
#wp-page-header-tabs li {
    background: none;
    background-color: #37474F;
    margin: 0px;
    margin-left: -1px;
}
}
