css:
@-moz-document url-prefix("http://www.efpfanfic.net") {
a {
    color: #94a8ff;
}
body {
    font-family: Helvetica;
    font-size: 12px;
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    padding: 0;
    margin: 0;
    background-color: #eeeeee;
    color: #333333;
}
b {
    color: #94a8ff;
}
#header {
    width: 98%;
    margin: 5px 0px 0px 0px;
    text-align: left;
    padding: 7px;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    -moz-border-radius: 0px;
    border-radius: 0px;
    background-color: #ffffff;
    height: 110px;
}
ul#nav {
    font: 120%/2.1 Helvetica;
    font-size: 13px;
    font-variant: small-caps;
    color: #FFFFFF
}
ul#nav,
ul#nav li,
ul#nav ul,
ul#nav ul li {
    margin: 0;
    padding: 0;
    list-style-type: none
}
ul#nav {
    height: 20px;
    line-height: 25px;
    margin: 0px auto 0 -1px;
    color: #ffffff
}
ul#nav a {
    display: block;
    padding: 0 10px;
    text-decoration: none;
    color: #000000;
    background-color: #ffffff;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    -moz-border-radius: 0px;
    border-radius: 0px;
}
ul#nav li {
    float: left;
    position: relative;
}
ul#nav li li {
    float: none;
    line-height: 22px;
    display: block !important;
    display: inline;
    /*IE*/
}
ul#nav ul {
    position: absolute;
    top: 23px;
    left: -9999px;
    width: 18em;
    background: #ffffff;
}
ul#nav ul {
    padding: 7px 0;
    border: 1px solid #ffffff;
}
ul#nav ul,
ul#nav li li a {
    background-color: #ffffff;
    color: #94a8ff;
}
ul#nav li:hover,
ul#nav a:hover {
    background-color: #c9d3ff;
    color: #ffffff
}
ul#nav li:hover ul,
ul#nav li.sfhover ul {
    left: -1px;
    z-index: 100
}
div#account {
    float: right;
    margin: 0px 17px 0px 0px;
    text-align: right;
    font: 120%/2.1 Helvetica, sans-serif;
    padding: 10 0px;
    text-decoration: none;
    color: #94a8ff;
    background-color: #ffffff;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    -moz-border-radius: 0px;
    border-radius: 0px;
}
#corpo {
    width: 98%;
    margin: 0px 0px 5px 0px;
    background-color: #FFFFFF;
    text-align: left;
    padding: 7px;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    -moz-border-radius: 0px;
    border-radius: 0px;
}
#footer {
    background-color: #FFFFFF;
    width: 98%;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    -moz-border-radius: 0px;
    border-radius: 0px;
    text-align: center;
    font-size: 10px;
    padding: 7px;
    margin: 10px 0px;
}
#quadrato {
    background-color: #FFFFFF;
    width: 100%;
    -webkit-bo-shadow: 0 0 0px #FFFFFF;
    -moz-box-shadow: 0 0 0px #FFFFFF;
    box-shadow: 0 0 0px #FFFFFF;
    text-align: center;
    font-size: 10px;
    padding: 0;
    margin: 5px 5px 5px 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
}
tr,
td,
form {
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    font-size: 1.0em;
    font-family: helvetica;
    color: #000000;
}
div.contgen {
    padding: 8px;
    margin: 5px 5px 5px 5px;
    border-width: 1px;
    border-color: #eeeeee;
    border-style: solid;
    background-color: #FFFFFF;
    vertical-align: middle;
}
div.hrsep {
    height: 1px;
    margin: 6px auto;
    border-top: 1px solid #94a8ff;
    width: 60%;
    color: #94a8ff;
}
ul#selection {
    width: 100%;
    height: 43px;
    background: #FFF top left repeat-x;
    font-size: 1em;
    font-family: Helvetica, sans-serif;
    font-weight: bold;
    list-style-type: none;
    margin: 0;
    padding: 0;
}
ul#selection li {
    display: block;
    float: left;
    margin: 0 0 0 5px;
}
ul#selection li a {
    height: 43px;
    color: #777;
    text-decoration: none;
    display: block;
    float: left;
    line-height: 200%;
    padding: 8px 15px 0;
}
ul#selection li a:hover {
    color: #333;
}
ul#selection li a.current {
    color: #FFF;
    background: #FFF top left repeat-x;
    padding: 5px 15px 0;
}
.header {
    font-family: Helvetica;
    font-size: 13pt;
    font-weight: bold;
    color: #94a8ff;
}
.title {
    font-family: Helvetica;
    font-size: 12pt;
    font-weight: bold;
    color: #94a8ff;
}
.titolo {
    font-variant: small-caps;
    font-size: 1.6em;
    font-weight: bold;
    color: #94a8ff;
}
div.titoletto {
    font-size: 1.1em;
    font-variant: small-caps;
    font-weight: bold;
    color: #000000;
}
p.titolettogrande {
    font-size: 1.4em;
    line-height: 1.0;
    text-decoration: none;
    font-variant: small-caps;
    font-weight: bold;
    color: #94a8ff;
}
.hrseparazione {
    height: 1px;
    margin: 10px auto;
    border-top: 1px solid #94a8ff;
    width: 70%;
    color: #94a8ff;
}
.sfondocolorato {
    background-color: #eeeeee;
}
#box-1 {
    margin-top: auto;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: auto;
    width: 100%;
    background-color: #ffffff;
    border: 1px solid #eeeeee;
}
#tab-cornice {
    margin-left: auto;
    margin-right: auto;
    width: 98%;
    padding: 0px;
    background-color: #ffffff;
    border: 1px solid #eeeeee;
    position: relative;
}
.rigaintestazione {
    margin-top: 2px;
    margin-left: 2px;
    margin-right: 0px;
    text-align: left;
    padding: 2px 4px;
    background-color: #eeeeee;
}
.titolostoria {
    float: right;
    margin-top: 1px;
    margin-left: 1px;
    font-weight: bold;
    font-size: 3em;
    letter-spacing: 1px;
}
.stato {
    margin-top: 3px;
    color: #94a8ff;
    font-weight: bold;
    font-size: 1em;
}
.recensioni {
    margin-top: 4px;
    font-size: 1em;
    color: #94a8ff;
}
.recensioni a,
.recensioni a:visited {
    color: #94a8ff;
}
.contriga {
    margin-left: 100px;
    margin-right: 15px;
    margin-top: auto;
    margin-bottom: auto;
    padding: 2px;
    height: 38px;
    font-style: normal;
    font-weight: normal;
    font-size: 11px;
    font-family: helvetica
}
.contriga1 {
    margin-left: 100px;
    margin-right: 15px;
    margin-top: 0px;
    padding: 2px;
    height: 37px;
    font-style: normal;
    font-weight: normal;
    font-size: 11px;
    font-family: helvetica
}
.contrigastory {
    margin-left: 150px;
    margin-right: 15px;
    padding: 4px;
    height: 38px;
    font-style: normal;
    font-weight: normal;
    font-size: 11px;
    font-family: helvetica
}
.titolocat {
    font-family: Helvetica, sans-serif;
    font-size: 200%;
    font-style: normal;
    font-variant: normal;
    font-weight: bold;
    text-align: center;
}
.hrwire {
    height: 1px;
    margin: 3px auto;
    border-top: 1px solid #94a8ff;
    width: 30%;
    color: #085575;
}
.shadetabs {
    border-bottom: 1px solid #eeeeee;
    margin-bottom: 1em;
}
.titlebloc {
    float: left;
    background-color: #eeeeee;
    padding: 4px 7px 4px 7px;
    width: 98%;
    vertical-align: middle;
}
.titlestoria {
    font-style: italic;
    font-weight: bold;
    font-size: 1.2em;
    color: #000000;
}
#verde {
    float: left;
    width: 10px;
    height: 10px;
    margin: 3px 8px 3px 2px;
    background-color: #8eff74;
    border: 1px solid #eeeeee;
}
#giallo {
    float: left;
    width: 10px;
    height: 10px;
    margin: 3px 8px 3px 2px;
    background-color: #faff74;
    border: 1px solid #eeeeee;
}
#arancione {
    float: left;
    width: 10px;
    height: 10px;
    margin: 3px 8px 3px 2px;
    background-color: #ffac74;
    border: 1px solid #eeeeee;
}
#rosso {
    float: left;
    width: 10px;
    height: 10px;
    margin: 3px 8px 3px 2px;
    background-color: #ff7474;
    border: 1px solid #eeeeee;
}
#logolink img {
    display: none;
}
#logolink {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-image: url(http://i.imgur.com/U4tVolC.png);
    width: 550px;
    /* Width of new image */
    
    height: 70px;
    /* Height of new image */
    
    padding-left: 550px;
    /* Equal to width of new image */
}
#icocontatta {
    display: none;
}
#icofacebook {
    display: none;
}
#icoforum {
    display: none;
}
ul#nav li.main {
    border: thin solid #eeeeee;
}
ul#nav li.first {
    border: thin solid #eeeeee;
}
#corpo a,
#account a {
    color: #94a8ff;
}
#corpo a:hover,
#account a:hover {
    color: #d2d2d2;
    text-decoration: none;
}
table {
    border: #eeeeee;
}
table img {
    border: #eeeeee;
}
#corpo img {
    opacity: 0.5;
    filter: alpha(opacity=50);
    /* For IE8 and earlier */
    
    transition: 1s;
    -webkit-transition: 1s;
    -webkit-transition-timing-function: ease-in-out;
    transition-timing-function: ease-in-out;
}
#corpo img:hover {
    opacity: 1;
    filter: alpha(opacity=100);
    transition: 1s;
    -webkit-transition: 1s;
    -webkit-transition-timing-function: ease-in-out;
    transition-timing-function: ease-in-out;
}
.titolocat {
    font-family: Helvetica;
    font-size: 200%;
    font-style: normal;
    font-variant: normal;
    font-weight: bold;
    text-align: center;
}
}
