css:
@-moz-document domain("forums.sonicretro.org"), domain("sonicretro.org"), domain("info.sonicretro.org") {
/*  Sonic Mania theme for Sonic Retro (forums mainly).
    by Xane Myers, updated in 2020 */

.pageWidth
{
    max-width: /*[[pagemaxwidth]]*/;
}
div.row2, .row2 td, .post_block.row2, .row2 .altrow, .row2.altrow, .general_box, #stats, #active_users, #forum_active_users, #cal_bdays, #forum_legend, table.ipb_table tr.firstTop th div.row2, div.firstTop div.row2, div.post_content_wrap div.row2, ul.row2, li.row2, p.row2, .author_info ul.user_fields, div.rb_subMenu, div.row1, .row1 td, .post_block.row1, .row1 .altrow, .row1.altrow, .personal_info, table.ipb_table tr.first
th div.row1, div.firstTop div.row1, div.post_content_wrap div.row1, ul.row1, li.row1, p.row1, div.rb_subMenu ul li, #content .pageContent
{
    background: linear-gradient(to bottom, rgba(125,126,125,1) 0%,rgba(14,14,14,0.25) 100%) !important;
    color: white;
    border-color: transparent !important;
    text-shadow: 0px 4px 4px #000;
}

.post_block h3, .dark_postrating_detail
{
    background: linear-gradient(to bottom, #81afe3 0%,#4f81d3 100%);
}

.post_content_wrap
{
    text-rendering: optimizeSpeed;
    background: linear-gradient(to bottom, #bbb 0%,#888 100%);
    color: #222;
    font-size: 12pt;
    text-shadow: 0px 3px 5px #888;
}

div#rb_menuBar div.item:hover, div#rb_menuBar div.itemSelect { background: none transparent !important; }

.post_block h3
{
    border-top: transparent 0 !important;
    border-left: transparent 0 !important;
    border-right: transparent 0 !important;
    border-bottom: transparent 0 !important;
    box-shadow: 0 0 0.5em cadetblue;
}

.xenOverlay .section, .xenOverlay .sectionMain
{
    border: 0 transparent none;
}

.hfeed
{
    position: relative;
    left: 25%;
    align-self: center;
    align-items: center;
    align-content: center;
    width: 50%
}

#logoBlock, .post-comments
{
    background-color: #248;
}
.subHeading, .sectionFooter
{
    background: #248;
}

li.group_icon, #section_links
{
    display: none;
}
  
  .first
  {
    	background-image: none;
  }
  
  ul.post_controls, tr.row1 tr.row2, tr.row1first, div#rb_extensionBar, ul.post_controls
  {
    	background: none transparent !important;
    	border-left: transparent 0 !important;
    	border-right: transparent 0 !important;
      	border-top: transparent 0 !important;
      	border-bottom: transparent 0 !important;
  }

object { display: none; } /* Hide the "sound manager" object as it doesn't work anymore. */

div.buttonlocked, div.buttonlocked:hover, .dropdown.button.small, ul.pagination li, .header2 th.col_f_header2, .subhead th.col_f_header2, table.ipb_table tr.subhead th, .header2 th.col_f_icon, .subhead th.col_f_icon, .header, .header2, .altbar, .subhead
{
	background-color: transparent;
    background: linear-gradient(to bottom, rgba(208,228,247,1) 0%,rgba(115,177,231,1) 24%,rgba(10,119,213,1) 50%,rgba(83,159,225,1) 79%,rgba(135,188,234,1) 100%) !important;
}

.block_wrap, #secondary_nav, .topic_controls
{
    width: 50%;
    position: relative;
    left: 25%;
}

div#rb_menuBar
{
	line-height: 42px;
    border: darkgrey 2px dotted;
    border-left: transparent 10px solid;
    background: linear-gradient(to bottom, rgba(197,222,234,1) 0%,rgba(138,187,215,1) 31%,rgba(6,109,171,1) 100%);
}

div.item:hover, div.item:focus, item withMenu
{
	height: inherit;
	background: linear-gradient(to bottom, #00457a 0%,#005da4 5%,#0074cc 15%,#5aa1d8 85%,#88bbe2 95%,#b8d7ee 100%) !important;
}

.comment-provider, .breakout th, .post-entry, .essbfc-template-metro .essbfc-twitter .essbfc-network
{
	background: linear-gradient(to bottom, rgba(30,87,153,1) 0%,rgba(78,136,193,0) 50%,rgba(125,185,232,1) 100%) !important;
}

div.item a
{
    line-height: 42px !important;
}

li strong a, li strong a:hover, li strong a:focus, li strong a:active, li strong a:visited
{
    line-height: 30px !important;
}

/*pre, code, textarea, tt, kbd, samp, var
{
	font-family: sans-serif !important;
}*/

pre.prettyprint { background: rgba(0,0,0,0.34); color: lightgoldenrodyellow; border: 0 solid transparent !important; }

.breadcrumb .crust a.crumb
{
	background: linear-gradient(to bottom, rgba(30,87,153,1) 0%,rgba(78,136,193,0) 50%,rgba(125,185,232,1) 100%);
}

.secondaryContent
{
    background: linear-gradient(to bottom, rgba(253,223,209,1) 0%,rgba(251,211,187,0) 50%,rgba(249,199,165,1) 100%);
}

.bbCodeQuote .attribution, .importantMessage, .essbfc-template-metro .essbfc-google .essbfc-network
{
    background: linear-gradient(to bottom, rgba(254,187,187,1) 0%,rgba(254,144,144,0.1) 45%,rgba(254,139,139,0) 50%,rgba(255,92,92,1) 100%);
    color: lightyellow;
    text-align: center;
}

.quoteContainer, .essbfc-template-metro .essbfc-facebook .essbfc-network, .post-pagination .pagi-next   /* Green w/ middle transparency */
{
	background: linear-gradient(to bottom, rgba(180,221,180,1) 0%,rgba(131,199,131,0.66) 17%,rgba(82,177,82,0.34) 33%,rgba(41,158,41,0) 50%,rgba(0,138,0,0.34) 67%,rgba(0,87,0,0.66) 83%,rgba(0,36,0,1) 100%);
}

.essbfc-template-metro .essbfc-facebook .essbfc-network, .essbfc-template-metro .essbfc-facebook .essbfc-network, .essbfc-template-metro .essbfc-google .essbfc-network, .essbfc-template-metro .essbfc-twitter .essbfc-network
{
    background-color: transparent !important;
}

.post-pagination .pagi-prev /* Pink gradient w/ middle transparency */
{
	background: linear-gradient(to bottom, rgba(221,181,212,1) 0%,rgba(199,131,183,0.66) 17%,rgba(178,82,155,0.34) 33%,rgba(158,41,131,0) 50%,rgba(138,0,106,0.34) 67%,rgba(88,0,67,0.66) 83%,rgba(36,0,28,1) 100%);
}

.row1 div.quote div.quote div.quote div.quote div.quote div.quote, .row1 div.quote div.quote div.quote div.quote div.quote div.quote div.quote, .row1 div.quote div.quote div.quote div.quote div.quote div.quote div.quote div.quote, table tr th, table tr td, .essbfc-template-metro .essbfc-steamcommunity .essbfc-network, .toc, .label, fieldset legend
{
	background: linear-gradient(to bottom, rgba(201,201,201,1) 0%,rgba(165,165,165,0.66) 17%,rgba(130,130,130,0.34) 33%,rgba(100,100,100,0) 50%,rgba(69,69,69,0.34) 67%,rgba(44,44,44,0.66) 83%,rgba(18,18,18,1) 100%);
}

.breadcrumb
{
	background: linear-gradient(to top, rgba(201,201,201,1) 0%,rgba(165,165,165,0.66) 17%,rgba(130,130,130,0.34) 33%,rgba(100,100,100,0) 50%,rgba(69,69,69,0.34) 67%,rgba(44,44,44,0.66) 83%,rgba(18,18,18,1) 100%);
}

.messageUserBlock div.avatarHolder, .messageUserBlock h3.userText, .messageUserBlock .extraUserInfo
{
    /* Background remover */
    background: none transparent !important;
    border-color: transparent !important;
}
.messageUserBlock .arrow, .pageContent .breadcrumb .crust .arrow
{
    /* Complete remover */
    display: none;
}

body .maintitle, table.ipb_table tr.darker th, .general_box h3, .messageHeading, .heading, .xenForm .formHeader
{
	background: linear-gradient(to bottom, #e1c29f 0%,#cf924d 50%,#b6711c 51%,#c7a21d 75%,#9c6016 100%);
}

#footer_utilities
{
   position: relative;
    left: 40%;
    width: 13%;
}

#privacy, #footer_utilities form
{
	display: none !important;
}

#disclaimer::before { content: "Legal "; }
#f-designby::before { content: "This has been "; }
#f-designby::after { content: ". Theme made by Xane Myers."; }

#footer_utilities, div#index_stat_links
{
	background: linear-gradient(to bottom, #87b4cf 0%,#5995bf 31%,#0a4e82 100%);
}

.right
{
	float: left;
}

.top-bar, .top-bar-section ul, .top-bar-section ul li.active > a, .top-bar-section li a:not(.button), .top-bar-section .has-form, .top-bar.expanded .title-area, .ca-nstab-category, .f-dropdown, .bottomtemphead, .post-header, .post-meta, table.wikitable>tr>th, table.wikitable>*>tr>th
{
    background: linear-gradient(to bottom, #bfd2e3 0%,#8eacce 22%,#7499c4 33%,#668ebe 50%,#5986b9 67%,#334d6f 82%,#110f09 100%) !important;
    color: #8cf;
}

.container, .archive-box, ul#all-comment-providers
{
    background-color: rgba(0,0,0,0.75);
}

.messageUserBlock, #sidebar
{
    background-color: rgba(0,0,0,0.25);
}

.quotetop, p.citation
{
    background: linear-gradient(to bottom, #d9edff 0%,#a0ccfc 100%);
}

.bbc_img, .thumbimage, .alignright, a img.alignright, .alignleft, a img.alignleft, .item-related img, .gallery .gallery-icon img, .post-comments
{
    border-top: #6af 5px solid !important;
    border-left: #48f 5px solid !important;
    border-right: #48f 5px solid !important;
    border-bottom: #26c 5px solid !important;
    box-shadow: 0px 6px 5px #444 !important;
    -webkit-border-radius: 13% !important;
    border-radius: 13% !important;
}

#secondary_nav a
{
	color: #8cf;
}

a, a:hover, a:focus, a:visited, .mw-bodytext a, .mw-bodytext a:visited, .editOptions a, .editOptions a:visited, h5 a, h5 a:visited, a, #secondary_nav a, a.new, #retro_title a
{ /* Don't include this or anything that mentions #adf unless you're sure it isn't the "link color" on UserStyles! */
	color: /*[[color]]*/ !important;
    fill: /*[[color]]*/;
    text-shadow: 0px 0px 8px #000;
}
.messageHeading span a, .messageHeading span a:hover, .messageHeading span a:focus, .messageHeading span a:visited
{
    background-color: rgba(64,128,255,0.425);
    border-radius: 2em;
    padding: 0.5em;
}
 
twitterwidget
{
	border-top: #6af 5px solid !important;
    border-left: #48f 5px solid;
    border-right: #48f 5px solid;
    border-bottom: #26c 5px solid !important;
    box-shadow: 0px 6px 5px #444 !important;
}

twitterwidget::after
{
	content: "The Twitter tweet in question:";
}

.navTabs
{
    border-top: #fa6 5px solid;
    border-left: #f84 5px solid;
    border-right: #f84 5px solid;
    border-bottom: #c62 5px solid;
    box-shadow: 0px 6px 5px #444;
}

.resized_img span, span#f-lastmod, .message.error
{
    background: linear-gradient(to right, rgba(221,181,184,0) 0%,rgba(215,166,170,1) 5%,rgba(199,131,137,1) 17%,rgba(178,82,90,1) 33%,rgba(138,0,11,1) 67%,rgba(88,0,7,1) 83%,rgba(51,0,4,1) 95%,rgba(36,0,3,0) 100%);
    border: transparent 0px solid;
}

.message
{
    background: linear-gradient(to bottom, #82a3c6 0%,#5881b6 31%,#123c76 100%);
    border: #8cf 2px solid;
	-webkit-border-radius: 5%;
    border-radius: 5%;
}

div#rb_extensionBar::after
{
    content: "Sonic Mania Sonic Retro forums theme made by Xane Myers.";
}

tr.row1 tr.row2, tr.row1first, div#rb_extensionBar, ul.post_controls
{
	background: linear-gradient(to bottom, rgba(0,0,0,0.75) 0%,rgba(0,0,0,0.0) 100%);
    border-bottom: #000 0px solid;
    box-shadow: 0px 0px 0px rgba(0,0,0,0.5);
}

#copyright { float: unset !important;}

#copyright::after
{
	content: ". Sonic Mania theme by Xane Myers."
}

#copyright, #board_footer, #board_footer a
{
	color: lightgrey;
    text-shadow: 0px 0px 16px #fff;
}

html, span.bbc_inline_spoiler
{
    text-shadow: 0px 0px 3px #000;
}

body, div#rb_loginBar, div#rb_logo, html
{ /* Don't edit the background image here! It's chosen on UserStyles! */
	/*font: normal 12pt "sans-serif" !important;*/
    
    background-size: cover;
    background-attachment: fixed;
    background-image: url('/*[[bgimage]]*/');
}

img#rb_logo
{
    background-size: 50%;
    background-image: url('http://i.imgur.com/Cb9IFU9.png');
}
}
