css:
@-moz-document domain('chiefdelphi.com') {
/* Variables */
:root {
	--primary-background: #111;
    --secondary-background: #1d1d1d;
    --content-background: #272727;
    --border-color: #343434;
    --font-color: #c0c0c0;
    
    --link-visited: #99ccff;
    --link-unvisited: #cc6d00;
    
    --chief-delphi-color: #ff8800;
    --chief-delphi-background: #663600;
    --chief-delphi-border-color: #995200;
    --chief-delphi-font-color: #cc6d00;    
}

body {
    background-color: var(--primary-background);
}

.page {
    background-color: var(--secondary-background);
    border-color: var(--border-color);
    border-style: solid;
    border-width: 1px;
    color: var(--font-color);
}

/* Removes unneccessary background borders */
.panelsurround {
    background: none;
    color: #000000;
}

.tborder {
	border: none;
    background: none;
}

.tfoot {
 	background: var(--secondary-background);
}

/* Borders for main page buttons */
.cd_site_menu {
 	background: var(--secondary-background);
    border: none;
}

/* Form entries */
.panel {
 	background: var(--secondary-background);
    color: var(--font-color);
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 14px;
    line-height: 1.5;
    word-wrap: break-word;
}

/* Quick reply box */
.tcat {
    background: none;
}

/* Background surrounding the ChiefDelphi47 picture on home page */
.head47 {
    background-color: var(--secondary-background);
}

/* Content boxes */
.alt1, .alt1Active {
    background: var(--secondary-background);
    color: var(--font-color);
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 14px;
    line-height: 1.5;
    word-wrap: break-word;
}

/* User header boxes and quote boxes */
.alt2, .alt2Active {
    background: var(--content-background);
    color: var(--font-color);
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 14px;
    line-height: 1.5;
    word-wrap: break-word;
}

.thead {
 	background: var(--chief-delphi-background);
    color: var(--chief-delphi-font-color);
    border-color: var(--chief-delphi-border-color);
    border-width: 1px;
    border-style: solid;
}

.vbmenu_control {
 	background: var(--chief-delphi-background);
    color: var(--chief-delphi-font-color);
    border-color: var(--chief-delphi-border-color);
    border-width: 1px;
    border-style: solid;
}

/* Links */

a:link {
    color: var(--link-visited);
  	text-decoration:  none;
}

a:visited {
    color: var(--link-visited);
  	text-decoration:  none;
}

a:hover {
    color: var(--link-visited);
  	text-decoration:  underline;
}

/* Main links */
.alt1 a:visited, .alt1_avisited, .alt1Active a:visited, .alt1Active_avisited {
    color: var(--link-visited);
  	text-decoration:  none;
}

.alt1 a:link, .alt1_alink, .alt1Active a:link, .alt1Active_alink {
    color: var(--link-visited);
  	text-decoration:  none;
}

.alt1 a:hover, .alt1_alink, .alt1Active a:hover, .alt1Active_alink {
    color: var(--link-visited);
  	text-decoration:  underline;
}

.alt2 a:visited, .alt2_avisited, .alt2Active a:visited, .alt2Active_avisited {
    color: var(--link-visited);
  	text-decoration:  none;
}

.alt2 a:link, .alt2_alink, .alt2Active a:link, .alt2Active_alink {
    color: var(--link-visited);
  	text-decoration:  none;
}

.alt2 a:hover, .alt2_alink, .alt2Active a:hover, .alt2Active_alink {
    color: var(--link-visited);
  	text-decoration:  underline;
}

.thead a:visited, .thead_avisited {
    color: var(--active-font-color);
}

.thead a:link, .thead_alink {
    color: var(--active-font-color);
}

.vbmenu_control a:link, .vbmenu_control_alink {
    color: var(--link-unvisited);
    text-decoration: none;
}

.vbmenu_control a:visited, .vbmenu_control_avisited {
    color: var(--link-unvisited);
    text-decoration: none;
}
}
