@-moz-document domain("weeb.tv") {
/*Scroll Bar*/
::-webkit-scrollbar {
    width: 15px;
    height: 15px;
    background-color: #222
}
::-webkit-scrollbar-thumb {
    background-color: #666;
    border: 2px solid #222
}
::-webkit-scrollbar-thumb:hover {
    background-color: #999
}
::-webkit-scrollbar-thumb:active {
    background-color: #ccc
}
::-webkit-scrollbar-corner {
    background-color: #222;
    color: #555555
}
::-webkit-scrollbar-button {
    border: 2px solid #222;
    width: 12px;
    height: 12px
}
::-webkit-scrollbar-button:hover {
    background-color: #333!important
}
::-webkit-scrollbar-button:end {
    display: block
}
::-webkit-scrollbar-button:start {
    display: none
}
::-webkit-scrollbar-button:vertical {
    border-top: 0
}
::-webkit-scrollbar-button:horizontal {
    border-left: 0
}
::-webkit-scrollbar-button:vertical:increment {
    background: url("https://i.imgur.com/oclbvml.png") center center no-repeat
}
::-webkit-scrollbar-button:vertical:decrement {
    background: url("https://i.imgur.com/14PHKSe.png") center center no-repeat
}
::-webkit-scrollbar-button:horizontal:increment {
    background: url("https://i.imgur.com/IBo4sTv.png") center center no-repeat
}
::-webkit-scrollbar-button:horizontal:decrement {
    background: url("https://i.imgur.com/eeJjqhE.png") center center no-repeat
}

/*Global*/

*{
    color: #fff;
    text-shadow:
   -1px -1px 0 #000,  
    1px -1px 0 #000,
    -1px 1px 0 #000,
     1px 1px 0 #000;
  text-rendering: optimizeLegibility;
}

body{
 	background-color:#000000!important;   
    font-family: 'Radiance', 'arial', sans-serif;
    text-shadow: 0px 0px 2px #000000;
}
#container > div.block.white{
 	background-color:#212121; 
}
#container > div.block.white > div:nth-child(3), #container > div.block.white > div:nth-child(2), #container > div.block.white > div:nth-child(4){
    display:none;
}
#player_video, #playlist{
 	border:3px solid black;
    background-color: #303030;
}
[id^="playlistItem_"]{
	background-color: #424242!important;
    color: #cccccc!important;
    border:1px solid #000000!important;
}
[id^="playlistItem_"] > strong {
    color: #ffffff!important;
}

[id^="channel_div_"], #container > div.notification.tip, #container > div.notification.information, #container > div.block_footer{
    display:none;
}
[id^="channel_fieldset_"]{
    background-color:#37474F;
    border:solid 3px #263238;
}
hr {
    border-color:#212121;
}
#container > div.notification.success, #container > div.block.white > div:nth-child(1), #container > div.block.white > div.notification.success {
    display:none!important;
}
#container > div.block.white > fieldset {
    background:#333;
    border-color: #000;
}
select {
	color: #fff!important;
    text-shadow: 0 1px 1px rgba(0,0,0,0.75)!important;
    background: none;
    background-color: #222;
    border: 1px solid #000!important;
}
select:focus {
    outline: none !important;
    border-color: #000;
    box-shadow: 0 0 10px #000;
}
#playlistSearchBox {
    background-color: #222!important;
    border: 1px solid #000;
    color: #fff;
}
#playlistSearchBox:focus {
    outline: none !important;
    border-color: #000;
    box-shadow: 0 0 10px #000;
}
.tabs li a:hover span span, .tabs .active a span span {
    background: #555!important;
}
.tabs {
    background: #333!important;
}
}
