css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("en.wikipedia.org") {

/*Reduce clutter*/
#p-logo, #p-navigation, #p-tb, #p-lang, #p-interaction,#p-search h5, #searchGoButton, #mw-searchButton, #protected-icon, li#pt-watchlist, li#ca-move {
  display: none !important ;
}
li#pt-userpage { background: none !important;}
li#pt-login {background: none !important; font-weight: normal !important;}

/*Adjust the positions of the rest. You may have to tinker some stuff here.*/

#p-personal {float: right !important; margin-top: 2px !important; margin-left: -1em !important; font-size: small !important;}
#p-personal li a:hover { background-color: transparent !important; padding-bottom: 0 !important; }

#p-cactions {top: 1.5em !important; margin-left: -8em !important; font-size: small !important; font-weight: normal !important;}

div#column-one {padding: 0em !important;}
div#column-content {float: none !important; margin-left: 0 !important; padding-top: 0em !important;}
div#column-content div#content { margin: 0px auto !important; margin-top: 3em !important; padding-left: 5em !important; padding-right: 5em !important}

input#searchInput {
-moz-appearance: none !important; border: 1px solid #cacaca !important;  padding: 6px 12px 4px !important; -moz-border-radius: 20px !important; width: 15em !important; background: url(http://farm4.static.flickr.com/3030/3077594241_439e2b8597_o.gif) no-repeat 96% 53% !important; background-color: #ffffff !important;
}

div#searchBody.pBody { padding: 1px !important; border: 0 !important; background: none !important; }

#p-search { position: absolute !important; top: 2.0em !important; width: 21em !important;}
}
