css:
@-moz-document domain("cloud.feedly.com"), domain("feedly.com") {
/* Author: #Ahad
 * Modified: trixisowned, pitolw
 * Updated: May 6th 2017
 * ======================== */
/* TABLE OF CONTENTS
 * ==================
 *  1. Fonts
 *  2. Login Page
 *  3. Floating Panel and Title Bar
 *  4. Left Sidebar
 *  5. Right Sidebar
 *  6. Title Only View
 *  7. Magazine View
 *  8. Cards View
 *  9. Full Article View
 * 10. Others
 **/

}


@-moz-document url-prefix("http://www.feedly.com/home"), url-prefix("https://www.feedly.com/home"), url-prefix("http://feedly.com/index.html"), url-prefix("https://feedly.com/index.html"), domain("cloud.feedly.com"), domain("feedly.com") 

{
.slideEntryContent img {
    margin:auto!important;
}

.entryBody {
  max-width: 100%!important;
  width: 76%!important;
  margin-left: auto!important;
  margin-right: auto!important;
}
                 
body {
background: #011820;
background-size: cover!important;
}

#feedlyCenter {
background: #011820;
background-size: cover!important;
width: 99%!important;
}

#feedlyTabs {
background: #011820;
background-size: cover!important;
color: #ffffff!important;
overflow-x: hidden!important;
}                 

/* ==================== Fonts ==================== */
* {
font-family: "tahoma"!important;
text-decoration: none!important;
}

/* ==================== Login Page ==================== */
#bookshelf {
background: #8e959a!important;
margin-left: 0!important;
margin-right: 0!important;
height: 52px!important;
}

.discoverHeader {
margin-left: auto!important;
margin-right: auto!important;
padding-left: 0!important;
padding-right: 0!important;
height: 40px!important;
position: absolute!important;
top: 0!important;
right: 20px!important;
}

[src="//s3.feedly.com/production/head/images/feedly-logo-black.png"] {
display: none!important;
}

.discoverHeader .loginButton {
background: #222222!important;
color: #ffffff!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 24px!important;
font-weight: bold!important;
line-height: 40px!important;
border: 2px solid #ffffff!important;
margin-right: 0!important;
margin-top: -10px!important;
padding: 0 27px!important;
height: 44px!important;
}

.discoverHeader .loginButton:hover {
background: #ffffff!important;
color: #ffffff!important;
border: 2px solid #ffffff!important;
}

.tagline {
color: #ffffff!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 22px!important;
font-weight: normal!important;
margin-left: auto!important;
margin-right: auto!important;
margin-top: 58px!important;
margin-bottom: 10px!important;
max-width: 586px!important;
min-width: 0!important;
}

#explain .mission {
color: #000000!important;
text-shadow: none!important;
margin-left: auto!important;
margin-right: auto!important;
margin-bottom: 25px!important;
max-width: 586px!important;
min-width: 0!important;
}

#explain .button.primary {
font-weight: bold!important;
}

#explain .button.secondary {
background: #ffffff!important;
color: #000000!important;
font-weight: bold!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
box-shadow: rgba(0, 0, 0, 0.2)0 1px 2px 0!important;
}

.feature .featureLabel {
color: #000000!important;
letter-spacing: 2px!important;
}

.feature .featureDescription {
color: #000000!important;
}

#explain .metricNumber {
color: #000000!important;
}

#explain .metricLabel {
color: #ffffff!important;
}

#explain [style="text-align:center; border-top:1px solid #EFEFEF; margin-top:68px; font-size:16px; color: #999; line-height:1.4em"] {
color: #ffffff!important;
}

#explain [style="text-align:center; border-top:1px solid #EFEFEF; margin-top:68px; font-size:16px; color: #999; line-height:1.4em"] a {
color: #000000!important;
}

#herculeFrame {
background-color: transparent!important;
border-color: transparent!important;
}

.home.streched #herculeHint {
background-color: #fafafa!important;
border-width: 0!important;
box-shadow: 1px 1px 2px 0 #bbbbbb inset!important;
}

.home #herculeHint {
background-color: #eff0f2!important;
border-width: 0!important;
box-shadow: 1px 1px 2px 0 #222222 inset!important;
}

#maxHerculeHint {
background-color: #eff0f2!important;
border-width: 0!important;
box-shadow: 1px 1px 2px 0 #222222 inset!important;
}

#herculePanel {
z-index: 600!important;
}

.herculeResult.selected {
background-color: #3498db!important;
}

#feedlyPage #herculeStore[style="margin-right:-34px; margin-top:60px"] {
background: #8e959a!important;
border: none!important;
box-shadow: none!important;
margin-left: auto!important;
margin-right: auto!important;
margin-top: 45px!important;
padding-top: 0!important;
padding-bottom: 0!important;
max-width: 496px!important;
}

#feedlyPage #herculeStore.shown[style="margin-right:-34px; padding-top:0px;"] {
background: #8e959a!important;
border: none!important;
box-shadow: none!important;
margin-left: 0!important;
margin-right: 0!important;
margin-top: 45px!important;
padding-top: 0!important;
padding-bottom: 0!important;
max-width: 868px!important;
}

#herculeStore .block {
border: 1px solid #000000!important;
border-radius: 0!important;
box-shadow: 1px 1px 2px 0 #222222!important;
margin: 3px!important;
}

#feedlyPage #herculeStore[style="margin-right:-34px; padding-top:0px"] .block {
margin: 3px!important;
}

#herculeStore .block .etiquette {
background: #eff0f2!important;
color: #000000!important;
text-shadow: none!important;
font-size: 12px!important;
font-weight: bold!important;
text-align: center!important;
border-top: 1px solid #000000!important;
}

#herculeMobile {
margin-left: auto!important;
margin-right: auto!important;
padding-left: 0!important;
padding-right: 0!important;
max-width: 586px!important;
}

#herculeMobile .cell2 {
margin-left: auto!important;
margin-right: auto!important;
width: 293px!important;
max-width: 293px!important;
min-width: 293px!important;
}

#herculeMobile .cell2 h3 {
color: #000000!important;
text-shadow: none!important;
font-size: 12px!important;
font-weight: bold!important;
}

div[data-scheme="essential"] {
margin-right: 0!important;
}

.magOverview {
background-color: #ffffff!important;
border: 1px solid #000000!important;
border-radius: 3px!important;
box-shadow: 0 0 4px 0 #757575!important;
}

.magOverview .magFeedInfoCell {
max-width: 80px!important;
}

.magReaderCount div[title="Add to your feedly reading list"] {
border: none!important;
box-shadow: none!important;
}

.magFeedInfo .promptLogin {
background-color: #005cb8!important;
color: #ffff00!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 14px!important;
font-weight: bold!important;
border: 1px solid #000000!important;
box-shadow: 0 0 2px 1px #555555!important;
}

.magOverview .followingButton {
color: #008f56!important;
}

#moreFeeds {
background-color: transparent!important;
border: none!important;
margin-right: 0!important;
box-shadow: none!important;
height: 54px!important;
}

#moreFeeds .secondaryCallForAction {
background-color: #ffffff!important;
color: #333333!important;
font-size: 13px!important;
font-weight: bold!important;
line-height: 40px!important;
border: 1px solid #000000!important;
border-radius: 3px!important;
padding: 0!important;
box-shadow: 0 0 4px 0 #757575!important;
width: 262px!important;
height: 40px!important;
}

.relatedLabel {
background-color: #ffffff!important;
color: #000000!important;
border: 1px solid #000000!important;
}

body div[style="max-width: 360px; margin-left: auto; margin-right:auto"] {
width: 208px!important;
}

div#intro {
background: transparent!important;
box-shadow: none!important;
padding-top: 150px!important;
width: 208px!important;
}

div#intro div[style="margin-top:50px; margin-bottom:50px; text-align: center;"], div#intro div#feedlyBanner {
display: none!important;
}

div#intro .button {
text-shadow: 0 0 6px #000000!important;
font-size: 12px!important;
font-weight: bold!important;
border-left: 2px solid #ffffff!important;
border-right: 2px solid #ffffff!important;
border-top: 1px solid #ffffff!important;
border-bottom: 1px solid #ffffff!important;
border-radius: 0!important;
margin-left: auto!important;
margin-right: auto!important;
margin-bottom: 0!important;
width: 140px!important;
}

/* ==================== Floating Panel and Title Bar ==================== */
#proBanner, #feedlyMessageBar, #feedlyProBar {
display: none!important;
}

#feedlyUndoSign {
font-weight: bold!important;
line-height: 10px!important;
width: 200px!important;
height: 10px!important;
}

.home.streched *.action[data-app-action="askUndo"], .home *.action[data-app-action="askUndo"] {
color: #f00000!important;
font-weight: bold!important;
}

.home.streched *.action[data-app-action="askUndo"]:hover, .home *.action[data-app-action="askUndo"]:hover {
color: #ff0000!important;
}

#floatingBar {
background: #ffffff!important;
border-bottom: 3px solid #213C48!important;
box-shadow: 0 0 3px 0 #333333!important;
}

#floatingBar[style*="margin-left: 268px;"] {
width: -moz-calc(99% - 336px)!important;
}

#floatingBar[style*="margin-left: 53px;"] {
width: -moz-calc(99% - 121px)!important;
}

#floatingBar[style*="transform: translateY(-45px);"], #floatingBar[style*="transform: translateY(-45px);"] #floatingTitleBar {
background: transparent!important;
border: none!important;
box-shadow: none!important;
}

#floatingTitleBar {
background: #ffffff!important;
color: #fc2103!important;
font-size: 18px!important;
border-bottom: 1px solid #ffffff!important;
}

#floatingBar span {
color: #404040!important;
font-size: 13px!important;
font-weight: bold!important;
}

#floatingBar span.action:hover {
color: #910e07!important;
}

.home.streched #feedlyTitleBar {
color: #032aff!important;
font-size: 18px!important;
}

.home #feedlyTitleBar {
color: #000000!important;
font-size: 18px!important;
}

#feedlyTitleBar span {
color: #202020!important;
font-size: 13px!important;
font-weight: bold!important;
}

#feedlyTitleBar span.action:hover {
color: #910e07!important;
}

.home.streched .hAction.secondary, .home.streched .hAction.tertiary {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #202020!important;
font-weight: bold!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 2px 4px!important;
top: 0!important;
opacity: 0.6!important;
transition: opacity 0.5s linear 0s!important;
}

.home .hAction.secondary, .home .hAction.tertiary {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #202020!important;
font-weight: bold!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 2px 4px!important;
top: 0!important;
opacity: 0.6!important;
transition: opacity 0.5s linear 0s!important;
}

.home.streched .hAction.promptLogin {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #202020!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 6px 16px!important;
top: 0!important;
opacity: 1!important;
}

.home .hAction.promptLogin {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #202020!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 6px 16px!important;
top: 0!important;
opacity: 1!important;
}

.home.streched .hAction.secondary:hover, .home.streched .hAction.tertiary:hover,
.home .hAction.secondary:hover, .home .hAction.tertiary:hover {
opacity: 1!important;
}

.pageAction {
opacity: 1!important;
}

.home.streched .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #202020!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 0 8px!important;
}

.home .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #202020!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 0 8px!important;
}

#floatingBar .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #202020!important;
border: 1px solid #aaaaaa!important;
margin-top: 5px!important;
}

#floatingBar .hAction.secondary,
#floatingBar .hAction.tertiary,
#floatingBar .hAction.promptLogin,
#floatingBar .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #202020!important;
border: 1px solid #aaaaaa!important;
}

.popup.customizer .item.selectedItem {
color: #000000!important;
font-weight: bold!important;
border: none!important;
}

h2 {
color: #000000!important;
font-size: 16px!important;
font-weight: bold!important;
text-transform: capitalize!important;
padding-top: 10px!important;
position: relative!important;
z-index: 500!important;
}

.home.streched h2 *.action {
color: #032aff!important;
font-weight: bold!important;
font-size: 13px!important;
}

.home h2 *.action {
color: #00008f!important;
font-weight: bold!important;
font-size: 13px!important;
}

.home.streched h2 *.action:hover {
color: #910e07!important;
}

.home h2 *.action:hover {
color: #610e07!important;
}

.feedUnreadCountHint {
color: #333333!important;
font-weight: bold!important;
}

.home.streched .infoBox {
color: #202020!important;
}

.home .infoBox {
color: #000000!important;
}

.home.streched .infoBox a {
color: #0000ff!important;
font-weight: bold!important;
}

.home .infoBox a {
color: #0000bf!important;
font-weight: bold!important;
}

.home.streched .infoBox a:hover, .home .infoBox a:hover {
color: #910e07!important;
}

#realtimeSign {
display: none!important;
}

/* ==================== Left Sidebar ==================== */
#feedlyTabsHolder {
background-color: transparent!important;
background-image: url('http://s3.feedly.com/production/head/images/icon-feedly-white.png')!important;
background-position: 14px 18px!important;
}



#feedlyTabsPin, #feedlyTabsUnPin {
background: #000000!important;
color: #ffffff!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 14px!important;
font-weight: bold!important;
text-align: right!important;
border: 1px solid #444444!important;
border-radius: 3px!important;
box-shadow: 0 0 3px 1px #000000!important;
margin-right: -30px!important;
margin-bottom: 10px!important;
padding: 2px 4px 4px 4px!important;
opacity: 1!important;
}

#feedlyTabs .header div, #feedlyTabs .header a, #feedlyTabs .feedIndex div {
color: #ffffff!important;
font-weight: bold!important;
}

#feedlyTabs .label.empty, #feedlyTabs .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

#feedlyTabs .header.selected .label.empty, #feedlyTabs .feedIndex.selected .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

#feedlyTabs .categoryUnreadCount.staticSimpleUnreadCount, #feedlyTabs .feedUnreadCount.staticSimpleUnreadCount {
font-size: 11px!important;
}

#feedlyTabs .header:hover, #feedlyTabs .feedIndex:hover {
background: rgba(255,255,255,0.3)!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 35px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

#feedlyTabs .header.selected, #feedlyTabs .feedIndex.selected {
background: #ffffff!important;
border-left: 9px solid #570000!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

#feedlyTabs .header.selected div, #feedlyTabs .feedIndex.selected div {
color: #570000!important;
font-weight: bold!important;
}

#feedlyTabs .header.selected:hover, #feedlyTabs .feedIndex.selected:hover {
background: #ffffff!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
}

#feedlyTabsHolder .icon, #feedlyTabsHolder:hover .header.selected .icon {
opacity: 0.4!important;
}

#feedlyTabsHolder:hover .icon {
opacity: 1!important;
}

#mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my-active.png")!important;
}

#musttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-mustread-active.png")!important;
}

#savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved-active.png")!important;
}

#latesttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-latest-active.png")!important;
}

.icon.handle {
background-image: url("http://s3.feedly.com/production/head/images/selector-right-arrow-active.png")!important;
}

#feedlyTabs .header.selected #mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my.png")!important;
}

#feedlyTabs .header.selected #musttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-mustread.png")!important;
}

#feedlyTabs .header.selected #savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved.png")!important;
}

#feedlyTabs .header.selected #latesttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-latest.png")!important;
}

#feedlyTabs .header.selected .icon.handle {
background-image: url("http://s3.feedly.com/production/head/images/selector-right-arrow.png")!important;
}

#feedlyTabs div[style="margin-left: 30px; padding-top:1px; margin-bottom: 34px; font-size: 11px; line-height: 1.3em; opacity: 0.65"] {
color: #ffffff!important;
opacity: 0.7!important;
}

#fixedProfile {
background-color: #011820!important;
border-right: 1px solid #000000!important;
border-top: 1px solid #000000!important;
border-bottom: 1px solid #000000!important;
box-shadow: 0 0 3px 0 #000000!important;
padding: 5px 0 7px 10px!important;
width: 40px!important;
