css:
@-moz-document domain("eldarya.ru"), domain("eldarya.fr") {
/*Блобби фамильяр*/
/*картинки*/

#pet-headerPortrait-base {
    background: url("http://i12.pixs.ru/storage/7/3/9/Screenshot_5468697_25717739.png") -15px top no-repeat !important;
    background-size: auto 125px !important;
}
#pet-portrait {
    background: url("http://i11.pixs.ru/storage/6/7/0/pusyagif_5561615_25717670.gif") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Инфа*/

#pet-info-name:before {
    content: "Блобби" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: "Блобби НЕ МОЖЕТ вырасти";
}
#pet-info-racegender:before {
    content: "???" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.fr/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}

/*Блобби с игроком*/

#player-display-pet {
    background: url("http://i12.pixs.ru/storage/7/5/1/Animation1_8566982_25729751.gif") no-repeat;
    background-size: auto 100%;
    height: 135px !important;
    width: 270px !important;
}
#player-display-pet > img {
    display: none !important;
}
}
