css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("madfanboy.com") {

	@media (min-width: 1200px) 
	{
		.body-layout-Fluid .wrap, .body-layout-Fluid #inner-wrap {
			width: 90%;
			min-width: 1200px;
		}
	}
	@media (min-width: 992px)and (max-width: 1199px) {
		.body-layout-Fluid .wrap, .body-layout-Fluid #inner-wrap {
			padding: 0 15px;
			width: auto;
		}
	}
	
	.username-coloured
	{
		font-weight: 700 !important;
	}
	
	h2 {
		font-weight: bolder;
    font-size: 2em;
  }
	a{
    color: #3a3a3a;
		font-weight: bolder;
  }	
	
	p.author, p.author a {
    color: #83888b;
  }
	
	#inner-wrap
	{
    background: #a8bcd0;
  }
	.forabg, .forumbg, .viewtopic_wrapper 
	{
    background-color: #3b4559;
  }
	.viewtopic_wrapper
	{
    padding: 8px;
  }
	.post 
	{
		padding: 8px 10px;
  }
	.bg2, .bg1 
	{
    background-color: #e0e8ec;
  }
  .postbody .content 
	{
    font-size: 1.2em;
		/*font-weight: 400;*/
		color: #212121;
  }
	.postprofile
	{
    padding: 1% 1%;
		width: 11%;
		border: 1px solid;
    /*min-height: 80px;
		max-width: 200px; 
		min-width: 180px; ;  
    border: 1px solid transparent;
    border-width: 0 0 0 1px;
    width: 22%;
    float: right;
    display: inline;
    text-align: center; */
  }
	.postbody 
	{
		padding: 5px;
		width: 85%;
  }
	.profile-rank img
	{
		width: 100%;
		max-width: 172px;
	}
	
	li.row 
	{
    background-color: #e8f1f6;
  }
	
	.responsive-show
	{
		display: block !important ;
	}
	.responsive-hide .username-coloured 
	{
		margin: 0px 0px 0px 0.5em;
	}
	
	.navbar a
	{
    color: #636669;
  }
	.action-bar.bar-bottom 
	{
    margin-top: 30px;
  }

  ul.topiclist dt {
    font-size: 1.1em;
    font-weight: 500;
  }
	a.forumtitle {
		color: #000000;
  }
	
	.panel {
    background-color: #e0e8ec;
		border: 1px solid;
  }
	.message-box 
	{
    width: 99%;
  }
	.smiley-box
	{
		width: 100%;
		float: none;
	}	
	.fields1
	{
		display: flex;
		flex-flow: column;
	}
	div#format-buttons{ 
    	order:1;
    }
	div#message-box{ 
    	order:2;
    }
	div#smiley-box{ 
    	order:9;
  }
	input.button1, input.button2 
	{
		color: #FFFFFF;
		background-color: #38618F;
  }
	input.button1:hover, input.button2:hover 
	{
		background-color: #597ca3;
  }
	
	
	a:hover, .social_links_footer a:hover span, a:hover .icon.fa-file.icon-red, .navigation .active-subsection a:hover, .navigation a:hover, .tabs .tab > a:hover, .tabs .activetab > a:hover, .button:hover .icon, a.postlink:hover {
    color: #49a09d;
  }
	
	.postbody h3 {
    font-size: 1.9em;
  }
	
	/*.headerbar, body.content_block_header_block li.header, body.content_block_header_stripe li.header::before, .no_avatar, .social_links_footer, .badge, thead tr, .sidebar_block_stripe::before, .fancy_panel::before 
	{
    background: #859398;
    background: -moz-linear-gradient(left, #859398 0%, #283048 100%);
    background: -webkit-linear-gradient(left, #859398 0%,#283048 100%);
    background: linear-gradient(to right, #859398 0%,#283048 100%);
  } */
	.particles_container
	{
		background-image: url(http://i.imgur.com/CQmGULR.png); 
		background-size: 300px 117px;
		background-position: left 45%;
    background-repeat: no-repeat;
    background-color: #00000000; 
	}
	.cp-main .pm
	{
		background-color: #c1c9cd;
		border: 1px solid;
	}
	.topicreview .bg1, .topicreview .bg2 {
		border: 1px solid;
		background-color: #e0e8ec;
  }
	#review
	{
		color: #000000;
	}
	.panel-container .postbody p.author
	{
		color: #55585a;
  }
	.navbar_footer a, .copyright_bar a
	{
		color: #0489b9;
	}
	.stat-block.thanks-list
	{
		background-color: #e8f1f6;
		margin: 0px;
	}
	
	blockquote
	{	
    background-color: #c1c9cd;
  }
	blockquote blockquote 
	{
    background-color: #e0e8ec;
  }
	blockquote blockquote blockquote 
	{
    background-color: #c1c9cd;
  }
	blockquote blockquote blockquote blockquote
	{
    background-color: #e0e8ec;
  }	
	blockquote blockquote blockquote blockquote blockquote 
	{
    background-color: #c1c9cd;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #e0e8ec;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #c1c9cd;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #e0e8ec;
  }
}
