@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("eldarya.pl") {



/*Valuret*/  

 

  /*Images - pictures*/

 #pet-headerPortrait-base {

   background: url("https://zapodaj.net/images/e660b4266cc3f.png") -10px top no-repeat !important;

  background-size : auto 90px !important ;

  }



  #pet-portrait {

    background : url("https://zapodaj.net/images/3bad43d2f8932.gif") center no-repeat !important ;

    background-size : auto 390px !important ;

    height : 450px !important ;

  }



  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}



    /*Infos*/



  #pet-info-name:before {

    content : "Pan Valuret ♥" !important ;

    font-size : 20px !important ;

  }

  

  #pet-info-name {

    font-size : 0 !important ;

  }  

  

  #pet-info-evolve > .on {font-size : 0 !important ;}

  

  .pet-info-evolve-icon {font-size : 13px !important ;}

  

  .pet-info-evolve-icon:after {

    content : " Valuret ma wysokie ambicje!";

  }

  

  

  #pet-info-racegender:before {

    content : "Valuret" !important ;

    font-size : 16px !important ;

    line-height : 20px !important ;

  } 

  

  #pet-info-racegender {

    font-size : 0 !important ;

    margin-top : 5px ;

  }  

  

  img[src*="/static/img/f.png"] {

    background : url("http://www.eldarya.com/static/img/m.png") !important ;

    padding-left : 14px !important ;

    padding-top : 15px !important ;

    width : 0 !important ;

    height : 0 !important ; 

    margin-bottom : 5px !important ;

  }

 

 /*Nourriture - food*/

  

 #pet-info-food > img {

  background : url("http://www.eldarya.pl/static/img/item/consumable/1382e609eddf83e19b9e30e3f2b200d9.png") !important ;

  background-size : 60px 60px !important ;

  margin-top : -5px !important ;

  padding-left : 60px !important ;

  padding-top : 60px !important ;

  width : 0 !important ;

  height : 0 !important ;

} 

  

  #pet-info-food {font-size : 0 !important ;}

  

  #pet-info-food:before {

    content : "Cukierki miłości  " !important ;

    font-size : 16px !important ;

  }

  

  #pet-info-food > span {font-size : 16px !important ;}

}
