css:
@-moz-document url-prefix("http://www.eldarya.fr/"), url-prefix("http://fa1.eldarya.com/"), url-prefix("http://www.eldarya.ru/"), url-prefix("http://www.eldarya.com/"), url-prefix("http://www.eldarya.com.br/"), url-prefix("http://www.eldarya.de/"), url-prefix("http://www.eldarya.es/"), url-prefix("http://www.eldarya.fi/"), url-prefix("http://www.eldarya.hu/"), url-prefix("http://www.eldarya.it/"), url-prefix("http://www.eldarya.pl/") {
#pet-headerPortrait #pet-headerPortrait-base {
    background: url("http://www.eldarya.com.br/static/img/event/aprilFool/pet-portrait~1490711516.jpg") no-repeat !important;
    -webkit-filter: hue-rotate(0deg);
    filter: hue-rotate(0deg);
}
#pet-portrait {
    background: url("http://i.imgur.com/KMaBg6u.gif") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
#player-display-pet {
    background: url("http://i.imgur.com/KijXHac.png") center no-repeat !important;
    background-size: auto 100%;
    height: 340px !important;
    width: 329px !important;
}
#player-display-pet > img[src*="/static/img/pet/mood/profile/"] {
    opacity: 0 !important;
}
#player-display-pet {
    background: url("http://i.imgur.com/0FQbaCl.png") center no-repeat !important;
}
/*Infos*/

#pet-info-racegender:before {
    content: "???" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
/*Infos*/

#pet-info-name:before {
    content: "Blobby" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Por algum milagre, você evoluiu Blobby!" !important;
    font-size: 13px !important;
    line-height: 20px !important;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.fr/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
img[src*="/static/img/f.png"] {
    background: url("http://i.imgur.com/UQC2hyQ.gif") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Nourriture - food*/

#pet-info-food:before {
    line-height: 15px;
    content: "Jenny the Jelly" !important;
    font-size: 20px !important;
    margin: 200px auto auto 34px;
}
#pet-info-food > div {
    margin-top: 0px !important;
    line-height: 20px;
    font-size: 10px;
}
#pet-info-food > img {
    background: url("http://i.imgur.com/CWrUbaE.png") !important;
    background-size: 70px 70px !important;
    margin-top: 5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}

}
