css:
@-moz-document domain("www.cloudflare.com") {
.apps-nav {
    background-color: #2C2F33;
}
.site-wrapper {
    background-color: #23272A;
}
h1,
.h1,
h4,
.h4 {
    color: #fff
}
.subheadline {
    color: #99AAB5;
}
h3,
.callout .callout-heading,
.h3,
.radio-group-jumbo label {
    color: #fff;
}
p {
    color: #fff;
}
.flexbox .mod-setting,
.flexbox .mod-setting .mod-setting-control {
    background-color: #2C2F33;
}
.card__control_text,
.card__control {
    background-color: #2C2F33;
}
.settings {
    background-color: #2C2F33;
}
.settings .setting {
    margin-top: 2em;
}
.cf-card__content,
.cf-card__control {
    background-color: #2C2F33;
}
.mod-radio-group label,
.mod-checkbox-group label {
    color: #fff
}
.controls .radio:only-child,
.controls .checkbox:only-child {
    color: #fff;
}
.modal-header {
    background-color: #23272A;
}
.modal-section,
.purge-tags {
    background-color: #2C2F33;
}
.modal-footer {
    background-color: #2C2F33;
}
.login-form,
.forgot-password-form,
.password-reset-form,
.signup-form,
.signup-promotion {
    background-color: #2C2F33;
}
.apps-nav-container {
    background-color: #2C2F33;
}
.icon-item a {
    color: #FFF;
}
.icon-item.active .icon-title {
    color: #ddd;
}
.icon-item .icon,
.icon-item.empty i {
    background-color: #99AAB5;
}
.icon-item:hover .icon {
    background-color: #7289DA;
}
.icon-item.active .icon {
    background-color: #7289DA;
}
.gradient-bar-header {
    background-image: -webkit-linear-gradient(315deg, #7289DA, #99AAB5);
    background-image: linear-gradient(135deg, #7289DA, #99AAB5);
}
.footer {
    background-color: #2C2F33;
}
.footer-column .widget-title {
    color: #fff;
}
.footer-column ul.menu-footer li {
    color: #fff;
}
.navigation a:hover {
    color: #5769f1;
}
.navigation a {
    color: #7289DA;
}
.mod-header {
    background-color: #2C2F33;
}
b,
strong {
    color: #fff;
}
.text-nonessential,
.pagination .pagination-info {
    color: #99AAB5;
}
.mod-title {
    color: #fff;
}
.analytics-data.columns.three>.column {
    background-color: #2C2F33;
    border-right: 1px solid #7289DA;
}
.analytics-data h2 {
    color: #fff;
}
a {
    color: #7289DA;
}
a:hover {
    color: #5769f1;
}
.modunit>*+* {
    border-top: 1px solid #7289DA;
}
.modunit-tabs .tabs li.active a {
    background-color: #23272A;
    color: #fff;
    border-top: 2px solid #7289DA;
}
.tabs a {
    background-color: #2C2F33;
    color: #fff;
}
.tabs li:hover a {
    background-color: #23272A;
    color: #fff;
}
.form-top-wrapper {
    background-color: #2C2F33;
}
.dns-modunit .legend .col-8 {
    margin-top: 2em;
}
.radio-group-jumbo>li:hover label {
    color: #7289DA;
}
.radio-group-jumbo input[type="radio"]:checked+label {
    background-color: #7289DA;
}
.radio-group-jumbo label {
    color: #7289DA;
}
}
