css:
@-moz-document domain("theoldreader.com") {
/* Restore Original Logo */

.navbar .brand {
	height: 43px;
	background-size: contain; 
	background-image: url("http://i.imgur.com/gsc6Dp7.png");
}
/* Make big buttons green */

.btn-primary {
	color: #fff;
	background-color: #28A77B;
	border-color: #28A77B;
}
.btn-primary:hover,
.btn-primary:focus,
.btn-primary.focus,
.btn-primary:active,
.btn-primary.active {
	color: #fff;
	background-color: #28A77B;
	border-color: #28A77B
}
/*Make hyperlinks GREEN */

a {
	color: #28A77B
}
a:hover {
	color: #28A77B;
	text-decoration: underline
}
/*make dropdown menus green */

.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus {
	text-decoration: none;
	color: #fff;
	background-color: #28A77B
}
/* Search Button --> Classic */

.form-search .btn
{
	background-image: url("https://i.imgur.com/diObMaD.png");
        background-size: 18px 18px;
}

/*Loader GIF (yes, that kind of GIF) --> Green */

.loader {
	filter: hue-rotate(90deg);
	-webkit-filter: hue-rotate(90deg);
}
/* Subscribe Plus button --> Classic */

.subscribe-button-container .subscribe .add-icon {
	background-image: url("http://i.imgur.com/XYHilSy.png");
}
/*Subscribe sub--menu plus button --> Green */

.subscribe-button-container .subscribe .dropdown-menu li form .control-group button {
	-webkit-filter: hue-rotate(90deg);
	filter: hue-rotate(160deg);
}
/*Make Sidebar hover Green Again! */

.nav-list li a:hover,
.nav-list li a:hover:hover,
.nav-list li a:focus:hover {
	color: #28A77B;
}
/*Selected sidebar page --> green */

.nav-list li.active a,
.nav-list li.active a:hover,
.nav-list li.active a:focus {
	color: #28A77B;
}
/*Hovering over active page in sidebar --> green */

.nav-list li.active a:hover,
.nav-list li.active a:hover:hover,
.nav-list li.active a:focus:hover {
	color: #28A77B;
}

/* "Import" & "help" buttons in header --> green */

.navbar-default .navbar-nav>li>a:hover {
	color: #28A77B
}
/*social features(liked, shared etc) icons --> classic red */

.nav-list li.social.active a, .nav-list li.social.active a:hover, .nav-list li.social.active a:focus {
    color: #D9442C;
}

.nav-list li.social a:hover, .nav-list li.social a:focus {
    color: #D9442C;
}

.nav-list li.social.active a:not(.fa):before, .nav-list li.social.active a:hover:not(.fa):before, .nav-list li.social.active a:focus:not(.fa):before {
     border-left: 3px solid #D9442C;
}

/* Make "move" position indicator green */

.nav-list .droptarget {
	border-top: 1px dotted #28A77B
}
/* Make "move to folder first choice green */
.dropdown-menu>.active>a, .dropdown-menu>.active>a:hover, .dropdown-menu>.active>a:focus {

    background-color: #28A77B;
}

/* Sidebar selected line --> green */

.nav-list li.nav-header.active a:not(.fa):before, .nav-list li.nav-header.active a:hover:not(.fa):before, .nav-list li.nav-header.active a:focus:not(.fa):before {
    border-left: 3px solid #999;
}

.nav-list li.active a:not(.fa):before, .nav-list li.active a:hover:not(.fa):before, .nav-list li.active a:focus:not(.fa):before {
    border-left: 3px solid #28A77B;
}

/* Unread post left border --> green */

.post.unread {
	border-left: 4px solid #28A77B !important;
}
/* Make feed label green */

.label.label-feed {
	background-color: #28A77B !important;
}

/*feed Unread counter badge bg --> green */

.badge.badge-info {
	background-color: #28A77B !important;
}
/*Green scrollbar */

.slider {
	background: none repeat scroll 0 0 #28A77B !important;
}
/*Following & Followers --> classic red */
.label.label-social {
    background-color: #D9442C;
}
/*Product Tour --> classic red */
.home-page .ourstuff ul:first-child li:first-child a {
    color: #D9442C;
}
/* "User likes xyz --> classic red */
.post .likes a {
    color: #D9442C;
}

/* Find Friends hover --> green */
a.social:hover {
    color: #28A77B !important;
}

/* Upgrade to premium --> classic red */
a.buy-now {
    color: #D9442C !important;
}
}
