css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("0chan.hk") {
  /* Применяет для всего сайта чёрный цвет. */
  #content {
    background: #333;
  }
  
  /* Шапка сайта. */
  .headmenu {
    background: #333;
    border-bottom: #D9D9D9 1px solid;
  }
  
  .headmenu-title span, .headmenu-title a {
    color: #D9D9D9 !important;
  }
  
  /* Облако поста ОПа. */
  div.block.post.post-op {
    background-color: #2D2D2D;
    box-shadow: none;
    border-color: rgba(60, 156, 60, 0.50);
  }

  .post .post-header {
    color: #3CCD9D;
  }
  
  /* Линия отделяющая контент поста от кнопок снизу. */
  div.post-footer {
    border-color: rgba(65, 56, 56, 215) !important;
  }

  /* Тёмный стиль спойлера. */
  mark, .spoiler {
    color: #3C3C3C;
    background: #3C3C3C;
  }

  /* Основной бэкграунд сайта. */
  #content > div {
    background-color: #1E1E1E;
  }
  
  /* Бэкграунд остальных ответов. */
  div.block.post {
    background-color: #282828;
    border-color: rgba(65, 56, 56, 215);
  }
  
  #content > div > div > div > div.thread > div > div > div.block.post.post-op > div.post-header > span.post-id > span {
    color: hsla(0,0%,100%,.7);
  }

  #content > div > div > div > div.thread > div > div > span {
    color: hsla(0,0%,100%,.7);
  }

  #content > div > div.headmenu > div.headmenu-title > span > span {
    color: hsla(0,0%,100%,.7) !important;
  }
  
  div.separator {
    border-color: hsla(0,0%,100%,.04);
  }

  div.post-body-message > div {
    color: hsla(0,0%,100%,.7);
  }

  div.block.post {
    background-color: #2D2D2D;
    box-shadow: none;
    border-color: hsla(0,0%,100%,.04);
  }

  input.form-control {
    background-color: #2e3136 !important;
  }
  
  /* Форма ответа. */
  .reply-form.block {
    background-color: #2B2B2B;
    box-shadow: none;
    border-color: hsla(0,0%,100%,.04);
  }

  .reply-form-message {
    color: hsla(0,0%,100%,.7); 
    background-color: #36393e !important;
  }

  textarea {
    color: hsla(0,0%,100%,.7);
    background-color: #1F2023;
  }

  button.btn.btn-xs.btn-default {
    color: #fff;
    background-color: #16a085;
    border-color: #16a085;
  }
  
  /* Сайдбар с кнопками слева. */
  div#sidebar {
    background: #1F1F1F;
    border-right: #D9D9D9 1px solid;
  }
  
  /* Преобразование кода под тёмный стиль. */
  pre, code {
    color: hsla(0,0%,100%,.7);
    background-color: #1C1C1C; 
    border-color: #363636; 
  } 
  
  /* Преобразование страницы аккаунта под тёмный стиль. */
  div.panel.panel-default {
    background: #282828;
    color: #FFF;
  }
  
  div.panel-footer.text-right {
    background: #282828;
  }
  
  div.panel-heading {
    background-color: #282828;
    background: #282828;
    color: #FFF;
  }

  /* Изменение бэкграунда Скроллбара справа. */
  ul {
    background: #000;
  }
  
  .threads-scroll-spy .threads-scroll-toggler {
    position: static;
    right: -10px;
    top: 0;
    padding: 1px;
    color: #FFF;
    cursor: pointer;
    margin: 2px;
    background: #222;
  }
  
  /* Фикс бэкграунда кнопок Сайдбара. */
  .sidebar ul.list li {
    background: #1F1F1F;
  }

  /* Личные сообщения. */
  .dialog-header {
    color: #000;						
  }

  .dialog .im-message {
    background: #333;
  }

  .panel-default > .panel-heading {
    color: #D9D9D9;
    background-color: #333;
    border-color: #ddd;
  }

  a.list-group-item, button.list-group-item {
    color: #AAA;
    background: #222;
  }

  .dialog .dialog-footer {
    position: absolute;
    bottom: 0;
    right: 0;
    left: 0;
    height: 80px;
    background-color: #2D2D2D;
    box-shadow: none;
    border-color: hsla(0,0%,100%,.04);
  }

  .dialog .dialog-footer textarea, .dialog .dialog-footer .btn {
    height: 60px;
    color: hsla(0,0%,100%,.7);
    background-color: #1F2023;
  }

  .panel {
    margin-bottom: 20px;
    background-color: #fff;
    border: 1px solid transparent;
    border-radius: 2px;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.05);
    height: 100%;
  }

  /* Изменение цвета капчи. */
  .captcha-form-wrapper {
    background: rgba(5,5,5,0.6);
  }

  .captcha-form {
    background: rgba(5,0,5,0.8);
    color: #16a085;
    width: 210px;
  }

  .captcha-form input {
    position: absolute;
    left: 2px;
    width: 155px;
    padding: 14px;
    border: 1px solid #666;
    background: #1F2023;
  }
}
