css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https://www\\.realmeye\\.com/(?!forum).*'){
  
  body {
    color: #fff;
    background: #0a0a0a;
    font-size: 12px;
  }
  
  a {
    color: #3af;
  }
  
  input {
    background: black !important;
    color: white !important;
  }
  
  hr.pill-separator {
    border: 1px solid;
    color: #666;
  }
  
  .tt-dropdown-menu {
    background: #333;
    box-shadow: 0 0 2px black;
  }
  
  .popover {
    background: #222;
  }
  
  .popover-inner {
    background: #333;
  }
  
  .popover-content {
    background: #333;
    border-radius: 5px;
  }
  
  .popover-content .character-dyes td, .popover-content .stats-table td, .popover-content .stats-table th, .popover-content .alternatives-table td {
    background: #333 !important;
    color: white;
    text-shadow: none !important;
  }
  
  .popover tr:hover { box-shadow: none; }
  
  .popover-title {
    background: #0a0a0a !important;
    color: white;
  }
  
  .to-max {
    color: #5af !important;
  }
  
  .above-avg {
    color: #0f0 !important;
  }
  
  .below-avg {
    color: #f03 !important;
  }
  
  .maxed {
    color: gold !important;
  }
  
  tr:hover {
    background: #0a0a0a !important;
    box-shadow: 0 0 5px black;
  }
  
  td {
    padding: 0 10px !important;
  }
  
  .navbar-default {
    background: linear-gradient(to top, #111, #222);
    border-color: #222;
  }
  
  .navbar-default span {
    color: white !important;
  }
  
  .navbar-nav > li > a {
    color: white !important;
  }
  
  .navbar-nav > li.open > a {
    background: linear-gradient(to top, #0a0a0a, #1a1a1a) !important;
    color: white !important;
    text-shadow: 0 0 3px;
  }
  
  .dropdown-menu {
    background: #111;
    color: #fff !important;
    border-bottom: 1px dashed #222;
  }
  
  .dropdown-menu > li > a {
    color: #ccc;
  }
  
  .navbar-right {
  }
  
  .summary tr {
    border-bottom: 1px dashed #333;
  }
  
  .well {
    background: #0a0a0a;
    color: #ccc;
    border: 1px dashed #222;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
  }
  
  .star-container div {
    margin-right: 5px;
  }
  .star {
    transform: scale(0.66);
  }
  
  .diff-bigger, .diff-smaller {
    text-shadow: 0 0 2px;
    font-size: .8em;
  }
  
  .diff-bigger {
    color: #0f2;
  } 
  .diff-smaller {
    color: red;
  }
  
  .pet-ability {
    text-shadow: none;
  }
  
  .pet-ability-maxed {
    color: gold;
  }
  
  .table > tbody > tr:nth-of-type(2n+1) {
    background: #333;
  }
  
  .table > tbody > tr:nth-of-type(2n) {
    background: #222;
  }
  
  .table thead {
    background: black;
  }

  .table-striped {
    background: black;
  }
  
  .table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    padding: 0px;
    border-top: none;
  }
  
  #f > tbody > tr > td:nth-child(9), #k > tbody > tr > td:nth-child(9), #k > thead > tr > th:nth-child(9), #f > thead > tr > th:nth-child(9) {
    text-align: left;
    padding-left: 8px !important;
  }
  
  .table td > .dropdown, .table td > .dropup {
    text-align: center;
  }
  .table > thead > tr > th {
    border-bottom: 1px dashed #555;
  }
  
  .item-wrapper{
    background-color: #363636;
    display: inline-block;
    border-bottom: 1px solid #444;
  }
  
  .item-wrapper:nth-child(5){
    border-bottom: none;
    transition: none;
  }
  
  .item-wrapper:nth-child(1) {
    border-left: 1px solid #444;
  }
  
  .item-wrapper:nth-child(4) {
    border-right: 1px solid #444;
  }
  
  .item-wrapper:nth-child(5) {
    background-color: transparent;
  }
  
  .table > tbody > tr:hover > td > .item-wrapper {
    background-color: #262626;
  }
  .table > tbody > tr:hover > td > .item-wrapper:nth-child(5){
    background-color: #0a0a0a !important;
  }
  
  .row > div > span .item-wrapper .item {
    margin: 4px 4px -1px !important;
  }
  
  .item-wrapper:nth-child(1) .item {
    margin-left: 4px;
  }
  .item-wrapper:nth-child(4) .item {
    margin-right: 4px;
  }
  
  .item {
    display: inline-block;
    background-color: #545454;
    
    margin: 4px 2px -1px;
  }
  
  .item[title="Backpack"] {
    outline: none;
    background-color: transparent;
  }
  
  button .item, .item[data-item], .btn-group .item, .modal-body .item-wrapper .item {
    margin: 0;
    background-color: transparent;
  }
  
  .current-offers .item-wrapper .item {
    background-color: transparent;
    padding: 0;
    margin: 0;
  }
  
  .current-offers .item-wrapper {
    border: none;
    box-shadow: 0 0 3px #aaa;
  }
  
  .popover .item, .item-editor .item, .modal-body .item, .item-static .item {
    border: none;
    background-color: transparent;
  }
  
  #item-selector .modal-content {
    background: transparent;
  }
  
  .modal-content {
    background: #111;
  }
  
  #g tbody td:nth-child(6){
    text-align: right;
  }
  
  .trades > tbody {
    background: #333;
  }
  
  thead tr:hover {
    background: black !important;
    box-shadow: none !important;
  }
  
  .trades tr:nth-of-type(2n){
    background: #222;
  }
  
  .motto {
    color: white;
    text-shadow: 0 0 10px #555;
  }
  
  .btn-success {
    background: linear-gradient(to top, #5b5, #494) !important;
  }
  .btn-warning {
    background: linear-gradient(to top, #fa4, #e91) !important;
  }
  .btn-danger {
    background: linear-gradient(to top, #d54, #b22) !important;
  }
  .btn-primary {
    background: linear-gradient(to top, #37b, #258) !important;
  }
  .btn-default {
    background: linear-gradient(to top, #161616, #262626);
  }
  
  .btn {
    color: #fff !important;
    text-shadow: none !important;
    border: 1px solid #000 !important;
  }
  
  .list-selector-item {
    border: none !important;
  }
  
  .btn-default { border: none; }
  
  .btn-default.disabled, .btn-default:disabled, .btn-default[disabled] {
    background-color: black;
  }
  
  .btn-group .active {
    background-color: #222;
  }
  
  .btn:focus, .btn:hover, .btn-default:focus, .btn-default:hover {
    background-position: 0 !important;
    background: linear-gradient(to top, #0a0a0a, #333) !important;
  }
  
  .trades .item-editor, .trades .item-add {
    margin: 4px 0
  }
  
  .guild-star-distribution {
    border: none;
    display: inline-flex;
    height: 7px;
    background: #555;
  }
  
  .guild-star-count{ height: auto; }
  
  .player-fame-history, .player-rank-histogram, .guild-server-activity {
    box-shadow: 0 0 5px black;
    background: #ccc;
    color: black;
  }
  
  .player-rank-histogram > .plate { background: white; }
  
  .form-control, .thread-information  .panel, .panel-body, select {
    background: black;
    color: white;
  }
  
  .panel-footer {
    background: #333;
  }
  
  .preview, .message-body {
    background: #0a0a0a;
    color: white;
  }
  
  #k button { margin: -15px 0; }
}
