css:
@-moz-document domain("minesweeper.online") {
a:active {font-size: 20px !important; 
    color: #ffffff !important;}
a:link {color: #ffffff !important;}
a:hover {text-decoration: underline !important; 
    color: #727272 !important;}
a:visited {color: #ffffff !important;}

/*bakground color*/
body {
    background-color: #313131 !important;
}

/*buttons*/
.btn-default {
    background-color: #424040 !important;
    color: #ffffff !important;
    border-color: #dddddd !important;
    border: 1px !important;
}

/*top bar*/
.navbar-desktop {
    background-image: linear-gradient(0deg, #313131 -2%, #727272 160%, #111111 50%)!important;
}

/*main content*/
.main-content {
background-color: #424040 !important;
}

/*side bar*/
.left-column {
    font-size: 18px !important;
    color: #FFFFFF !important;
}
.sidebar-nav h4 {
    font-size: 18px !important;
    color: #FFFFFF !important;
}
.sidebar-nav ul li a {
    color: #FFFFFF !important;
}

.nf-label {
    color: #ffffff !important;
}
.select-dropdown {
    color: #ffffff !important;
}
*, :after, :before {
    color: #ffffff !important;
}


/*score bord*/
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    background-color: #424040 !important;
        color: #ffffff !important;
}


/*page selector*/
.pagination-sm>li>a, .pagination-sm>li>span {
    background-color: #424040 !important;
}
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover {
    background-color: #424040 !important;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    background-color: #424040 !important;
    border-color: #dddddd !important;
}


/*season dropdown menu*/
.dropdown-menu {
    background-color: #424040 !important;
}


/*zoom dropdown menu*/
.form-control {
    background-color: #424040 !important;
    border: 1px solid #dddddd !important;
}
.input-group-addon {
    background-color: #424040 !important;
    border: 1px solid #dddddd !important;
}


/*edit profil*/
.btn-primary {
    background-color: #424040 !important;
    border-color: #dddddd !important;
}


/*spectate button*/
.spectate {
    background-color: #424040 !important;
    border: 1px solid #ddd !important;
}


/*change server menu*/
.modal-body {
    background-color: #424040 !important;
}
.modal-header {
    border-bottom: 1px solid #dddddd !important;
    background-color: #424040 !important;
}
.modal-footer {
    border-top: 1px solid #dddddd !important;
    background-color: #424040 !important;
}

/*login, signing button*/
.btn-info {
    color: #ffffff !important;
    background-color: #424040 !important;
    border-color: #dddddd !important;
}
.btn-success {
    color: #ffffff !important;
    background-color: #424040 !important;
    border-color: #dddddd !important;
}





}
