css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.zhihu.com/question") {
 
  /*置顶导航*/.is-hidden,
  /*相关问题*/.Question-sideColumn 
  {display: none !important;}
  
  .Question-mainColumn {width:100%;}
  
  .List-item .RichContent img {width:auto !important;max-width: 100%;}
  .RichContent p {line-height: 200%;}
  
  .VagueImage {width:0px !important;}

/*使用了 creampie 的 江泽之力*/

body {

    background:linear-gradient(to bottom, rgba(255,255,255,0.8) 300px, rgba(255,255,255,0) 1200px, rgba(255,255,255,0)), url(http://ww2.sinaimg.cn/large/005Suejwgw1f3v9bmqltxj30dw0dwglr.jpg)!important;
  
}}
