css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.linux.org.ru/) {
body {
	background: #515457 !important;
	color: #fff !important;
	font-size: 12px !important;
	padding: 0 !important;
}
h1 {
	font-size: 16px !important
}
h2 {
	font-size: 13px !important
}
h2 a {
	color: #fff !important
}
body div p {
	color: #fff !important;
	font-size: 12px !important;
}
a {
	color: #6f6 !important;
	text-decoration: none !important
}
img {
	display: none !important
}
body > h1, #bd > h1 {
	display: none !important
}
#head-main {
	margin-left: 0 !important;
}
#head-main > br {
	display: none;
}
#head-main table {
	border: none !important;
}
#head-main td {
	padding: 0 1em !important;
}
div[align="center"] p {
	margin-left: 220px !important
}
.boxlet {
	background: #555 !important;
	border: solid 1px #222 !important;
	font-size: 11px !important; 
	margin: 0 0 4px 0 !important;
	text-align: left !important; 
	padding: 4px !important;
	width: 220px !important;
}
.boxlet h2 {
	display: block !important;
	background-color: #3e403b !important;
	border-color: #222 !important;
	border-width:0 0 1px 0 !important;
	border-style:solid !important;
	color: #fff !important;
	font-size: 14px !important;
	margin: -4px 0 0 -4px !important;
	padding: 0 4px !important;
	width: 100% !important;
}
.boxlet h3 {
	font-size: 12px !important;
	margin-top: 2px !important;
	margin-bottom: 3px !important
}
.boxlet form {margin: 0 !important}
.boxlet form img {display:inline !important}
.boxlet form p {margin: 0 !important}
.boxlet a img {
	display: block !important;
	border: none !important
}
.boxlet a {color: #3c3 !important}
.boxlet i, .boxlet p i {
	color: #ccc !important;
	font-style: normal !important;
}
.boxlet br {line-height: .1em !important}
.boxlet iframe {width: 220px !important}

.newsblog-in {margin-left: 238px !important}
.newsblog h1 {display: none !important}
.newsblog .nav {
	border: none !important;
}
.news {
	background-color: #545751 !important;
	border:1px solid #222 !important;
	margin: 0 0 5px 0 !important;
	padding: 7px !important;
}
.news h2 {
	display: block !important;
	background-color: #3e403b !important;
	text-align: left !important;
	border-bottom: 1px solid #222 !important;
	padding: 2px 5px !important;
	margin: -7px -7px 0 -7px !important;
}
.entry-body {
	margin: 0 0 0 2em !important;
	padding: 5px 0 0 0 !important;
}
.entry-body .msg {
	border: none !important;
}
.entry-body .nav {
	display: block !important;
	background-color: #454 !important;
	border-color: #222 !important;
	border-width:1px 0 0 1px !important;
	border-style:solid !important;
	margin: 0 -7px -7px 30% !important;
	padding: 2px !important;
	font-size: 11px !important;
}
.sign {
	font-size: 11px !important;
	color: #ccc !important
}
.sign a {
	color: #3c3 !important
}
.head-main table {
	font-size: 11px !important;
	border: none !important
}
.head-main table td {
	padding: 0 1em !important;
	border: none !important
}
.nav {
	background-color: #3e403b !important
}
.messages {
	background-color: #515457 !important;
	color: #fff !important;
	font-size: 12px !important;
	margin: 5px 0 !important;
}
.title {
	background-color: #3E403B !important;
	color: #aaa !important;
	font-size: 10px !important;
	padding: 0 5px !important;
}
.title a {
	color: #aaa !important
}
.title strong {
	background-color: #611 !important;
	color: #fcc !important;
}
.userpic {
	padding: 2px !important
}
.userpic img, .msg img, .message-table img {
	border: none;
	display: inline !important
}
.msg {
	background-color: #545751 !important;
	border: solid 1px #222 !important;
	color: #fff !important;
	font-size: 12px !important;
	margin: .4em 0 !important;
	padding: 0 !important;
}
.msg h2 {
	background-color: #3E403B !important;
	margin-top: 0 !important;
	padding: 3px !important
}
.msg_body {
	background-color: #545751 !important;
	color: #fff !important;
}
.msg_body h2 {
	margin: 0 -0.5em 4px -0.5em !important;
}
.msg p a {
	color: #3c3 !important
}
.msg:target {
	border:1px solid #f96 !important;
}
div.forum table thead, div.forum table tfoot tr, div.forum table th {
	background-color: #3e403b !important
}
div.forum table tbody tr {
	background-color: #545751 !important
}
div.forum table td {
	padding: .1em .2em !important
}
div.forum table tbody tr:hover {
	background-color: #3e403b !important
}
.infoblock {
	margin: 1em 0 !important;
	background: #4d4f49 !important;
}

#dw {display:none !important}
}

@-moz-document url-prefix(http://www.linux.org.ru/gallery/) {
	body > img {display: block !important}
}


/* wiki */

@-moz-document url-prefix(http://www.linux.org.ru/wiki/) {
	#wiki-page {
		color: #fff !important
	}
    .portlet, #contents {
		background: #545751 !important
	}
	#nav-search input {
		background-color: #4c4f4a !important;
		color: #fff !important
	}
	#category-index {
		background-color: transparent !important
	}
	ul.tab-menu li {
		background: none !important
	}
	h1, h2, h3, h4, h5 {
		color: #ff6 !important
	}
	#toc {
		background: #4c4f4a !important
	}
	pre {
		background: #4c4f4a !important;
		color: #ccc !important
	}
	li.standardchange {
		color: #fff !important
	}
	li.deletechange {
		color: #f99 !important
	}
	li.minorchange, .formhelp {
		color: #999 !important
	}
	a.edit:hover {
		border-bottom: 1px dotted #f99 !important
	}
	.diff-unchanged {
		background: transparent !important;
		color: #999
	}
	.diff-delete, .diff-add {
		background: #40423e !important;
		border-top: solid 3px #aa7348;
		font-weight: normal !important
	}
	.diff-add {
		border-top: solid 3px #4baa48
	}
	.diff-change {
		color: #ff6b48 !important
	}
}
