css:
@-moz-document url-prefix("http://localhost:8001"), domain("crystal-lang.org") {
/* By tzekid with <3 */

/* Main website */
.code_section .highlight code, .code_section .highlight pre { background-color: #262626; color: #999; }
.highlight .n{ color: #999; }
.highlight .o{ color: #666; }
.highlight span.no{ color: #111; }
.highlight .p{ color: #777; }
.highlight .vi{ color: #8e9222; }
.highlight span.no{ color: #4f4f4f; }
.main #benchmark svg .label{ fill: white; }
.main #benchmark svg .axis-name{ fill: white; }
.main #benchmark svg .axis text{ fill:white; }
.main .flag{ background-color: #333; }
.main .flag h3 { background-color: #333; }
}

@-moz-document url-prefix("http://localhost:8001"), domain("crystal-lang.org") {

/* API Section */
body{ background: #262626; color: #aaa; border-color: #575757;}
h1{ color: #eee; }
h1.type-name{ background-color: #262626; color: #574273; border-color: #575757;}
h2{ color: #ddd; border-color: #575757;}
h3{ color: #eee; border-color: #575757;}
pre{ background-color: #262626; border-color: #575757;}
code{ color: #ccc; border-color: #575757;}
a{ color: #4979D1; border-color: #575757;}
a:visited{ color: #3B62A8; border-color: #575757;}
#types-list{ background-color: #1D1626; border-color: #575757;}
.entry-detail .signature .method-permalink{ color: #eee; border-color: #575757;}
.methods-inherited a { color: #745899; border-color: #575757;}
.entry-detail .signature{ background-color: #262626; color: #9569D1; border-color: #575757; }
.superclass-hierarchy .superclass a, .other-type a, .entry-summary .signature{
  background-color: #262626;
  color: #9569D1;
  border-color: #575757;
}
input{ background-color: #383838; color: #ccc; }
p a img[alt="born-and-raised"]{ 
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://i.imgur.com/tOjYmlJ.png) no-repeat;
  width: 0px;
  height: 160px;
  padding-left: 600px;
  margin-top: 36px;
}
.s{ color: #2A5FFF; }

}

@-moz-document url-prefix("http://localhost:8000"), url-prefix("http://localhost:8080") {

/* Crystal Playground */

.container{ width: 100vw; }
#mainEditorContainer{ width: 100vw; }
@media only screen and (min-width: 993px){ .container{ width: 100vw; margin: 0px; } }
@media only screen and (min-width: 601px){ .container{ width: 100vw; margin: 0px; } }
.row .col.s7{ width: 60vw; padding: 0px; }
.row .col.s5{ width: 40vw; padding: 0px; }
.crystal-version{ margin: 30vw; }
}
