css:
@-moz-document url-prefix("https://www.pokemonlegends.com/explore") {
body {
    color: #CCC;
}

/* Background */
body,
#mws-container {
    background-image: url(data:image/png;base64,) !important;
}

#tabs {
	color: white;
}

.mws-panel .mws-panel-body {
	border: none;
	margin: 0;
}

/* Pokemon Legends Extension */
#location_data_grid {
	float: left;
	position: absolute;
	top: 55%;
	margin: 0 0 0 3px;
	width: 18.5%;
}
#location_data_grid > div.mws-panel-body {
	background-color: #35353A;
	display: block !important;
}
#location_data_grid > div.mws-panel-body i {
	color: white;
	font-weight: bold;
}

/* Side bar stitch */
div#mws-sidebar-stitch {
    background-image: url(data:image/png;base64,);
    background-color: #35353A;
}

/* Game container */
#mws-container > div > div.mws-panel.grid_5 {
    max-width: 1024px;
    margin-left: 19%;
}

/* Chat box */
#mws-container > div > div.mws-panel.grid_3.mws-collapsible {
    width: 22.5%;
    margin-left: 0;
    margin-right: 0;
}
@media screen and (max-width: 1902px) {
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        width: 22%;
    }
}
@media screen and (max-width: 1886px) {
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        width: 21.5%;
    }
}
@media screen and (max-width: 1871px) {
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        width: 21%;
    }
}
@media screen and (max-width: 1856px) {
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        width: 20%;
    }
}
@media screen and (max-width: 1827px) {
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        width: 19%;
    }
}
@media screen and (max-width: 860px) {
	body {
		overflow: hidden;
	}
	#divPm,
	#divTip,
	#mws-header,
	#mws-container > div > div.mws-panel.grid_5 > div.mws-panel-header,
	#mws-sidebar-stitch,
	#mws-sidebar-bg,
	#mws-sidebar,
    #mws-container > div > div.mws-panel.grid_3.mws-collapsible {
        display: none;
    }

	/* Game Panel */
	#cvsGame {
		width: 100% !important;
	}
	#mws-container > div > div.mws-panel.grid_5 {
		margin-left: 1%;
		margin-right: 1%;
		width: 98%;
	}
	#mws-container > div > div.mws-panel.grid_5 > div.mws-panel-body {
		padding-top: 49px;
		background: transparent;
	}
	#mws-container > div {
		height: 100% !important;
		padding-top: 0;
	}
	
	/* Encounter panel */
	#mws-container > div > div.mws-panel.grid_5 > div:nth-child(9) {
		position: fixed;
		padding-top: 130px;
	}
    div#mws-container {
		padding-top: 100px !important;
		margin-left: 0px;
	}
	#mws-container > div > div.mws-panel.grid_5 > div.mws-panel-body,
	#mws-container > div > div.mws-panel.grid_5 > div.mws-panel-body {
		border: none;
		background-color: transparent;
	}
	
	/* Party */
	#party {
		position: fixed !important;
		top: 15px;
		width: 100% !important;
	}
	#party > div.pokemon {
		width: 16.66%;
	}
	#party-info > div.info-slot {
		position: fixed;
	}
	.poke-edit {
		width: 16.66% !important;
	}
	
	/* Edit Skills */
	#pokeSkills {
		position: fixed !important;
		width: 60% !important;
		top: 0 !important;
		left: 20%;
	}
	#pokeSkills > .mws-panel-body > form {
		width: 98%;
		margin: 0 auto;
	}
	#pokeSkills .chosen-container {
		width: 100% !important;
	}
	
	/* PokeLegends Extension */
	#location_data_grid {
		position: fixed;
		top: 0;
		right: 0;
		width: 50%;
	}
	#location_data_grid > div.mws-panel-header {
		background-color: transparent;
		height: 0px;
		width: 0px;
		padding-bottom: 5px;
	}
	#location_data_grid > div > span.mws-i-24 {
		position: fixed;
		top: 0;
		right: -15px;
		width: 0;
		height: 15px;
		background-size: 15px 15px;
	}
	#location_data_grid > div.mws-panel-body {
		display: none  !important;
	}
}
@media screen and (max-width: 500px) {
	#pokeSkills {
		left: 0 !Important;
		width: 100% !important;
	}
	#pokeSkills > .mws-panel-body > form {
		width: 100%;
	}
	#pokeSkills .chosen-container {
		width: 100% !important;
	}
	#location_data_grid {
		width: 100%;
	}
}

#chat {
    max-height: 700px;
}

/* PMs */
#divPm {
    position: absolute;
    width: 18.5% !important;
    margin-top: 280px;
	z-index: 2;
}
#divPm > div.mws-panel.grid_2 {
    width: 100%;
}

/* Pokemon Party */
#party {
    float: left;
	top: 10px;
    width: 18.5%;
    position: absolute;
}
.pokemon {
    display: inline-block;
    width: 33.33%;
    min-height: 100px;
}
#hp-bar,
#exp-bar,
.poke-centered,
.pokemon-name,
.pokemon > div > a,
#poke-lvl,
.progress-text {
    display: table;
    margin: 0 auto;
}
#hp-bar,
#exp-bar {
	height: 12px;
}
.progress-text {
	width: 50%;
	margin: 0 auto;
	text-align: center;
	vertical-align: middle;
	font-size: 11px;
	color: white;
	font-weight: bold;
}
#hp-bar .progress-text {
	text-shadow: -1px 0 #025d06, 0 1px #025d06, 1px 0 #025d06, 0 -1px #025d06
}
#exp-bar .progress-text {
	text-shadow: -1px 0 #6f6401, 0 1px #6f6401, 1px 0 #6f6401, 0 -1px #6f6401
}
.pokemon > div > a {
    height: 62px;
}
.pokemon > div > a > img {
    max-height: 62px;
}
.pokemon-name {
    padding-bottom: 1px;
}
#hp-bar,
#exp-bar {
    width: 85%;
}
#hp-bar {
    margin-bottom: 2px;
}
#hp-bar > div {
    background-color: #20B938;
}
.empty-slot {
    height: 100px;
    width: 100%;
}
.poke-edit {
	position: absolute;
	width: 33.33%;
}
.poke-edit > a {
	float: right;
	height: 15px;
	width: 15px;
	background-size: 100%;
}

/* Pokemon Info Tooltip */
.hidden {
    display: none;
}
.info-slot {
    z-index: 1;
    position: absolute;
    background-color: #222;
}
.info-slot > table {
    width: 100%;
}
.info-slot > table th {
    padding: 5px;
}
.info-slot > table td,
.info-slot > table th {
    text-align: center;
}

/* Skills form */
#pokeSkills {
	position: absolute;
	width: 18.5%;
	top: 10px;
}
#pokeSkills > .mws-panel-body {
	background-color: #35353A;
	padding: 0px 2px 1px 2px;
	border-color: white;
}
#pokeSkills > .mws-panel-body > form > button,
#pokeSkills > .mws-panel-body > form > input {
	float: right;
	text-align: center;
}
#pokeSkills > .mws-panel-body > form > button {
	height: 32px;
}
#pokeSkills > .mws-panel-body > form > input {
	height: 18px;
}
#pokeSkills > div > select {
	display: none;
}
#pokeSkills .chosen-results > .active-result {
	color: black;
}
#pokeSkills .chosen-results > .highlighted {
	color: white;
}
#pokeSkills .chosen-container {
	max-width: 100%;
}
}
