@-moz-document domain("share.dmhy.org") {
/*
	背景
*/
#mini_jmd,
table.tablesorter,
.table,
.top_sort,
.quick_search,
.top_sort > .menu > ul > li > ul,
#recent-commnet:before,
.topic-nfo.box.ui-corner-all:before,
.topic-main > .box:nth-child(1):before {
	background-image: var(--blur-bg);
}
body,
.container > .bg > .header {
	background-image: var(--bg);
}
#mini_jmd,
table.tablesorter,
.table,
.top_sort,
.quick_search,
.top_sort > .menu > ul > li > ul,
body,
.container > .bg > .header,
#recent-commnet:before,
.topic-nfo.box.ui-corner-all:before,
.topic-title.box.ui-corner-all:before,
.topic-main > .box:nth-child(1):before {
	background-size: cover;
	background-attachment: fixed;
	background-position: center;
}
#recent-commnet,
.topic-nfo.box.ui-corner-all,
.topic-main > .box:nth-child(1) {
	position: relative;
}
#recent-commnet:before,
.topic-nfo.box.ui-corner-all:before,
.topic-title.box.ui-corner-all:before,
.topic-main > .box:nth-child(1):before {
	width: 100%;
	content: '';
	position: absolute;
	left: 0;
	right: 0;
	top: 0;
	display: block;
	z-index: -1;
	height: 100%;
}


