@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nrc.nl") {
    
    header[class^="header"] {

    background-image: none;
    
    background-color: #000;
    
    background: #000;

    color: #AAA;        
        
    }

    A[data-category^="sections-bar"] {
    
    background-color: #000;

    color: #FFF;
    
    }

    body[data-page-type^="frontpage"],body[class^="artikel"],body[class^="liveblog"],body[class^="error-page"] {
    
    background-image: none;
    
    background-color: #000;
    
    background: #000;

    color: #AAA;

    }
  
    div[class^="nmt"],div[class^="user-confirmation"] {

    background-color: #000;

    color: #AAA;

    }
  
    main {

    background-color: #000;

    color: #AAA;

    }
  
    div[class^="related"] {

    background-color: #000;

    color: #AAA;

    }

    aside[class^="inline"] {

    background-color: #000;

    color: #AAA;

    }
  
    p {

    background-color: #000;

    color: #AAA;

    }
  
    p[class^="cookie-bar"] {

    background-color: #FFF;

    color: #000;

    }

    p[class^="user-confirmation"]{
    
    background-color: #000;

    color: #AAA;
    
    }
  
    div[class^="localfocus"] {

    background-color: #FFF;

    }  
  
    A[class^="nmt"] {
    
    background-color: #000;

    color: #AAA;
    
    }
  
    h1[class^="gn4"],h2[class^="gn4"],h3[class^="gn4"],h4[class^="gn4"],h5[class^="gn4"],h6[class^="gn4"] {
    
    background-color: #000;

    color: #AAA;
    
    }  
  
    h1[class^="nmt"],h2[class^="nmt"],h3[class^="nmt"],h4[class^="nmt"],h5[class^="nmt"],h6[class^="nmt"] {
    
    background-color: #000;

    color: #AAA;
    
    }
  
    h1[class^="block"],h2[class^="block"],h3[class^="block"],h4[class^="block"],h5[class^="block"],h6[class^="block"] {
    
    background-color: #000;

    color: #AAA;
    
    }
  
    h1[class^="timed-articles"],h2[class^="timed-articles"],h3[class^="timed-articles"],h4[class^="timed-articles"],h5[class^="timed-articles"],h6[class^="timed-articles"] {
    
    background-color: #000;

    color: #AAA;
    
    }  

    h1[class^="photo-articles"],h2[class^="photo-articles"],h3[class^="photo-articles"],h4[class^="photo-articles"],h5[class^="photo-articles"],h6[class^="photo-articles"] {
    
    background-color: #000;

    color: #AAA;
    
    }  
  
    h1[class^="tabbed"],h2[class^="tabbed"],h3[class^="tabbed"],h4[class^="tabbed"],h5[class^="tabbed"],h6[class^="tabbed"] {
    
    background-color: #000;

    color: #AAA;
    
    } 
  
    h1[class^="section"],h2[class^="section"],h3[class^="section"],h4[class^="section"],h5[class^="section"],h6[class^="section"] {
    
    background-color: #000;

    color: #AAA;
    
    }  

    div[class^="content"] {
    background-color: #000;

    color: #AAA;
    }

    div[class^="selections"] {
    background-color: #666;

    color: #AAA;
    }
  
    div[class^="header"] {
    background-color: #666;

    color: #AAA;
    }

    div[class^=" header"] {
    background-color: #FFF;

    color: #AAA;
    }
  
    div[class^="section"] {
    background-color: #000;

    color: #AAA;
    }  
  
    A[class^="header"] {
    
    background-color: #666;

    color: #AAA;
    
    }
  
    A[class^="inner"] {
    
    background-color: #000;

    color: #AAA;
    
    }

    A[class^="block"] {
    
    background-color: #000;

    color: #AAA;
    
    }
  
    div[class^="footer"] {
    
    background-color: #666;

    color: #AAA;
    
    }
  
    div[class^="flexfloat-wrapper"] {
    
    background-color: #000;

    color: #AAA;
    
    }
  
    footer[class^="foot"] {
    
    background-color: #666;

    color: #AAA;
    
    }
}
