css:
@-moz-document domain("peka2.tv") 
 {
  .header-wrapper--has-feature {
      height: 30%;
  }
  .content {
      background-color: black;
  }
  .news-carousel-item__title {
      color: #cccccc;
  }
  .index-stream-divider a {
      color: #cccccc;
  }
  .stream-card__title {
      color: #cccccc;
  }
  .stream-card__streamer {
      color: #7f7f7f;
  }
  .article-card__title {
      color: #cccccc;
  }
  .blog-card-author__nickname {
      color: #cccccc;
  }
  .blog-card__title {
      color: #cccccc;
  }
  .recent-blogs-item__title {
      color: #ccc;
  }
  .forum-thread__title {
      color: #ccc;
  }
  .recent-blogs-item__author {
      color: #4c4c4c;
  }
  .forum-thread__info {
      color: #4c4c4c;
  }
  .blog-card-info, .blog-card__read-more {
      color: #4c4c4c;
  }
  .article-card__read-more {
      color: #4c4c4c;
  }
  .news-carousel-item__info {
      color: #4c4c4c;
  }
  .blog-card-author__reader-count {
      color: #4c4c4c;
  }
  .publication__title {
      color: #dedede;
  }
  .publication__summary div {
      color: #cccccc;
  }
  .ql-editor .ql-align-justify {
      color: #cccccc;
  }
  .publication__content h2, .publication__content ol, .publication__content p, .publication__content ul {
      color: #cccccc;
  }
  .publication-donate__subheader {
      color: #717171;
  }
  .comments__header {
      color: #b7b7b7;
  }
  .post-comment__input {
      background-color: black;
      color: #cecece;
  }
  .label {
      color: #9e9e9e;
  }
  .comment-message {
      color: #cccccc;
  }
  .comment-header__name {
      color: #ccc;
  }
  .comment-header__date {
      color: #4c4c4c;
  }
  .publication-author__nickname {
      color: #ccc;
  }
  .blogs-author__nickname {
      color: #e0e0e0;
  }
  .hafp-more, .hafp-streams-item__streamer {
      color: #929292;
  }
  .hafp-blogs {
      background-color: #424242;
  }
  .hafp-empty span {
      color: #ececec;
  }
  .ql-editor {
      color: #929292;
  }
  .modal-content__header {
      color: #ccc;
  }
  .donate-modal {
      background-color: #2d2d2d;
  }
  .donate-modal-message {
      color: #d2d2d2;
  }
  .input {
      background-color: black;
      color: #d2d2d2;
  }
  .news-archive__header {
      color: #ccc;
  }
  .news-archive-item__title {
      color: #ccc;
  }
  .shop {
      background-color: #212121;
      color: #fdfdfd;
  }
  .dark-page {
      background-color: #000;
  }
  .my-wallet {
      color: #fff;
  }
  .modal {
      background-color: #2d2d2d;
  }
  .body--has-ad {
    padding-top: 0;
  }
  .body--has-ad:before {
    display: none;
  }
 }
