css:
@-moz-document domain(buddyfightsim.myseeyou.com) {
	/* hide Bushiroad copyright disclaimer */
	#copy {
		display: none;
	}
	/* hide "browser compatibility" message */
	div#body > div:not([class]):not([id]) {
		display: none;
	}
	/* disable unnecessary spacing */
	#menu1 {
		border-spacing: 0px;
	}
	/* disable all wikia links in card info UI to avoid misclick in game */
	.card-info a[href*="wikia"] {
		pointer-events: none;
		cursor: default;
	}
}
