css:
@-moz-document domain("slatestarcodex.com"), domain("unsongbook.com") {
.hentry, #respond, #respond textarea, #respond input, #comments {
	background: rgb(9,9,9);
    border: 1px solid #222;
    color:#aaa;
  }

  .new-comment, .commentholder {
	background: rgb(12,12,12);
    border: 1px solid #222;
    color:#bbb;
  }

#pjgm-description, #pjgm-ender a, .pjgm-postmeta, .pjgm-postutility,
  #pjgm-menubar .menu-header, div.menu, a.pjgm-home, a.pjgm-feed {
    font-family: "Futura LT", Tahoma, Verdana, sans-serif;
  }

  body, input, textarea, .pjgm-pagetitle span, .pingback a.url, blockquote,
  h3#comments-title, h3#reply-title, #cancel-comment-reply-link, .form-allowed-tags, #pjgm-title, #wp-calendar, .comment-meta, .comment-body tr th, .comment-body thead th, .pjgm-postcontent label, .pjgm-postcontent tr th, .pjgm-postcontent thead th, .pjgm-posttitle, #respond label, .pjgm-navigation, .pjgm-pagetitle, .pingback p, .reply, .widget-title, .wp-caption-text, input[type="submit"], #pjgm-content h1, #pjgm-content h2, #pjgm-content h3, #pjgm-content h4, #pjgm-content h5, #pjgm-content h6 {
    font-family: "Source Serif Pro", Georgia, "Bitstream Charter", serif;
    color:#aaa;
  }
  
  #pjgm-title a {
    font-family: Amiri, 'Times New Roman', serif;
    letter-spacing: 0.15em;
    font-variant: small-caps;
    text-shadow: 1px 1px 1px #aae;
  }
  
  html {
    background: #111;
  }
  
  #pjgm-wrap {
    width: auto;
    max-width: none;
    margin: 0;
  }
  
  #primary, #left-sidebar, #primary > .xoxo, #left-sidebar > .xoxo, #pjgm-box {
    background: none;
    border: none;
  }
  
  #pjgm-box {
    padding-top: 1.5em;
  }
  
  #pjgm-header {
    background: linear-gradient(to bottom, #26a 0%, #124 80px, #112 120px, #111 162px);
    margin: 0 0 -56px 0;
    padding: 0 8px 56px 8px;
    box-shadow: none;
    border-radius: 0;
  }
  
  #pjgm-main {
    background: none;
    box-shadow: none;
    margin: auto;
  }
  
  #pjgm-main .widget-area ul,
  .pjgm-navigation a:link,
  .pjgm-navigation a:visited {
    text-shadow: 1px 1px 1px #222;
  }
  
  .widget-title, .widget_links .widget-title {
    color:#ddd;
  }

  .widget_search #s {
	background: rgb(9,9,9);
    border: 1px solid #222;
    color:#aaa;
  }
  
  .textwidget {
    color: #999;
  }
  
  #pjgm-ender {
    background: none;
  }
  
  body, #pjgm-menubar {
    margin: 0;
    padding: 0;
  }
  
  #pjgm-description, #pjgm-ender a {
    color: white;
  }
  
  #pjgm-menubar ul > li, a.pjgm-feed, .pjgm-ender {
    border: none;
  }

  #pjgm-menubar a:hover {
    background: rgba(255, 255, 255, 0.2) none repeat scroll 0% 0%;
  }

  .pjgm-postmeta {
    border: 0px;
	background: rgba(0, 0, 0, 0);
    text-shadow: 1px 1px 1px #333;
  }

  a:link, a:visited, .pjgm-postmeta a, .pjgm-postutility a {
    color: #6bf;
  }
  
  #pjgm-content h1.pjgm-pagetitle {
    color: #888;
    font-size: 18px;
  }
  
  #pjgm-content h1.pjgm-pagetitle > span {
    color: #aaa;
    font-family: inherit;
    font-size: inherit;
  }
  
  #pjgm-content .pjgm-posttitle, #pjgm-content .pjgm-posttitle a {
    font-size: 20px;
    color: #6bf;
    text-shadow: 1px 1px 1px #444;
  }
  
  #pjgm-content .pjgm-posttitle a:hover {
    font-size: 20px;
    color: #f63;
    text-shadow: 1px 1px 1px #555;
  }

  .comment-author cite {
	color:#ddd;
  }

.comments-floater {
    background: rgba(0,0,0,0.8);
}

.date-input {
    background: rgb(9, 9, 9);
    border: 1px  solid #444;
}

input[type=submit] {
    background: linear-gradient(to bottom, #333 0%, #222 100%);
    border: 1px solid #111;
	color: #aaa;
}

.s2-expand #left-sidebar {
    background: #111;
}

.s2-expand #left-sidebar > .xoxo {
    background: #111;
}

.widget-container {
	border-top: 0px;
}

a:hover {
	color:#f63;
}
}

@-moz-document domain("unsongbook.com") {
#pjgm-header {
    background: linear-gradient(to bottom, #c82 0%, #841 80px, #421 120px, #111 162px);
    margin: 0 0 -56px 0;
    padding: 0 8px 56px 8px;
    box-shadow: none;
    border-radius: 0;
  }
  
  #pjgm-content .pjgm-posttitle {
    font-size: 20px;
    color: #f63;
    text-shadow: 1px 1px 1px #432;
  }

  .reply a, a:link, a:visited, .pjgm-postmeta a, .pjgm-postutility a {
    color: #f63;
  }

  .reply a:hover, a:hover, .pjgm-postmeta a:hover, .pjgm-postutility a:hover {
    color: #ddf;
  }
}
