css:
@-moz-document domain(duolingo.com) {.home, #app, html{
    background-color: #333!important;
}
.topbar *, .coach-message{
    color: #5a5a5a!important;
}
a>strong{
    color: #333!important;
}
.fluency-score-shield-silver, .fluency-score, .fluency-score-percent{
	color: #3f3f3f!important;
}
*, .dropdown-menu *{
    color: #EEC!important;
}
.page-main, .box-colored, .box-gray{
    background-color: #1e1e1e!important;
    color: #eee!important;
   	border: 2px solid #333!important;
}
.btn-standard, .btn-white, .sticky-discussion-message, .vote-count, .icon-comment-medium, input[name=search], .shortcut, .shortcut + td{
    background-color: #1e1e1e!important;
    border-color: #2f2f2f!important;
    color: #eee!important;
}
.btn{
    color: #555!important;
}
.player-container{
	background-color: #1e1e1e!important;
}
.player-header{
	border-bottom: 2px solid #333!important;
}
.player-footer, .session-end-footer{
 	background-color: #272727!important;
}
.carousel-dot:not(.active){
    border-color: #666!important;
    background-color: #666!important;
}
.carousel-dot.active{
    border-color: #666!important;
}
.progress-xp>.col-left{
    border-right: 2px solid #555!important;
}
.progress-bar-distinct>li{
  	border-left: 2px solid #222!important;
    background-color: #333!important;
}
.progress-bar-distinct>li:first-child {
    border-left: none!important;
}
.speech-bubble.arrow-left>.inner, .arrow, .inner::after{
    background-color: #333!important;
}
.hint-table.hint-popover.below *{
    background-color: #333!important;
}
.hint-table>.inner>.content>table>tbody>tr>td {
    border-top: 1px solid #555;
}
.hint-table>.inner>.content>table>tbody>tr:first-child>td{
    border-top: none;
}
.list-lesson-item.locked>.inner{
    background-color: #272727!important;
}
.list-lesson-item.past>.inner, .lingot-bill{
    background-color: #333!important;
}
.player.speech-underlined{
    background-color: #222!important;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-bottom: 2px solid #EEC;
}
.dropdown-menu.arrow-top::after{
    border-bottom: 10px solid #555!important
}
li>label, li>label>table>tbody>tr>td.checkbox>input, input{
	background-color: #373737!important;
    border-color: #EEC!important;
}
li>label:hover, li>label>table>tbody>tr>td.checkbox>input:hover{
   	border: 2px solid #1caff6!important;
}
.text-to-translate, .textarea-translate, .input-listen, .input-block, .input-lg{
	background-color: #373737!important;
}
.input-listen, .input-block, .input-lg{
    border: 2px solid #444!important;
}
.textarea-translate{
	border: 2px solid #444;
}
.player-footer h2 {
    background: #333;
}
.progress-bar-dynamic.left.strength-bar{
    background-color: #373737!important;
    border: 2px solid #444!important;
}
.achallenge-num-backing{
    border: 2px solid #333!important;
}
.bar.blue::after{
	display: none!important;
}
.challenge-num-circle.white{
    background-color: #444!important;
}
.badge-correct-big, .badge-wrong-big{
    background-color: #1e1e1e!important;
}
#record-button{
    border: 0px solid #444;
}
.speaker-slow{
    border-color: #555!important;
}
select>option{
    background-color: #333!important;
}
.challenge-num-backing, .backing{
    display: none!important;
}
.icon-comment-medium{
	-webkit-filter: gray;
    filter: gray;
}
.btn-yellow{
    color: #3f3f3f!important;
}
.strengthen-skills-container{
   	border-top: 2px solid #333!important;
}
.hover-areas{
    background-color: #444!important;
}
circle[fill="#e2e2e2"]{
    fill: #2e2e2e!important;
}
circle[fill="#ffffff"]{
    fill: #333!important;
}
path[fill="url(#4060-_fcc34b:20-_fe922e)"]{
    stroke:"#000"!important;
}
#weekly-progress>svg>circle{
    fill: #333!important;
}
#weekly-progress>svg>path[stroke="#eeeecc"]{
    stroke: #666!important;
}
#daily-donut>svg>circle{
    stroke: #555!important;
}
#daily-donut>svg>circle:nth-of-type(1){
    fill: #555!important;
}
#daily-donut>svg>circle:nth-of-type(3){
    fill: #1e1e1e!important;
}
.skill-icon.locked{
    background-color: #555!important;
}
.language-choice.active>a{
    background-color: #666!important;
}
.language-choice.active>a:hover{
    background-color: #1caff6!important;
}
#settings_tab>.active{
    background-color: #666!important;
}
#settings_tab>li:hover{
    background-color: #1caff6!important;
}
.keyboard-shortcuts *:not(.key){
    background-color: #333!important;
}
.dropdown-menu{
    background-color: #555!important;
}
.course-card.course-profile-badge {
    background-color: #333!important;
}
.nav-sidebar>li.divider {
    border-top: 2px solid #333;
}
.icon:not(.icon-comment-medium):not(.flag):not(.icon-section-troubleshooting),  .topbar-brand.navigate-home.track-click.white{
	background-image:url(http://imgh.us/icon-sprite_duolingo_black_3.svg);
}
.icon.icon-comment-medium::after{
	background-image:url(http://imgh.us/icon-sprite_duolingo_black_3.svg)!important;
}
.list-discussion-item{
    border-top: 2px solid #333!important;
}
.nav-sidebar.discussion-topic-list > li:not(.divider):hover{
    background-color: #0a8dd4;
}
.nav-tabs{
    border-bottom: 2px solid #333!important;
}
.owl{
	background-image:url(http://imgh.us/owl-sprite20.svg);
}
.icon.icon-notification-small{
	opacity: .75!important; 
}
}
