css:
/* i really want this to be global */
.cardmenu, .planfixcardmenu {background: #ffffff;}
.cardmenu tr .link a, .planfixcardmenu tr .link a, .cardmenu tr .link .fakelink-dashed, .planfixcardmenu tr .link .fakelink-dashed {color: #2f3031;}
.cardmenu tr .link .menuitem-bigfolder svg, .planfixcardmenu tr .link .menuitem-bigfolder svg {fill: #c5c5c5;}
.cardmenu tr .link .b-inside-num, .planfixcardmenu tr .link .b-inside-num, .cardmenu tr .link .b-inside-num-new, .planfixcardmenu tr .link .b-inside-num-new, .cardmenu tr .link .b-inside-num .fakelink-dashed, .planfixcardmenu tr .link .b-inside-num .fakelink-dashed {color: #f90404;}
.cardmenu tr:hover:not(.active) .link, .planfixcardmenu tr:hover:not(.active) .link {background: #d3d4d8;}
.cardmenu .left-panel-hide svg, .planfixcardmenu .left-panel-hide svg {fill: #403e3e;}
.leftmenu-topblock{background: #e4e4e4;border-bottom: 1px solid #b1b1b1;}
