css:
@-moz-document domain("hitbox.tv") {
:root {
    --new-width: 300px;
}
#chat {
    width: var(--new-width);
}
#detailpage #content #leftcolumn {
    padding-right: var(--new-width);
}
.chat-input {
    margin: 0 0px 0px;
}
.nav-links__item:nth-of-type(3),
.nav-links__item:nth-of-type(4),
.ng-scope.button-share,
.detail-navigation > htbx-button-show-more > .ng-scope.htbx-button-show-more,
.main-footer__main__section--apps.main-footer__main__section,
.main-footer__main__section--support.main-footer__main__section,
.main-footer__main__section--platform.main-footer__main__section,
.main-footer__main__section--social.main-footer__main__section,
.ng-scope.main-footer__masthead,
.home-hashtags,
.home-popular,
.drag-handle,
.ng-isolate-scope.home-carousel-container,
.tse-scrollbar {
    display: none !important;
}
}
