css:
@-moz-document domain("vk.com") {
/* remove frames */
* {box-shadow: none !important}

/* remove bg */
[dir] body,
[dir] body.new_header_design .TopSearch__input,
[dir] body.new_header_design #page_header_cont,
[dir] div[contenteditable="true"],
[dir] .deep_active .replies .reply_fakebox,
[dir] .deep_active .replies .reply_box div.submit_post_field, 
[dir] .deep_active .replies .reply_box div.submit_post_field[contenteditable="true"],
[dir] .wall_module .reply_box,
[dir] .wall_module .reply_fakebox_wrap,
[dir] .wall_module .reply_form,
_ {background: transparent}

/* remove borders */
[dir] body.new_header_design #page_header_cont,
[dir] .deep_active .post_replies_header,
[dir] .deep_active .replies .replies_wrap_deep,
[dir] .deep_active .replies .replies_list:empty,
[dir] .deep_active .replies .reply .reply_wrap,
[dir] .like_cont,
[dir] .wall_module .replies_list,
[dir] .wall_module .reply,
_ {border: none}

/* text */
.wall_post_text,
.wall_reply_text,
_ {
	font-family: Georgia;
	font-size: 1.2rem;
	line-height: 1.4;
}

/* comments */
.wall_reply_text {font-size: 1.0rem}

/* header */
[dir="ltr"] #page_header_wrap {background: #FFFC}

/* space between posts */
.feed_row {padding-top: 1rem}

/* after post */
[dir] .like_cont {padding: 0}

/* reply comment */
[dir="ltr"] .post_field_user_link {visibility: hidden}

/* external link preview caption */
[dir] .media_link__label {border: none; padding-left: 0}

/* quote */
[dir="ltr"] .wall_module .copy_quote {border-left-width: 1px;}
}
