css:
@-moz-document domain("temainarod.ru") {
:root {
  --bg: #495055;
  --fg: #ccc;
  --sel: #596066;
  --bright: white;
}

body {
  color: var(--fg) !important;
}

a,
a:link,
.lju a,
.menu li a:hover,
.menu li .sel:hover,
.menu li .cr,
#content-pane .toolbar .sortby,
.toolbar .sel,
.toolbar a {
  color: var(--fg) !important;
}

#splitter,
.menu li .cc {
  background: none !important;
}

#content {
  background-color: var(--bg);
}

.cmt {
  font-family: georgia;
  font-size: 1.3rem;
  line-height: 1.3em;
  overflow: scroll;
  min-width: 1% !important;
}

.cmt:hover {
  background-color: var(--bg) !important;
  color: var(--bright);
}

.toolbar,
#content-pane,
.menu li a,
.author,
#splitter {
  background-color: var(--bg) !important;
  color: var(--fg);
}

.sel,
.menu li .sel {
  background-color: var(--sel) !important;
  color: var(--bright);
}

#splitter {
  border-right: 1px solid var(--sel);
}

.menu li a {
  border-top: none;
  border-bottom: 1px solid var(--sel);
}

#bmbar,
.experiment {
  display: none;
}

.parent,
.parent p,
.parent li {
  background-color: var(--sel);
  outline: 10px solid var(--sel);
}
}
