css:
@-moz-document domain("lenta.ru") {
* {
  border: none !important;
}

._social-network,
.b-topic__socials._footer,
.b-sidebar-menu__socials,
.b-sidebar-menu__auth:before,
.b-socials__container,
.b-tags-list .items .active .decorator,
.b-topic__favorite {
  display: none;
}

body,
.b-footer,
.b-sidebar-menu_mini,
.b-sidebar-menu_origin,
.b-sidebar-menu__auth_active,
.b-sidebar-menu__delimiter,
.b-sidebar-menu__search input,
.b-tabloid,
.b-tags-list,
.b-topic_gallery .photo,
.b-inline-topics-box h2,
.b-yellow-box_popular .b-yellow-box__header,
.b-yellow-box__header,
.line {
  background: none;
}

.b-inline-topics-box,
.b-inline-topics-box h2,
.b-inline-topics-box .topics,
.b-text-box .b-text-box__wrap,
.b-topic-addition .bordered-title,
.b-topic__content p,
.b-topic-layout,
.b-yellow-box__header,
.b-yellow-box__wrap {
  padding: 0;
}

.b-topic_column .b-topic__author,
.b-yellow-box__header {
  margin: 0;
}

.b-footer,
.g-application {
  box-shadow: none;
  width: calc(100% - 200px);
}

.b-sidebar-menu__auth-label b,
.b-sidebar-menu__list-item a {
  color: #8888 !important;
}

.b-tags-list {
  line-height: 1;
}

.b-inline-topics-box h2 {
  line-height: normal;
}

.b-tags-list .items .item,
.b-sidebar-menu__auth {
  height: auto;
}

.b-sidebar-menu__auth-link {
  padding: 0;
  margin: -11px;
}

.b-inline-topics-box .topics .g-picture {
  margin-right: 5px;
}

.b-inline-topics-box .topics .info {
  width: 200px;
}

.b-box_left {
  margin-right: 10px;
}

.b-inline-topics-box_wide {
  width: 308px;
}

.b-topic__content {
  margin-top: -25px;
}

.b-topic__title {
  margin: 0 0 10px 0 !important;
}

.b-text h1 {
  font-size: 2rem;
}

.premial-body > .b-text p,
.b-topic__content p {
  font-family: Georgia;
  font-size: 1.6em;
  line-height: 1.4;
}

.b-box_floated {
  margin-bottom: 0;
}

.b-topic_article .b-text .b-box_left p,
.b-topic_article .b-text .b-box_right p {
  font-size: 1rem;
}

.voice-over {
	position: fixed;
	margin-left: 0;
	top: 0;
}
}
