css:
@-moz-document domain("lichess.org") {
.purple .is2d .cg-board, #top div.color_demo.purple  {
	background-image:url("http://i.imgur.com/HJF9SBC.png")  !important;
	}

piece.pawn.black { background-image: url("http://imgh.us/bp_14.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.knight.black { background-image: url("http://imgh.us/bn_6.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.bishop.black { background-image: url("http://imgh.us/bb_16.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.rook.black { background-image: url("http://imgh.us/br_5.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.queen.black { background-image: url("http://imgh.us/bq_7.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.king.black { background-image: url("http://imgh.us/bk_9.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 

piece.pawn.white { background-image: url("http://imgh.us/wp_7.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.knight.white { background-image: url("http://imgh.us/wn_6.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.bishop.white { background-image: url("http://imgh.us/wb_6.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.rook.white { background-image: url("http://imgh.us/wr_6.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.queen.white { background-image: url("http://imgh.us/wq_7.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;} 
piece.king.white { background-image: url("http://imgh.us/wk_9.svg")!important;background-size: 95%;background-repeat: no-repeat;background-position: center;}

.cg-board square.last-move { background-color: rgba(200,200,0,0.1) !important;}
/* .cg-board square.last-move {border: 2px solid brown !important;} */

.lichess_board {
	
    border: 28px solid #7B411E !important;
}
.underboard {
	margin-top: 50px !important;
}
.ranks coord{
    margin-left: 7px !important;
}
.files coord{
    margin-top: 5px !important;
}

.cg-board-wrap coords {
    text-transform: lowercase;
    font-family: 'Calibri', sans-serif;
    font-size: 14px;
    font-weight: bold;
    color: black;
}

/* http://i.imgur.com/Ij5rRRD.png */
}
