css:
@-moz-document domain("en.wikipedia.org"){
  
  @font-face{
    font-family: 'Proxima';
    font-style: normal;
    font-weight: normal;
    src: url('https://file.io/kN70N2') format("opentype");
  }
   
  a{
    color: #7998b5 !important;
  }
  
  a:visited{
    color: #F67E7D !important;
  }
  
  .external{
    background-image: none !important;
  }
  
  body{
    font-family: 'Proxima', sans-serif;
    font-size: 18px;
    background: #ECECEC !important;
    margin-top: -10%;
    color: #333;
  }
  
  #mw-head{
    opacity: 0;
  }
  
  #mw-head:hover{
    opacity: 1;
    background: #ECECEC;
  }
  
  .mw-wiki-logo{
    background-color: #ECECEC;
    background-image: url('https://en.wikipedia.org/static/images/project-logos/enwiki.png') top !important;
  }
  
  #mw-panel{
    opacity: 0;
  }
  
  #mw-panel:hover{
    opacity: 1;
    background: #ECECEC;
  }
  
  .mw-body{
    margin-left: 5%;
    margin-right: 5%;
    margin-top: 7%;
    margin-bottom: 5%;
    /*box-shadow: 12px -12px 0px #9BA2CC;*/
    border: none;
    padding: 7%;
    text-align: justify;
  }
  
  .firstHeading{
    border: none;
  }
  
  #siteSub{
    display: none;
  }
  
  .ambox{
    border: none !important;
    background: #ECECEC !important;
  }
  
  .infobox{
    background: #ECECEC;
    border: none;
    color: #333;
    padding: 1.5%;
  }
  
  #toctitle{
    display: none;
  }
  
  #toc{
    border: none;
    padding: 1.5%;
    background: #ECECEC;
  }
  
  h2{
    border: none !important;
  }
  
  .mw-editsection{
    opacity: 0;
  }
  
  .mw-editsection:hover{
    opacity: 1;
  }
  
  .reference{
    display: none;
  }
  
  .thumb{
    border: none !important;
  }
  
  .thumbinner{
    border: none !important;
    border-image-width: 0 0 0 0 !important;
  }
  
  #footer{
    display: none;
  }
}
